INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please write the name.','名前を書いてください。','Namae o kaite kudasai.  (kaku = to write)',107,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please open the suitcase.','スーツケースを開けてください。','Sutsukeesu o akete kudasai.  (akeru = to open)',108,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please show the passport.','パスポートを見せてください。','Pasupooto o misete kudasai.',109,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please pick up the honey.  This could also mean, please pass the honey.','はちみつを取ってください。','Hachimitsu o totte kudasai.  (toru = to take, to pass,  also = to take a picture)',110,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please lend me the pen.  Me is understood.','ペンを貸してください。','Pen o kashite kudasai.  (kasu = to lend)',111,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please lend me some money.','お金を貸してください。','Okane o kashite kudasai.',112,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please eat the sushi.','寿司を食べてください。','Sushi o tabete kudasai.  (taberu = to eat)',113,1);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Say, It’s a discourtesy, but is it Webb, meaning ‘are you Webb’?','あの、失礼ですがウェッブさんですか。','Anoo, shitsurei desu ga, Uebbu-san desu ka.  (shitsurei = discourtesy)',1,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s so.  Who is it?  Use a polite','はい、そうです。どなたですか。','word for who.Hai, soo desu.  Donata desu ka.(donata and dare both mean ‘who’, donata is more polite)',2,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s Abe Industries’ Tsuchida.','安部産業の土田です。','Abe sangyoo no tsuchida desu.  (Tsuchida would not use san after his own name)(no is used to show possession or belonging)',3,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, is it Abe Industries’ Tsuchida?','ああ、安部産業の土田さんですか。','Aa, abe sangyoo no tsuchida san desu ka.',4,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How do you do?  Go ahead be good to me.  Use a 2-word version of the second sentence.','初めまして。どうぞよろしく。','Hajimemashite.  Doozo yoroshiku.(hajimeru = hajimemasu = to start,  hajimemashite is the te form which in this case means ‘how do you do?’,  since the te form of a verb can add the meaning ‘and,’ hajimemashite could also be understood to mean ‘I will start and ...,’ but it’s best to memorize this word as meaning simply ‘how do you do?’) (yoroshiku is an adverb derived from the polite adjective yoroshii = good, so doozo yoroshiku can be understood as ‘go ahead, in a good way,’ or ‘please be good to me’,  it is often translated as ‘pleased to meet you’)',5,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The pleasure’s all mine.  Please be good to me.  Use a 2-word version of the second sentence.  As for this way, is it the honorable wife?','こちらこそ。どうぞよろしく。こちらは、奥さんですか。','Kochira koso.  Doozo yoroshiku.  Kochira wa, okusan desu ka.  (kochira koso = the pleasure is mine)  (the kochira in the 3rd sentence means this way, sochira = that way, achira = that way over there) 	(okusan = someone else’s wife,  kanai or tsuma = my wife)	(kochira wa, okusan desu ka illustrates sentence pattern A)',6,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s the wife’s Barbara.   (meaning, my wife Barbara)','はい、家内のバーバラです。','Hai.  Kanai no Baabara desu.  (Michael would not use san after his wife’s name when addressing a third party)',7,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How do you do?  It’s Barbara.  (Meaning, I’m Barbara.)  Please be good to me.  Use the 4-word version of ‘please be good to me.’','初めまして、バーバラです。どうぞよろしくお願いします。','Hajimemashite, Baabara desu.  Doozo yoroshiku onegai shimasu.  (This 4-word version of ‘please be good to me’ is more polite and is used more by women.)  (negau = to beg or pray,  suru = shimasu = to do. To form a humble verb construction, referring to actions that you perform, put o in front of the verb stem, and follow the verb stem with suru or shimasu.Onegai shimasu = I will do humble begging, usually translated as ‘please’ or ‘if you please’ or ‘I beg’)',8,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tsuchida, as for a bank, at where does it exist?','土田さん、銀行はどこにありますか。','Tsuchida san, ginkoo wa, doko ni arimasu ka.(doko = where) (use wa rather than ga because ‘bank’ is the topic,  the subject is the silent pronoun ‘it’,  this sentence illustrates sentence pattern A)',9,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s a bank, huh.','銀行ですね。','Ginkoo desu ne.',10,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Uh, at this road’s far end, there’s a bookstore.  Use toori.  ‘End’ is understood.  Meaning, it’s down the road.','ええと、この通りの向こうに本屋があります。','Eeto, kono toori no mukoo ni, honya ga arimasu.  (kono = this, sono = that, ano = that over there,  these terms are always followed by a noun, unlike the equivalent kore, sore and are) (toori = street)  (mukoo ni = at farther away,  mukoo = opposite side,  the muck is on the opposite side of the river)  (use ya after a product to indicate a store selling that product, e.g., hon = book, honya = bookstore) (arimasu = aru = exist, used for inanimate objects including plants,  when you say that something exists in a particular place, follow the place description with the particle ni)(Use ga rather than wa in this sentence because 1) you’re talking about a particular bookstore, 2) you’re introducing the idea of the bookstore, and 3) you’re using arimasu to say that something exists.)',11,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the bank, it’s that bookstore’s neighbor.','銀行はその本屋の隣です。','Ginkoo wa sono honya no tonari desu.  (use wa because ‘bank’ is the topic of the sentence,  the silent pronoun ‘it’ is the subject,  sentence pattern A )',12,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s the bookstore’s neighbor, huh.','本屋の隣ですね。','Honya no tonari desu ne.',13,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that bookstore, is it big?','その本屋は大きいですか。','Sono honya wa, ookii desu ka.',14,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t big, but you will soon know/understand it, for sure.  ‘It’ is understood.','いいえ、大きくありませんが、すぐわかりますよ。','Iie, ookiku arimasen ga, sugu wakarimasu yo.(ookii is an i adjective,  to make the negative form of an i adjective, remove the final i and add ku,  then add arimasen or nai)  (sugu = soon)',15,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that so?  Thanks a lot.','そうですか。どうも有難う。','Soo desu ka.  Doomo arigatoo.',16,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will go and come.  Use te to mean and.','じゃ、行ってきます。','Ja, itte kimasu.  (iku = ikimasu = to go,  kuru = kimasu = to come)(itte kimasu is a standard expression that you use when you leave a place and intend to come back)',17,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I’m sorry to have kept you waiting.  Hey, my wife doesn’t exist, huh.  Where is she?','お待たせしました。あれ！家内がいませんね。どこですか。','Omatase shimashita.  Are!  Kanai ga imasen ne. Doko desu ka.(omatase shimashita is a standard polite phrase used when one has made someone wait,  o is a polite prefix which can mean either ‘honorable’ or ‘humble’,  mataseru = to make someone wait = the causative tense of matsu = to wait,  matase is the stem, or the pre-masu form, of mataseru,  the stem of a verb, preceded by ‘o’ and followed by shimasu, is often used in making humble expressions – see lesson 21) (shimashita is the past tense of shimasu = suru = to do, so this phrase means, ‘I humbly made you wait) (use ga rather than wa because you’re introducing the wife into the conversation, and you’re using imasu) (imasen = negative form of imasu = iru = to exist, used for animate objects like people and animals, but not plants and trees)',18,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Barbara exists in that place over there’s jewelry store, for sure.','バーバラさんはあそこの宝石屋にいますよ。','Baabara san wa, asoko no hoosekiya ni imasu yo.  (use wa because Barbara has already been introduced as a topic into the conversation)(koko = here, soko = there,  asoko = over there)(hooseki = precious, jewel)',19,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Eh!  As for that, terrible.  Which store is it?','えっ！それは大変！どの店ですか。','E! sore wa taihen!  Dono mise desu ka.(dono = which, and it’s always followed by a noun,  dore also = which, but it’s used by itself, without a noun)',20,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it that over there place’s small store?','あそこの小さい店ですか。','Asoko no chiisai mise desu ka.(chiisai = small, an i adjective)',21,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s not that small store over there.  That over','いいえ、あの小さい店じゃありません。あれはレストランです。','there is a restaurant.  From now on, use ja rather than dewa in these lessons, unless otherwise stated.Iie, ano chiisai mise ja arimasen.  Are wa resutorandesu.  (use wa because the topic ‘that over there’ has already been introduced)',22,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s the that front’s pretty store, meaning the pretty store in front of that.','その前のきれいな店です。','Sono mae no, kirei na mise desu.(mae = front)  (kirei = pretty = a na adjective,  when you use it to modify a noun, follow it with na)',23,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, soon I will call and come.  Use de to mean and.','じゃ、すぐ呼んで来ます。','Ja, sugu yonde kimasu.  (yobu = call, invite, send for,  call to say that the yogurt burned)',24,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, the weather is good.','今日は天気がいいですね。','Kyoo wa, tenki ga ii desu.  (use ga because you’re introducing the weather topic)  (there are ten keys to predicting the weather)',25,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the bank, it exists at over there.','銀行はあそこにあります。','Ginkoo wa, asoko ni arimasu.  (use wa because the speaker assumes that the bank has already been introduced into conversation,  sentence pattern A)(contrast this with ‘ginkoo ga asoko ni arimasu’ = the bank exists over there, or there is a bank over there, in this case the speaker assumes that the listener doesn’t know about the bank and is introducing the subject)',26,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for my wife, it’s Barbara.  Use watashi no.','私の家内はバーバラです。','Watashi no kanai wa, baabara desu.  (use wa because you’re assuming that your listeners already know you have a wife,  if you wanted to introduce the concept that you have a wife, you could say ‘kanai ga imasu’)',27,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that person over there, the eyes are big.','あの人は目が大きいです。','Ano hito wa, me ga ookii desu.  (this illustrates sentence pattern B, in which a topic is followed by wa,  and then a subject is followed by ga)',28,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the bookstore, where is it?','本屋はどこですか。本屋はどこですか。','Honya wa, doko desu ka.  (sentence pattern A) (doko ni arimasu ka, also OK)',29,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s over there.','あそこです。','Asoko desu.  (asoko ni arimasu, also OK)',30,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that, whose suitcase is it?','それはだれのスーツケースですか。','Sore wa, dare no suutsukeesu desu ka.   (sentence pattern A)',31,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s my suitcase.','私のスーツケースです。','Watashi no suutsukeesu desu.',32,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Which is Michael’s passport?','どれがマイケルさんのパスポートですか。','Dore ga maikerusan no pasupooto desu ka.  (use ga because you’re asking a question using an interrogative pronoun, i.e. dore, as the subject)(interrogative pronouns include dore = which?, nani = what?, doko = where?, dare = who?, dooshite = why?, doo = how?, ikutsu = how many?, ikura = how much?, itsu = when?)',33,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is Michael’s passport.','これがマイケルさんのパスポートです。','Kore ga maikerusan no pasupooto desu.  (use ga because you’re answering a question formed with an interrogative pronoun, i.e., dore, as the subject)',34,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael’s passport, which is it?','マイケルさんのパスポートはどれですか。','Maikerusan no pasupooto wa, dore desu ka.  (sentence pattern A)',35,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Which person is Barbara?','どの人がバーバラさんですか。','Dono hito ga baabarasan desu ka.  (use ga because you’re asking a question using dono hito which functions like the interrogative pronoun dare)  (baabarasan wa, dono hito desu ka, also OK)',36,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That person over there is Barbara.','あの人がバーバラさんです。','Ano hito ga baabarasan desu.  (use ga because you’re answering a question formed with an interrogative pronoun)',37,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, it’s that person over there.','バーバラさんはあの人です。','Baabarasan wa, ano hito desu.  (sentence pattern A)',38,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That person over there is not Barbara.','あの人はバーバラさんじゃありません。','Ano hito wa baabarasan ja arimasen.  (pattern A)',39,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The bank’s neighbor is not a restaurant.','銀行の隣はレストランじゃありません。','Ginkoo no tonari wa resutoran ja arimasen.  (sentence pattern A)',40,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That store over there is small.','あの店は小さいです。','Ano mise wa chiisai desu.',41,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That store over there is small.  Plain speech.','あの店は小さい。','Ano mise wa chiisai.  (don’t use da after i adjectives)',42,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not small.','この店は小さくありません。','Kono mise wa chiisaku arimasen.(the ku form of i adjectives is used before negative words like nai and arimasen, to convert an i adjective into the ku form, remove the final i and add ku)',43,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not small.  Plain speech.  Use desu.','この店は小さくないです。','Kono mise wa chiisaku nai desu.(nai is the plain speech form of arimasen)(the word desu makes the expression more polite, but desu is omitted after i adjectives when using plain speech)',44,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not small.  Plain speech.  Don’t use desu.','この店は小さくない。','Kono mise wa chiisakunai.',45,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that over there, it’s a small store.','あれは小さい店です。','Are wa, chiisai mise desu.',46,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That store over there is quiet.','あの店は静かです。','Ano mise wa shizuka desu.(shizuka = quiet, a na adjective)To make the negative form of a na adjective, like shizuka, follow the adjective with ja arimasen, dewa arimasen, ja nai or dewa nai.  Ja is the plain speech form of dewa.',47,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not quiet.  Use ja arimasen.','この店は静かじゃありません。','Kono mise wa shizuka ja arimasen.',48,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not quiet.  Use dewa arimasen.','この店は静かではありません。','Kono mise wa shizuka dewa arimasen.',49,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not quiet.  Use plain speech.  Use desu.','この店は静かじゃないです。','Kono mise wa shizuka ja nai desu.',50,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This store is not quiet.  Use plain speech. Don’t use desu.','この店は静かじゃない。','Kono mise wa shizuka ja nai.',51,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That over there is a quiet store.','あれは静かな店です。','Are wa shizuka na mise desu.Here are some i adjectives.',52,2);
