INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Low','低い。','Hikui (hicks are low on the social scale)',57,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('High or tall','高い。','Takai',58,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('New','新しい。','Atarashii',59,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Old','古い。','Furui',60,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Difficult','難しい。','Muzukashii',61,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Easy','易しい。','Yasashii',62,2);
