INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Narrow or small','狭い。','Semai (it’s narrow in the semi truck where I live)',64,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Good','いい。','Ii',65,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Bad','悪い。','Warui',66,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Delicious','おいしい。','Oishii',67,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Unappetizing','まずい。','Mazui',68,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Near or close','近い。','Chikai',69,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Far','遠い。','Tooi',70,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Noisy','うるさい。','Urusai',71,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Dirty','汚い。','KitanaiHere are some na adjectives.',72,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Convenient','便利。','Benri',73,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Inconvenient','不便。','Fuben',74,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Quiet','静か。','Shizuka',75,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Clean or pretty','きれい。','Kirei',76,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This personal computer is very convenient.','このパソコンはとても便利です。','Kono pasokon wa totemo benri desu. (totemo = very)',77,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, are the circumstances good, meaning is it convenient for you?  Use the expression tsugoo ga ii.','今日は都合がいいですか。','Kyoo wa tsugoo ga ii desu ka.  (tsugoo ga ii is an expression used to say that a person’s circumstances are good, meaning that there is time available)',78,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This book is new.','この本は新しいです。','Kono hon wa atarashii desu.',79,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this, it’s a new book.','これは新しい本です。','Kore wa, atarashii hon desu.',80,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This book isn''t old.','この本は古くありません。','Kono hon wa furuku arimasen',81,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is this apple delicious?','このりんごはおいしいですか。','Kono ringo wa oishii desu ka.  (Ringo eats apples)',82,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s delicious.','はい、おいしいです。','Hai, oishii desu.',83,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t delicious.','いいえ、おいしくありません。','Iie, oishiku arimasen.',84,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t delicious.  Plain speech, don’t use desu.','いいえ、おいしくない。','Iie, oishikunai.',85,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are those shoes expensive?','その靴は高いですか。','Sono kutsu wa takai desu ka.',86,2);
