INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t easy.  Plain speech.  Use desu.','いいえ、易しくないです。','Iie, yasashikunai desu.',108,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s easy.','はい、易しいです。','Hai, yasashii desu.',109,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are Japanese prices cheap?  Use bukka.','日本の物価は安いですか。','Nihon no bukka wa yasui desu ka',110,2);
