INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t new.','いいえ、新しくありません。','Iie, atarashiku arimasen.',117,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t new.  Plain speech.  Don’t use desu.','いいえ、新しくない。','Iie, atarashikunai.',118,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this room, how is it?','この部屋はどうですか。','Kono heya wa, doo desu ka.',119,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so, huh.  In this case, this means let me see.','そうですね。','Soo desu ne.',120,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s a little small or tight, huh.  (referring to a room)  Use chotto.','ちょっと狭いですね。','Chotto semai desu ne.  (cannot use chiisai to describe a room,  must use semai)',121,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that so?','そうですか。','Soo desu ka.',122,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, as for this one, how is it?','じゃ、これはどうですか。','Ja, kore wa, doo desu ka.',123,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This one isn''t small or tight, for sure.','これは狭くありませんよ。','Kore wa semaku arimasen yo (ga, not OK here)',124,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so huh.  It’s spacious but it’s a little expensive, for sure.  Use chotto.','そうですね、広いですがちょっと高いですよ。','Soo desu ne.  Hiroi desu ga, chotto takai desu yo.',125,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the bank, where does it exist?','銀行はどこにありますか。','Ginkoo wa, doko ni arimasu ka.',126,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It exists at the bookstore’s neighbor, meaning next to the bookstore.','本屋の隣にあります。','Honya no tonari ni arimasu.',127,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the Japanese language book, where does it exist?','日本語の本はどこにありますか。','Nihongo no hon wa, doko ni arimasu ka.',128,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It exists on that place’s desk’s top, meaning on top of that desk.','そこの机の上にあります。','Soko no tsukue no ue ni arimasu.  (ue = above or top, shita = below or bottom)',129,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the pen, does it exist here?','ペンはここにありますか。','Pen wa, koko ni arimasu ka.',130,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, the pen doesn’t exist here.','いいえ、ペンはここにありません。','Iie, pen wa koko ni arimasen.',131,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It exists in that place over there’s desk’s inside.','あそこの机の中にあります。','Asoko no tsukue no naka ni arimasu.   (naka = inside,  soto = outside)',132,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, now, where does he exist?','マイケルさんは今どこにいますか。','Maikerusan wa, ima, doko ni imasu ka.',133,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Does he exist at school?','学校にいますか。','Gakkoo ni imasu ka.',134,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, he exists at the airport.','いいえ、空港にいます。','Iie, kuukoo ni imasu.  (I wore my cool coat at the airport)',135,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Where is the bank?  Use desu.','銀行はどこですか。','Ginkoo wa, doko desu ka.',136,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s there.','そこです。','Soko desu.',137,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Where is Barbara?  Use desu.','バーバラさんはどこですか。','Baabarasan wa doko desu ka.',138,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, it’s the jewelry store, meaning she’s in the jewelry store.','バーバラさんは宝石屋です。','Baabarasan wa hoosekiya desu.',139,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('On the far side of the post office, a hospital exists. Side is understood.','郵便局の向こうに病院があります。','Yuubinkyoku no mukoo ni byooin ga arimasu.  (OK to substitute mukoogawa for mukoo,  gawa = side)',140,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the school’s neighbor’s bank, Michael exists.','学校の隣の銀行にマイケルさんがいます。','Gakkoo no tonari no ginkoo ni, maikerusan ga imasu.',141,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the desk’s top, Barbara’s pen exists.','机の上にバーバラさんのペンがあります。','Tsukue no ue ni, baabarasan no 	pen ga arimasu.',142,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tsuchida, where does he exist?','土田さんはどこにいますか。','Tsuchida san wa, doko ni imasu ka.',143,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara’s bag, where does it exist?  Use baggu.','バーバラさんのバッグはどこにありますか。','Baabarasan no baggu wa, doko ni arimasu ka.  (Japanese people pronounce baggu ‘bakku’)',144,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In front of the desk','机の前。','Tsukue no mae',145,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Behind the desk','机の後ろ。','Tsukue no ushiro',146,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Above the desk or on top of the desk','机の上。','Tsukue no ue',147,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Under the desk','机の下。','Tsukue no shita',148,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Around the desk','机のまわり。','Tsukue no mawari',149,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Close to the desk.  Use soba.','机のそば。','Tsukue no soba',150,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Near the desk (literally, the desk’s closely)','机の近く。','Tsukue no chikaku.  (chikaku is the adverbial form of chikai = near or close, but chikaku is also used as a locative expression meaning ‘close to’,  another example of a locative expression is mae = front)',151,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It exists on the far side of the desk. ‘Side’ is understood.','机の向こうにあります。','Tsukue no mukoo ni arimasu.  (cf. mukoogawa = far side, OK to substitute mukoogawa for mukoo in this sentence)',152,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the telephone, it’s beside the desk. Use yoko.','電話は机の横です。','Denwa wa tsukue no yoko desu.',153,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The desk’s right side.  Side is understood.','机の右。','Tsukue no migi.  (migi = right, hidari = left)  (cf. migigawa = right side, OK to substitute migigawa for migi in this sentence)',154,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The desk’s left side.  Side is understood.','机の左。','Tsukue no hidari.  (hidarigawa = left side,  OK to substitute hidarigawa for hidari in this sentence)',155,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The bookstore’s neighbor, meaning next to or next door.','本屋の隣。','Honya no tonari',156,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The box’s inside.','箱の中。','Hako no naka',157,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The box’s middle','箱のまん中。','Hako no mannaka.',158,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the phone, where does it exist?','電話はどこにありますか。','Denwa wa, doko ni arimasu ka.',159,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it the phone?','電話ですか。','Denwa desu ka.',160,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the phone, it exists beside the desk.  Use yoko.','電話は机の横にあります。','Denwa wa, tsukue no yoko ni arimasu.',161,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the cat, it exists below the desk.','猫は机の下にいます。','Neko wa, tsukue no shita ni imasu.',162,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the dog, it exists at the box’s inside.','犬は箱の中にいます。','Inu wa, hako no naka ni imasu.',163,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the eye glasses, they exist on the TV’s top.','メガネはテレビの上にあります。','Megane wa, terebi no ue ni arimasu.',164,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the hospital, it exists at the post office’s rear.','病院は郵便局の後ろにあります。','Byooin wa, yuubinkyoku no ushiro ni arimasu.',165,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the benches, they exist at the fountain’s around, meaning around the fountain.','ベンチはふん水のまわりにあります。','Benchi wa, funsui no mawari ni arimasu.  (mawari = around,  this comes from mawaru = to turn or spin) (funsui = fountain,  we have fun in the sun in the fountain)',166,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the flower shop’s left, what exists?','花屋の左に何がありますか。','Hanaya no hidari ni, nani ga arimasu ka.',167,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the hospital’s closely, what exists?','病院の近くに何がありますか。','Byooin no chikaku ni, nani ga arimasu ka.',168,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At Barbara’s proximity, what exists?  Use soba.  This thing is animate.','バーバラさんのそばに何がいますか。','Baabara san no soba ni, nani ga imasu ka.',169,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A dog exists.','犬がいます。','Inu ga imasu.',170,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for here, it’s a park.','ここは公園です。','Koko wa, kooen desu.  (kore wa, kooen desu, if you mean ‘this is a park,’ is not OK in Japanese)',171,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Around the park, big buildings, a lot, exist.  Use tatemono.','公園のまわりに大きい建物がたくさんあります。','Kooen no mawari ni ookii tatemono ga takusan arimasu.',172,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the park’s inside, it’s quiet.','公園の中は静かです。','Kooen no naka wa shizuka desu',173,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In the middle of the park, a fountain exists.','公園のまん中にふん水があります。','Kooen no mannaka ni funsui ga arimasu',174,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Around the fountain, benches exist.','ふん水のまわりにベンチがあります。','Funsui no mawari ni benchi ga arimasu.',175,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the benches’ rear, big trees exist.','ベンチの後ろに大きい木があります。','Benchi no ushiro ni, ookii ki ga arimasu.',176,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('On the big tree’s underneath bench, Barbara exists.','大きい木の下のベンチにバーバラさんがいます。','Ookii ki no shita no benchi ni, Barbara-san ga imasu.',177,2);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now, it’s 3:00, huh.','今、３時ですね。','Ima, sanji desu ne.',1,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, let’s go.','じゃ、行きましょう。','Ja, ikimashoo.  (the shoo ending replaces the su ending on a masu verb like ikimasu when you want to say ‘let’s do something’ or when you want to say ‘I shall do something’,  the shoo ending is also used to convert the word desu to the word deshoo = ‘it probably is’)',2,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, from here, as far as the hotel, about how long will it take?','ええ、ここからホテルまでどのくらいかかりますか。','Ee.  Koko kara, hoteru made, dono kurai kakarimasu ka.  (kurai = about, approximately, almost, something like,  often softened to gurai,  OK to substitute gurai for kurai in this sentence)  (kakarimasu = kakaru = ‘take’ when referring to time, or ‘cost’ when referring to money)',3,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so, huh.  Meaning, let me see.','そうですね。','Soo desu ne.',4,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('By car, it will take about 2 hours.','車で２時間ぐらいかかります。','Kuruma de, nijikan gurai kakarimasu.  (kurai, not OK,  it sounds too harsh with nijikan)  (jikan = time, hours,  ichijikan = 1 hour, nijikan = 2 hours, etc.)',5,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To a great degree, it’s far, huh.','随分遠いですね。','Zuibun, tooi desu ne.  (zuibun = extremely, to a great degree)',6,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a train, does it exist?','電車はありますか。','Densha wa, arimasu ka.',7,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A train also exists, but it isn’t very convenient.','電車もありますがあまり便利じゃありません。','Densha mo arimasu ga, amari benri ja arimasen.(mo = also,  it replaces wa and ga when used after a subject or topic)(amari = not very, in negative constructions, very much or excessively, in positive constructions)',8,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, let’s go by taxi.','今日はタクシーで行きましょう。','Kyoo wa, takushii de ikimashoo.  (de = by means of)',9,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so huh.  Let’s do so.','そうですね。そうしましょう。','Soo desu ne.  Soo shimashoo.',10,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The street is being uncrowded, huh.','道がすいていますね。','Michi ga suite imasu ne.  (suku = to be uncrowded)  (the te or de form of a verb can carry the meaning ‘ing’ when combined with iru or imasu,  such verbs can be used with both animate and inanimate objects)',11,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it isn’t being very crowded, huh.  Use amari.','ええ、あまり込んでいませんね。','Ee, amari konde imasen ne.  (konde is the de form of komu = to get crowded)',12,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah!  Is that over there visible, meaning can you see it?','あっ、あれが見えますか。','A!  Are ga miemasu ka.  (ga is always used with  mieru = can be seen)',13,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Which is it?','どれですか。','Dore desu ka.',14,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it that big building over there?','あの大きい建物ですか。','Ano ookii tatemono desu ka.',15,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t that building over there.','いいえ、あの建物じゃありません。','Iie, ano tatemono ja arimasen.',16,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s that neighbor, meaning it’s next to that.   Use tonari.','その隣です。','Sono tonari desu.',17,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What is it?','何ですか。','Nan desu ka.',18,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s Disneyland for sure.','ディズニーランドですよ。','Dizuniirando desu yo.',19,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, is that over thereTokyo Disneyland?','ああ、あれが東京ディズニーランドですか。','Aa, are ga tookyoo Dizuniirando desu ka.  (ga is used for emphasis)',20,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s big, huh.','大きいですね。','Ookii desu ne.',21,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it’s very big for sure.','ええ、とても大きいですよ。','Ee, totemo ookii desu yo.',22,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Barbara, sometime together let’s go.','バーバラさん、いつか一緒に行きましょう。','Baabarasan, itsuka issho ni ikimashoo.  (itsu = when,  itsuka = sometime, dokoka = somewhere, dareka = someone,  nanika = something) (issho ni = together)',23,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s interesting for sure.','面白いですよ。','Omoshiroi desu yo.  (omoshiroi = interesting, fun, funny)',24,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, let’s do so.','ええ、そうしましょう。','Ee, soo shimashoo.',25,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From Narita airport, as for as far as Tokyo, is it close?','成田空港から東京まで近いですか。','Narita kuukoo kara tookyoo made wa, chikai desu ka.',26,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, are the streets being crowded?','今日は道が込んでいますか。','Kyoo wa, michi ga konde imasu ka.',27,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Narita, it’s a new airport, but it''s a bit inconvenient.  Use sukoshi.','成田は新しい空港ですが少し不便です。','Narita wa atarashii kuukoo desu ga, sukoshi fuben desu.',28,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A bus also exists, but it takes time.','バスもありますが時間がかかります。','Basu mo arimasu ga, jikan ga kakarimasu.  (use ga with kakaru, e.g., ‘okane ga kakaru’ = it costs money)',29,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tokyo, it’s interesting, but the prices are high. Use bukka.','東京は面白いですが物価が高いです。','Tookyoo wa, omoshiroi desu ga, bukka ga takai desu.  (even though ‘prices’ are plural in English, use ga because you’re referring to the specific prices in effect now)',30,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tokyo, it’s interesting, but ...  This hanging ‘but’ implies that there’s also something not so good about Tokyo.','東京は面白いですが...','Tookyoo wa omoshiroi desu ga ...',31,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sushi is good, but it’s expensive.','寿司はおいしいですが高いです。','Sushi wa oishii desu ga, takai desu.',32,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Japan is tight or narrow, but people a lot exist.','日本は狭いですが人がたくさんいます。','Nihon wa semai desu ga, hito ga takusan imasu.',33,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the Japanese language, it’s difficult, but it’s interesting.','日本語は難しいですが面白いです。','Nihongo wa muzukashii desu ga, omoshiroi desu.',34,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This magazine is old, but that magazine over there is new.','この雑誌は古いですが、あの雑誌は新しいです。','Kono zasshi wa furui desu ga, ano zasshi wa atarashii desu.  (use wa twice, because you’re comparing 2 contrasting topics)',35,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Taxis are convenient, but they are expensive.','タクシーは便利ですが高いです。','Takushii wa benri desu ga, takai desu.',36,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Here is quiet, but over there is noisy.','ここは静かですが、あそこはうるさいです。','Koko wa shizuka desu ga, asoko wa urusai desu.  (not OK to substitute kore wa or kore ga for koko wa)  (use wa for contrasting topics)',37,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Today the circumstances are good, but tomorrow they’re bad.  Use dame.','今日は都合がいいですが、明日はだめです。','Kyoo wa tsugoo ga ii desu ga, ashita wa dame desu.',38,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This restaurant is expensive, but that restaurant over there is cheap.','このレストランは高いですが、あのレストランは安いです。','Kono resutoran wa takai desu ga, ano resutoran wa yasui desu.',39,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the work, is it interesting?','仕事は面白いですか。','Shigoto wa omoshiroi desu ka.',40,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it’s interesting, but it’s very busy.','ええ、面白いですがとても忙しいです。','Ee, omoshiroi desu ga, totemo isogashii desu.',41,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a personal computer, is it convenient?','パソコンは便利ですか。','Pasokon wa benri desu ka.',42,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it’s convenient, but it’s difficult.','ええ、便利ですが難しいです。','Ee, benri desu ga, muzukashii desu.',43,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To say ‘very’ or ‘extremely,’ in positive constructions, you may use either totemo or zuibun.  As for that person over there, she is a very pretty person.  Use totemo.','あの人はとてもきれいな人です。','Ano hito wa, totemo kirei na hito desu.',44,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that person over there, she is a very pretty person.  Use zuibun.','あの人は随分きれいな人です。','Ano hito wa, zuibun kirei na hito desu.',45,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, cars are very numerous.  Use totemo.','今日は車がとても多いです。','Kyoo wa, kuruma ga totemo ooi desu.  (ooi = numerous, an i adjective)',46,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, cars are very numerous.  Use zuibun.','今日は車が随分多いです。','Kyoo wa, kuruma ga zuibun ooi desu.',47,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Amari, when used in negative constructions, means ‘not very.’  As for the weather, it isn’t very good.  Use yoku arimasen.','天気は、あまりよくありません。','Tenki wa, amari yoku arimasen.  (ii = good, an i adjective,  to make the adverbial ku form of this adjective, don’t say iku, instead use the similar word yoi = good, and convert this to yoku)',48,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this TV, it isn’t very expensive.','このテレビはあまり高くありません。','Kono terebi wa, amari takaku arimasen.',49,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it isn’t very interesting.','いいえ、あまり面白くありません。','Iie, amari omoshiroku arimasen.',50,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The Japanese language book isn’t very new.','日本語の本はあまり新しくありません。','Nihongo no hon wa amari atarashiku arimasen.',51,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for hiragana, it isn’t very difficult.','ひらがなはあまり難しくありません。','Hiragana wa amari muzukashiku arimasen.',52,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tokyo’s streets are not very clean.','東京の道はあまりきれいじゃありません。','Tookyoo no michi wa amari kirei ja arimasen.  (this can also mean, not very pretty) (kirei is a na adjective)',53,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From my house, until the school, is not very far.','私の家から学校まであまり遠くありません。','Watashi no ie kara, gakkoo made, amari tooku arimasen.',54,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I drink, or I will drink.  In this section, use the masu form of verbs.','飲みます。','Nomimasu.  (from nomu)',55,3);
