INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I always read books.','いつも本を読みます。','Itsumo hon o yomimasu.  (itsu = when,  itsuka = sometime,  itsumo = ‘always’ in positive constructions, ‘never’ in negative constructions,  itsudemo = anytime)',85,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I write my name, or I will write  my name.  ‘My’ is understood.','名前を書きます。','Namae o kakimasu.',86,3);
