INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s about 1 ½ hours','1時間半ぐらいです。','Ichijikan han gurai desu. (kurai, not OK, in this case)',201,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sigh.  I don’t know/understand exactly.','さあ、ちょっとわかりません。','Saa.  Chotto wakarimasen.  (cf. choodo ichiji = exactly 1:00)  (not OK to substitute choodo for chotto in this sentence)',202,3);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for time (literally hour duration), about how much does it take?','時間はどのくらいかかりますか。','Jikan wa, dono kurai kakarimasu ka. (gurai, also OK)',203,3);
