INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When you want to say that something will happen before or after a given time, do not use the optional kan (e.g., futsuka kan,  meaning 2 days’ duration) before (3 responses)','前に。後に。後に。','Mae ni, ato ni, or go ni (or ato de) (mae de is only used when referring to location, e.g., eki no mae de)',121,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I came to Japan at 3 days ago.','三日前に日本に来ました。','Mikka mae ni Nihon ni kimashita.',122,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How many weeks duration is summer vacation?','夏休みは何週間ですか。','Natsu yasumi wa nanshuukan desu ka.',123,10);
