INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Use this particle after a number or quantity for emphasis, to indicate that it is more than expected,  or with negative verbs, to indicate that it is less than expected','も','Mo',125,10);
