INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('soccer','サッカー','Sakka',150,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('baseball','野球。','Yakyuu',151,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('pingpong','卓球。','Takkyuu  (I''ll take you to ping pong)  (pinpon, also OK)',152,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('rugby','ラグビー。','Ragubii',153,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('bookshelf','本棚','Hondana  (bookcase like a Honda)',154,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('radio-cassette player','ラジカセ','Rajikase',155,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('CD','シーディー','Shiidii',156,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('B is bigger than A.  Don’t use hoo ga.','BはＡより大きいです。','B wa A yori ookii desu.',157,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A vs. B, which is better?  (2 different responses, without and with hoo ga)','ＡとＢとどちらがいいですか。ＡとＢとどちらの方がいいですか。','A to B to, dochira ga ii desu ka.  A to B to, dochira no hoo ga ii desu ka.  (you cannot substitute dore for dochira before ‘no hoo ga’ in these sentences, even if you have 3 or more choices, you cannot say dore no hoo ga or dare no hoo ga)',158,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Compared to A, B is more expensive.  Use hoo ga.','AよりBの方が高いです。','A yori B no hoo ga takai desu.',159,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A is not as expensive as B.  Use plain speech with desu.   Don’t use hoo ga.','AはBほど高くないです。','A wa B hodo takakunai desu.  (OK to use yori instead of hodo in this sort of sentence)',160,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A also, B also, are expensive.','AもBも高いです。','A mo B mo takai desu.',161,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The trains are faster than a car.  Don’t use hoo ga.','電車は車より速いです。','Densha wa kuruma yori hayai desu.  (densha no hoo ga, kuruma yori, also OK)',162,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Train v. car, which is faster?  Use hoo ga.','電車と車とどちらの方が速いですか。','Densha to kuruma to, dochira no hoo ga hayai desu ka.   (dochira ga, also OK)',163,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Compared to a car, the train is faster. Use hoo ga.','車より電車の方が速いです。','Kuruma yori, densha no hoo ga hayai desu.  (kurumayori, densha ga hayai desu, also OK, not as good)',164,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the train, compared to a car, it is fast.','電車は車より速いです。','Don’t use hoo ga.Densha wa, kuruma yori, hayai desu.',165,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Cars, compared to trains, are not as fast.','車は電車ほど速くありません。','Kuruma wa, densha hodo, hayaku arimasen.',166,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sushi vs. tempura, which do you like better?  Don’t use hoo ga.  Use dochi.  (plain speech)','寿司と天ぷらとどっちが好き。','Sushi to tenpura to, dotchi ga suki.',167,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I like sushi also, tempura also.  Plain speech,','寿司もてんぷらも好き。','Sushi mo tenpura mo suki.',168,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I like both.  Use dochira.  Plain speech.','どちらも好き。','Dochira mo suki.  (dochi de mo suki, dore demo suki, both also OK)',170,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is Yokohama big?','横浜は大きいですか。','Yokohama wa ookii desu ka.  (hiroi also OK, but hiroi refers to area, while ookii refers to population)',171,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, but compared to Tokyo, Osaka, etc., it isn’t big.','ええ、でも東京や大阪ほど大きくありません。','Ee, demo Tookyoo ya Oosaka hodo ookiku arimasen.',172,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it''s big, but not compared to Tokyo.','ええ、大きいですが東京ほどじゃありません。','Ee, ookii desu ga, Tookyoo hodo ja arimasen.',173,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('a great amount of time or distance etc.','ずーっと。','Zutto  (zoo tower is at a great distance)  (cf. zuibun = very, extremely)',174,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for salary, compared to a division manager, a president is a great deal more numerous.  Use hoo ga for the next 7 responses.','給料は部長より社長の方がずーっと多いです。','Kyuryoo wa, buchoo yori shachoo no hoo ga, zutto ooii desu.  (Shachoo wa, buchoo yori, kyuryoo ga zutto ooii desu, also OK) (OK to substitute daibu for zutto here, but zuibun sounds strange)',175,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the roads, compared to Monday, they are more crowded on Sundays.  Use michi.  Use motto.','道は月曜日より日曜日の方がもっと込んでいます。','Michi wa getsuyoobi yori nichiyoobi no hoo ga motto konde imasu.  (OK to omit motto)',176,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Compared to to go by bus, to go by car is a little faster.  Use chotto.','バスで行くより車で行くほうがちょっと速いです。','Basu de iku yori, kuruma de iku hoo ga, chotto hayai desu.  (cannot say iku no hoo ga) (iki yori, iki hoo ga, not OK)',177,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to Osaka, compared to the bullet train, the airplane is two hours faster.','大阪へは新幹線より飛行機の方が２時間速いです。','Oosaka e wa, shinkansen yori, hikooki no hoo ga, nijikan hayai desu.',178,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for allowances, compared to me, my older brother is 1000 yen more numerous.  Use a man’s word for me.','こづかいはぼくより兄の方が千円多いです。','Kozukai wa, boku yori, ani no hoo ga, sen yen ooi desu.  (Kozue gives Kyle an allowance)',179,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everest v. Mt. Fuji, which is higher?','エベレストと富士山とどちらの方が高いですか。','Eberesuto to fujisan to, dochira no hoo ga takai desu ka.',180,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everest is much higher.','エベレストの方がずーっと高いです。','Eberesuto no hoo ga zutto takai desu.',181,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Mt. Fuji is not as tall as Everest.  Don’t use hoo ga.','富士山はエベレストほど高くありません。','Fujisan wa Eberesuto hodo takaku arimasen. (yori is also OK)',182,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('People who like Japanese food vs. people who like Western food , which are more numerous?  Don’t use hoo ga.','和食が好きな人と洋食が好きな人とどちらが多いですか。','Washoku ga suki na hito to, yooshoku ga suki na hito to, dochira ga ooi desu ka.',183,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for summer’s travel, compared to the people','夏の旅館は海へ行った人より山へ行った人の方が多いですか。','who went to the ocean, are the people who went to the mountains more numerous?  Use hoo ga.Natsu no ryokoo wa, umi e itta hito yori, yama e itta hito no hoo ga, ooi desu ka.',184,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Women who smoke tobacco, compared to','タバコを吸う女の人はタバコを吸わない女の人より何人少ないですか。','women who don''t smoke tobacco, are how many people fewer?  Don’t use hoo ga.Tabako o suu onna no hito wa, tabako o suwanai onna no hito yori, nan nin sukunai desu ka.  (not OK to substitute hodo for yori here, when hodo, meaning ‘not as ... as,’ is preceded by a noun, the verb must be negative, e.g., kuruma wa densha hodo hayaku arimasen = the car is not as fast  as the train)',185,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for hourly honorable money, compared to the English teacher, the model is more numerous.  Use hoo ga.','１時間のお金は英語の先生よりモデルの方が多いです。','Ichijikan no okane wa, eigo no sensei yori, moderu no hoo ga ooi desu.',186,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s a very good book, I think.','とてもいい本だと思います。','Totemo ii hon da to omoimasu.',187,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s a very good book, I think, for sure. (plain speech)','とてもいい本だと思うよ。','Totemo ii hon da to omou yo.',188,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is probably good.  This is the subject.','これがいいでしょう。','Kore ga ii deshoo.',189,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is probably good, I think.','これがいいだろうと思います。','Kore ga ii daroo to omoimasu.',190,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Isn''t this good (soften the word good).','これがいいんじゃありませんか','Kore ga iin ja arimasen ka.',191,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Isn''t this good question, I think. (soften the word good)','これがいいんじゃないかと思います。','Kore ga iin ja nai ka to omoimasu.',192,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is probably good (plain speech).','これがいいだろう。','Kore ga ii daroo.',193,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is probably good, I think, but...  Use kedo.  Plain speech.','これがいいだろうと思うけど。','Kore ga ii daroo to omou kedo.',194,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Isn''t this good.  Soften the word good.  Don’t use ka.  (plain speech)','これがいいんじゃない。','Kore ga iin ja nai.',195,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Isn''t this good, question, I think, but ... (plain speech)  Soften the word good.  Use kedo.','これがいいんじゃないかと思うけど。','Kore ga iin ja nai ka to omou kedo.',196,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How do you think of this book?','この本をどう思いますか。','Kono hon o doo omoimasu ka.',197,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I think it''s very good.  Don’t use da or desu in the next 6 responses.','とてもいいと思います。','Totemo ii to omoimasu.',198,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I think it is not very good.','あまりよくないと思います。','Amari yokunai to omoimasu.  (iin ja nai  means the opposite of yokunai, i.e., isn’t it good?)',199,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you think Japanese prices are high?  Use bukka.','日本の物価は高いと思いますか。','Nihon no bukka wa takai to omoimasu ka.',200,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, I think they''re high.','ええ、高いと思います。','Ee, takai to omoimasu.',201,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, I think so.','ええ、そう思います。','Ee, soo omoimasu.',202,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I don''t think they''re high.  (use wa after to in negative sentences)','いいえ、高いとは思いません。','Iie, takai to wa omoimasen.',203,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To state your own opinion, say','と思います。と思っている。','To omoimasu (to omotte iru, also OK)205.  to state someone else''s opinion, say  (plain speech)To omotte iru',204,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, she thinks Japanese prices are high.  Use bukka.','バーバラさんは日本の物価は高いと思っています。','Baabarasan wa nihon no bukka wa takai to omotteimasu.  (bukka ga takai is also OK, stronger emphasis)',206,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for him, he said he would go tomorrow.','彼は明日行くと言いました。','Kare wa ashita iku to iimashita.',207,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I wrote to the letter that I''m very healthy.','手紙にとても元気だと書きました。','Tegami ni totemo genki da to kakimashita.  (tegami de, also OK, but the meaning is a little different, since ni = in the letter and de = by the letter)',208,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanaka, last week he went to Hong Kong I heard, but ...','田中さんは先週香港へ行ったと聞きましたが。','Tanaka san wa senshuu Honkon e itta to kikimashita ga',209,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it better to play sports a lot?','スポーツをたくさんした方がいいですか。','Supootsu o takusan shita hoo ga ii desu ka.  (takusan no supootsu, also OK, but changes the meaning)',210,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for inside the trains’ Japanese people’s manners, are they good?  Use no rather than de after ‘inside the trains.’ Use manaa.','電車の中の日本人のマナーはいいですか。','Densha no naka no nihon jin no manaa wa ii desu ka.  (densha no naka de nihonjin, or densha no naka de no nihonjin, both OK)',211,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michiko''s story','みち子さんの話。','Michiko san no hanashi.',212,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in my class, the travel-liking people are numerous.   Use ni to mean in.  Use kurasu.  Use no instead of ga.','私のクラスには旅行の好きな人が多いです。','Watashi no kurasu ni wa, ryokoo no suki na hito ga ooi desu.  (kurasu de, also OK, in this case, you may use ni to mean in the class, or you may use de if you want to say of the class) (ryokoo ga, also OK)',213,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('There are also a lot of people who have been to foreign countries.','外国へ行ったことがある人もたくさんいます。','Gaikoku e itta koto ga aru hito mo takusan imasu.',214,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This year''s summer, there are also people who went to Europe, but ...','今年の夏ヨーロッパに行った人もいますが...','Kotoshi no natsu, yooroppa ni itta hito mo imasu ga...',215,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Compared to the people who went to Hawaii, Korea, etc., as for numerous, it is not.','ハワイや韓国へ行った人ほど多くはありません。','Hawai ya kankoku e itta hito hodo, ooku wa arimasen.  (OK to omit wa after ooku,  used for emphasis)',216,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since Japanese transportation, hotels, etc. are expensive ...  Don’t use da or desu.','日本の乗り物やホテルは高いから...','Nihon no norimono ya hoteru wa takai kara ...  (ga is too specific here, so use wa)',217,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To go to foreign countries is cheaper, Jiroo-kun was saying, but is it probably true?   Use hoo ga.','外国へ行った方が安いと次郎君が言っていましたが、ほんとでしょうか。','Gaikoku e itta hoo ga yasui to jirookun ga itte imashita ga, honto deshoo ka.  (cannot substitute iku for itta)  (kun can be added to the names of boys and young men, instead of san,  kun can also be used after the names of young women)',218,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I also, at next year''s spring vacation,  for about 3 weeks, want to go to Europe, I think, I started a part-time job.  Use te to mean since.','私も来年の春の春休みに３週間ぐらいヨーロッパへ行きたいと思ってアルバイトを始めました。','Watashi mo rainen no haru yasumi ni, sanshuukan gurai, yooroppa e ikitai to omotte, arubaito o hajimemashita.',219,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in Michiko''s class, what  liking people','みち子さんのクラスには何が好きな人が多いですか。','are numerous?  Use ni to mean in.Michiko san no kurasu ni wa, nani ga suki na hito ga ooi desu ka.',220,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Travel-liking people are numerous.','旅行が好きな人が多いです。','Ryokoo ga suki na hito ga ooi desu.  (hito wa, not OK, since you’re answering the question ‘nani ga’)',221,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The people who went to Hawai, Korea etc. are numerous.','ハワイや韓国へ行った人が多いです。','Hawai ya kankoku e itta hito ga ooi desu.',222,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He thinks that foreign countries'' travel is cheaper.  ‘He’ is understood.','外国の旅行の方が安いと思っています。','Gaikoku no ryokoo no hoo ga yasui to omotte imasu. (...ryokoo wa motto yasui ...  also OK)  (when telling what someone else thinks, use omotte iru)',223,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She thinks that she would like to go at next spring vacation.','来年の春休みに行きたいと思っています。','Rainen no haruyasumi ni ikitai to omotte imasu.',224,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She thinks that she would like to go for about 3 weeks.','３週間ぐらい行きたいと思っています。','Sanshuukan gurai ikitai to omotte imasu.',225,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, compared to classical, he likes jazz.  Don’t use hoo ga.   (plain speech)','マイケルさんはクラッシックよりジャズが好きだ。','Maikeru san wa kurashikku yori jazu ga suki da.(... jazu no hoo ga ...  also OK)',226,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tsuchida dislikes jazz. (plain speech)','土田さんはジャズがきらいだ。','Tsuchida san wa jazu ga kirai da.',227,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tsuchida doesn''t like classical.  (plain speech)','土田さんはクラッシックが好きじゃない。','Tsuchida san wa kurashikku ga suki ja nai.',228,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tsuchida, this week v. next week, which is busier?  Use dochira.  Don’t use hoo ga.','土田さんは今週と来週とどちらがいそがしいですか。','Tsuchida san wa konshuu to raishuu to, dochira ga isogashii desu ka.  (... dochira no hoo ga ... also OK)  (dore not OK if only 2  items are being compared,  dore is not used with no hoo ga)',229,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Citizens’ national holiday','国民の祝日。','Kokumin no shukujitsu  (kokumin = citizen, people of a country,  a koku [country] full of mean people)  (cf. kookoku = advertisements,  kyuujitsu = holiday, vacation,  shukujitsu = national holiday,   ganjitsu = New Year’s Day)',230,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('January 1st.  New Year''s Day.','１月１日。元日。','Ichigatsu tsuitachi.  Ganjitsu. (Gandalf''s holiday)  (cf. oshoogatsu = New Year)',231,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('May 5th.  Boy''s Day.','５月５日。男の子の日。','Gogatsu itsuka.  Otoko no ko no hi.  (this is more generally  known as kodomo no hi = Children’s Day)',232,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What you say when you pray in front of a butsudan.','南無阿弥陀仏。','Namu amida butsu.  (this means something like ‘Hail to Amida Buddha’,  butsu = buddha if used in combination, as in butsudan or amidabutsu,  hotoke = buddha, if used by itself,  bukkyoo = Buddhism)  (butsudan = altar in Japanese homes, with photos & artifacts of dead relatives)',233,10);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since there''s a koala bear, is it an Australian stamp?','コアラだからオーストラリアの切手ですか。','Use dakara.Koara da kara, oosutorariya no kitte desu ka.',1,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, I received it from a friend.  Use ni. Soften this.','ええ、友達にもらったんです。','Ee, tomodachi ni morattan desu  (kara also OK)',2,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since 3 exist, I shall give you one.  (referring to stamps)','３枚あるから１枚あげましょう。','San mai aru kara, ichimai agemashoo.  (san mai ga aru, not OK,  you don’t need to use ga, wa or wo after numbers)  (mai is a counter for flat thin items)',3,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will you honorably give?  Pleased!  Speaking to someone outside your in-group.  Soften this.  Use a woman’s word as an intensifier.','くださるんですか。うれしいわ。','Kudasarun desu ka.  Ureshii wa.  (from the verb kudasaru) (kudasaru no desu ka or kudasaimasu ka, both also OK) (ureshii= pleased, happy, glad, pleasant)  (the doctors are pleased about the urea she is making, referring to a patient whose kidneys are recovering)',4,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The son is collecting stamps for sure.  Soften this.','息子が切手を集めているんですよ。','Musuko ga, kitte o atsumette irun desu yo. (the verb is atsumeru) (collect atsui [hot] mares)',5,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to see unusual foreign countries’s stamps, it’s pleasant, right?  Use no to make a noun phrase.','珍しい外国の切手を見るのは楽しいですね。','Mezurashii gaikoku no kitte o miru 	no wa tanoshii desu ne.  (I visited an unusual Mexican zoo, where I got a rash)',6,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Others also, various, exist, for sure.  Use hoka ni.','他にも色々ありますよ。','Hoka ni mo iroiro arimasu yo. (hoka no also OK) (iroiro hoka ni mo, also OK) (not OK to substitute betsu for hoka here,  hoka means ‘another undefined object, person or place’ but betsu means ‘another defined object, person or place,’ e.g., hoka no hito no mono = some other person’s thing, while betsu no hito no mono = the other person’s thing,  also, hoka no heya ni iku= go to another room, betsu no heya ni iku = go to the other room)',7,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since panda stamps and unusual fish stamps etc. also exist, I shall give you this one too.  Use polite speech, rather than plain speech, in the subordinate clause in this sentence.','パンダの切手や珍しい魚の切手もありますから、これもあげましょう。','Panda no kitte ya mezurashii sakana no kitte mo arimasu kara, kore mo agemashoo.',8,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Wow!  This many?  Use a woman’s word for wow.','まあ、こんなにたくさん。きっと息子が喜びますわ。','Maa.  Konna ni takusan.  (maa is woman''s talk)9a.  Surely the son will get delighted.  Use a woman’s word for emphasis.Kitto musuko ga yorokobimasu wa.  (the verb is yorokobu) (I’m delighted that I can go to Europe to comb a bull) (wa is woman''s speech)',9,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since my husband  was saying tonight late, go ahead, take your honorable time.  Use a short form of ‘he was saying.’  Use a 2-word abbreviation of ‘take your honorable time.’','主人は今晩遅いと言ってましたから、どうぞごゆっくり。','Shujin wa konban osoi to ittemashita kara, doozo goyukkuri.  (goyukkuri shite kudasai, also OK) (yukkuri = slowly, taking time, leisurely,  the prefix go is honorific or humble, similar to o)',10,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the one that is there, is it a picture of the honorable family?','そこにあるのはご家族の写真ですか。','Soko ni aru no wa gokazoku no shashin desu ka.',11,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it''s a picture of the family that exists in America.','ええ、アメリカにいる家族の写真です。','Ee, Amerika ni iru kazoku no shashin desu.  (Amerika ni iru no – not OK)',12,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Middle is my mother, and the at that behind standing one is my younger sister.  Use ushiro.  Use de to mean and.  ‘Middle’ and ‘at that behind standing one’ are both subjects.','真ん中が母でその後ろに立っているのが妹です。','Mannaka ga haha de, sono ushiro ni tatte iru no ga imooto desu.  (mannaka no ga  also OK)  (the usher Shiro stands behind the auditorium)   (not OK to use ura instead of ushiro in this sentence)',13,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She''s a pretty person, right?  Use a polite word for person.','きれいな方ですね。','Kirei na kata desu nee.',14,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The one that exists beside your younger sister, is it the honorable husband?  Use tonari.  ‘The one that exists’ is the subject.','妹さんの隣にいるのがご主人ですか。','Imootosan no tonari ni iru no ga, goshujin desu ka.',15,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He''s employed at a city office, for sure.  Soften this.','市役所に勤めているんですよ。','Shiyakusho ni tsutomete irun desu yo.  (the kanji ‘shi’ = city, e.g. toshi = city)  (use ni with tsutomeru = be employed,  use de with hataraku = to labor)',16,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for honorable children, it’s 3 people, right?','お子さんは３人ですね。','Okosan wa sannin desu ne.',17,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How honorably old is the youngest honorable child?','一番下のお子さんはおいくつですか。','Ichiban shita no okosan wa oikutsu desu ka.',18,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('They were saying he will become six next month.  Use the native Japanese counting method to say ‘six.’ Use a contracted form of ‘they were saying.’','来月六つになると言ってました。','Raigetsu muttsu ni naru to ittemashita.  (rokusai, also OK)  (naru = narimasu = to become, usually preceded by ni)',19,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, pretty soon school is, huh?','じゃ、もうすぐ学校ですね。','Ja, moo sugu gakkoo desu ne.',20,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will enter elementary school this year.','今年小学校に入ります。','Kotoshi shoogakkoo ni hairimasu.  (cf. chuugakkoo = middle school)',21,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This, it’s a little, but go ahead.  Don’t use wa or ga. Use sukoshi.  Use kedo.','これ、少しですけど...どうぞ。','Kore sukoshi desu kedo ... doozo.',22,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Wow, it’s big strawberries, right?  Use waa.  Use ookina.','わあ、大きないちごですね。','Waa, ookina ichigo desu ne.  (ookii ichigo, also OK)',23,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My wife also, I also, love them.  Use nan to soften this.','家内も私も大好きなんです。','Kanai mo watashi mo daisuki nan desu.',24,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My home town’s older brother, together with my parents, is growing them for sure.   Soften this.','田舎の兄が両親と一緒に作っているんですよ。','Inaka no ani ga ryooshin to issho ni tsukutte irun desu yo.  (when I’m inactive, I go to my hometown)',25,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Your older brother, with your parents together, right?  ‘Your older brother’ is the subject.','お兄さんがご両親と一緒にねぇ。','Oniisan ga go ryooshin to issho ni nee.',26,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Yamada, as for the home town, where is it?  Use a polite word for where.  Meaning, where’s your home town?','山田さんは田舎はどちらですか。','Yamada san wa inaka wa dochira desu ka.',27,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shizuoka is famous for strawberries, right?','静岡はいちごで有名ですね。','Shizuoka wa ichigo de yuumei desu ne.  (Shizuoka ga, not OK)',28,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I saw it on TV.','テレビで見ました。','Terebi de mimashita.',29,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for your family, are they all Shizuoka?  Use minna.','ご家族はみんな静岡ですか。','Gokazoku wa minna shizuoka desu ka.',30,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for my older sister, she got married and lives in Osaka.  Use te to mean and.','姉は結婚して大阪に住んでいます。','Ane wa kekkon shite oosaka ni sunde imasu.',31,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because my younger brother is a trading company employee, since 4 years ago, he went to Germany and exists.Use de to mean because.','弟は商社員で４年前からドイツに行っています。','Otooto wa shooshain de yonen mae kara doitsu ni itte imasu.  (shoosha = trading company,  they show shawls at the trading company) (cf. booeki = trading,  booekigaisha = trading company, but booekigaishain is not OK)',32,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Yamada, why did she receive the stamps and get delighted?  Use naze.  Use te to mean and.','山田さんはなぜ切手をもらって喜びましたか。','Yamada san wa naze kitte o moratte yorokobimashita ka.',33,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The room became clean.  (for a na adjective, add ni and naru to describe a change in state)','部屋がきれいになりました。','Heya ga kirei ni narimashita.',34,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Children become big, meaning they grow up.  Use kodomo.  (for an i adjective, remove the final i, then add ku and naru to describe a change in','子供は大きくなります。','state)Kodomo wa ookiku narimasu.',35,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He''s 5 now.  Next month he will become 6.','今五つです。来月六つになります。','Ima itsutsu desu.  Raigetsu muttsu ni narimasu.  (gosai, rokusai, also OK)',36,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Japanese study became interesting.','日本語の勉強がおもしろくなりました。','Nihongo no benkyoo ga	omoshiroku narimashita.',37,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it''s 50 yen apiece, altogether it amounts to 250 yen.','一つ５０円ですから全部で２５０円になります。','Hitotsu gojuen desu kara, zenbu de nihyaku gojuen ni narimasu.',38,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the little brother, he thinks he wants to become superman.','弟はスーパーマンになりたいと思っています。','Otooto wa suupaaman ni naritai to omotte imasu.  (otooto ga, also OK) (to omoimasu, not OK, since you’re saying what another person thinks)',39,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the Japanese became skillful, it developed that I want to go to Japan.  (the tai form is inflected like an adjective)','日本語が上手になったから日本に行きたくなりました。','Nihongo ga joozu ni natta kara, Nihon ni ikitaku narimashita.',40,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I practiced frequently every day.','毎日よく練習しました。','Mainichi yoku renshuu shimashita.  (renshuu = practice.  I wrenched my back practicing shooting)',41,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('You became skilled, huh.','上手になりましたね。','Joozu ni narimashita ne.',42,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Next month, in front of the station, a large supermarket will accomplish for sure.  (plain speech, soften this)','来月駅の前に大きいスーパーができるんですよ。','Raigetsu, eki no mae ni, ookii suupaa ga dekirun desu yo.  (use dekiru with ni, not de)',43,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I washed the hands with soap for sure.  (plain speech)','石鹸で手を洗ったよ。','Sekken de te o aratta yo.',44,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the honorable work, these days, how is it?  Use goro.','お仕事はこの頃どうですか。','Oshigoto wa konogoro doo desu ka.',45,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What do they want to become?','何になりたいですか。','Nan ni naritai desu ka.  (nani ni also OK)',46,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Taroo, since comics are skillful, he wants to become a cartoonist he is thinking.  Use dakara.   Don’t use san after names in the next 3 responses.','太郎はマンガが上手だからマンガ家になりたいと思っています。','Taroo wa manga ga joozu dakara, mangaka ni naritai to omotte imasu.',47,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michiko, since she wants to go to various countries, she would like to become a stewardess she is thinking.','みち子は色々な国へ行きたいからスチュワーデスになりたいと思っています。','Michiko wa iroiro na kuni e ikitai kara, suchuwaadesu ni naritai to omotte imasu.   (kuni = country, hometown,  there are a lot of cunning people in my country and in my hometown)',48,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Haruko, since she likes children, she would like to become an elementary school teacher she is thinking.  Use kodomo.  Use dakara.','春子は子供が好きだから小学校の先生になりたいと思っています。','Haruko wa kodomo ga suki dakara, shoogakkoo no sensei ni naritai to omotte imasu.',49,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Occupation','職業','Shokugyoo (my occupation is to shock George)  (cf. sotsugyoo suru = to graduate)',50,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Film star','映画スター','Eiga sutaa',51,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Architect','建築家','Kenchikuka (architecture = kenchiku,  a Ken doll made some architecture with cheese and a cucumber)',52,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Throughout the morning, all the way through, I was studying Japanese.','午前中ずーっと日本語を勉強していました。','Gozenchuu zutto nihongo o benkyoo shite imashita.  (zutto = all the way, always, very much, far more,  you have to go all the way through the dictionary to find zutto, which is nearly the last word)',53,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I''m married.','私は結婚しています。','Watashi wa kekkon shite imasu.',54,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for my younger brother, now, he went to Germany and is there.','弟は今ドイツに行っています。','Otooto wa ima doitsu ni itte imasu.',55,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, he came to Japan and is here.','マイケルさんは日本に来ています。','Maikeru san wa nihon ni kite imasu.',56,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is currently employed (shortened for speech)','勤めてます。','Tsutometemasu',57,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you know?  (shortened for speech)','知ってますか。','Shittemasu ka.',58,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The black car is being parked since 3 o''clock.','黒い車が３時から止まっています。','Kuroi kuruma ga san ji kara tomatte imasu.',59,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Which particles turn a previous phrase into a noun? (the first is preferred for something directly perceived by the senses, while the second is more formal or bookish)','の。こと。','No.  Koto.',60,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the one who gave us strawberries, who?   Use no to make a noun phrase.  Don’t use ka.  Plain speech.','いちごをくれたのは誰。','Ichigo o kureta no wa dare.',61,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yesterday, as for the one Barbara bought, what?  Use no to make a noun phrase. Don’t use ka.  Plain speech.','昨日バーバラが買ったのは何。','Kinoo baabarasan ga katta no wa nani.  (OK to substitute baabarasan no katta mono for baabarasan ga katta no wa, in this sentence)',62,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to memorize kanji, it’s terrible.  Use no to make a noun phrase.','漢字を覚えるのは大変です。','Kanji o oboeru no wa taihen desu.',63,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the cold, good, but as for the heat,  it’s irritating.  Use kedo.  Use no twice to make noun phrases.','寒いのはいいけど暑いのはいやです。','Samui no wa ii kedo, atsui no wa iya desu.',64,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for tomorrow is vacation, it’s pleasing.  ‘Tomorrow’ is the subject.  Use yasumi.  Use no to make a noun phrase, and therefore use a substitute for da.','明日が休みなのはうれしいです。','Ashita ga yasumi na no wa ureshii desu.  (cannot say yasumi da no,  must use na to mean ‘it is’,  also cannot say da koto)  (ashita wa, not OK, since you want to emphasize tomorrow specifically, not tomorrows in general)',65,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the son, he loves to collect stamps.  2 responses, using no and koto.','息子は切手を集めるのが大好きです。息子は切手を集めることが大好きです。','Musuko wa kitte o atsumeru no ga daisuki desu.Musuko wa kitte o atsumeru koto ga daisuki desu.',66,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are you knowing that Tanaka yesterday came to the home?  2 responses.','田中さんが昨日うちに来たのを知っていますか。田中さんが昨日家に来たことを知っていますか。','Tanaka san ga kinoo uchi ni kita no o shitte imasu ka.Tanaka san ga kinoo uchi ni kita koto o shitte imasu ka.',67,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This area, as for to be this quiet, isn’t it unusual?  2 responses.  Use hen.  Hen is the subject.  Soften the word ‘unusual.’','この辺がこんなに静かなのはめ珍しいんじゃありませんか。この辺がこんなに静かなことは珍しいんじゃありませんか。','Kono hen ga konna ni shizuka na no wa mezurashiin ja arimasen ka.  (use na, since you cannot use da with no)  (mezurashiku arimasen ka also OK)Kono hen ga konna ni shizuka na koto wa mezurashiin ja arimasen ka.  (use na because shizuka is a na adjective modifying koto)',68,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I felt the cold wind to blow.  Only 1 response because this is directly perceived by the sense.','冷たい風が吹くのを感じました。','Tsumetai kaze ga fuku no o kanjimashita.  (tsumetai is used for cold objects,  samui is used for cold weather, cold days etc.)  (fuku = to blow, breathe, whistle,  kanjiru = to feel or sense)',69,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I saw Tanaka to leave the room.','田中さんが部屋を出るのを見ました。','Tanaka san ga heya o deru no o mimashita.   (Heya o deta also OK, same meaning)',70,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That person over there has lived in Mexico.  (cannot use no in this expression)','あの人はメキシコに住んだことがあります。','Ano hito wa mekishiko ni sunda koto ga arimasu.',71,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael can speak Japanese.  Use dekiru.  (Again, we cannot use no in this expression.)','マイケルは日本語を話すことができます。','Maikeru wa nihongo o hanasu koto ga dekimasu.  (a verb followed by koto ga dekiru = can do) (nihongo ga hanasemasu, also OK, but nihongo ga hanasu koto ga, not OK)',72,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Webb, also, his wife, also,like collecting stamps.  Use no to make a noun phrase.','ウエッブさんも奥さんも切手を集めるのが好きです。','Uebbusan mo okusan mo kitte o atsumeru no ga suki desu.',73,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Webb, he went to Kamakura also, to Nikko also.','ウエッブさんは鎌倉へも日光へも行きました。','Uebbusan wa kamakura e mo nikko e mo ikimashita.',74,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you like strawberries also tangerines also?','いちごもみかんも好きですか。','Ichigo mo mikan mo suki desu ka.',75,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, I love strawberries also, tangerines also.','はい、いちごもみかんも大好きです。','Hai, ichigo mo mikan mo daisuki desu.',76,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I don''t like strawberries also tangerines also.','いいえ、いちごもみかんも好きじゃありません。','Iie, ichigo mo mikan mo suki ja arimasen.',77,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I like strawberries but I dislike tangerines.','いいえ。いちごは好きですが、みかんは嫌いです。','Iie, ichigo wa suki desu ga, mikan wa kirai desu.  (use wa for contrasting expressions)',78,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I like running also, swimming also, dancing also.  Use oyogu.  Use dansu o suru.  Use no to make noun phrases.','私は走るのも泳ぐのもダンスをするのも好きです。','Watashi wa hashiru no mo, oyogu no mo, dansu o suru no mo suki desu.',79,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I like running and swimming.  Use to to mean and.  Use no to make noun phrases.','走るのと泳ぐのが好きです。','Hashiru no to oyogu no ga suki desu.',80,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is there a present’s box, meaning a box for the present?  Use purezento to mean box in these lessons.  Box is the subject.','プレゼントの箱がありますか。','Purezento no hako ga arimasu ka.',81,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another person''s son.  2 responses.','息子さん。坊ちゃん。','Musuko san.  Botchan.  (cannot sayBosan)  (obotchan, also OK)',82,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another person''s daughter.  2 responses.','娘さん。お嬢さん。','Musume san.  Ojoosan. (Ojoochan also OK)',83,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another person''s grandchild.','お孫さん。','Omagosan.  (your grandchild has maggots)',84,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another person''s nephew.','おいごさん。','Oigosan.  (oi = nephew) (the nephew is oily)',85,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another person''s niece.','めいごさん。','Meigosan.  (mei = niece) (the niece is named May)',86,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s  my wife, Kyooko.','家内の京子です。','Kanai no kyooko desu.',87,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To give to an equal or superior.  2 responses, one honorific and one normal.  Plain speech.','差し上げる。上げる。','Sashiageru.  Ageru.',88,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To give to an equal (in informal speech) or to an inferior.  Plain speech.','やる。','Yaru.',89,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I gave flowers to the teacher.','先生に花をさしあげました。','Sensei ni hana o sashiagemashita.',90,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What shall we give for the child''s birthday (a man''s plain speech)?','子供の誕生日に何をやろうか。','Kodomo no tanjoobi ni nani o yaroo ka.   (to say I shall, for a u verb, add oo to the root)  (yarimashoo, also OK)',91,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What shall we give for the child''s birthday (a woman''s speech or polite speech)?','子供の誕生日に何を上げましょうか。','Kodomo no tanjoobi ni nani o agemashoo ka.',92,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall we give him an English picture book?   Polite speech.','英語の絵本をあげましょうか。','Eigo no ehon o agemashoo ka.  (ageyoo, also OK)',93,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, every morning I water the flowers.','私は毎朝花に水をやります。','Watashi wa maiasa hana ni mizu o yarimasu.',94,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('An equal or inferior gives to you or someone in your in-group.  Plain speech.','くれる。','Kureru.',95,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A superior gives to you or someone in your in-group.	  Plain speech.','くださる。','Kudasaru.',96,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The teacher gave Tanaka a book.  Tanaka is in your in-group.','先生が田中さんに本をくださいました。','Sensei ga tanaka san ni hon o kudasaimashita.',97,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that stamp, who gave it to you? Soften this.','その切手は誰がくれたんですか。','Sono kitte wa dare ga kuretan desu ka.  (sono kitte o also OK)',98,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My younger brother gave it to me.','弟がくれました。','Otooto ga kuremashita.',99,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This, I will give to an equal or superior.  Don’t use wo.  (plain speech)','これあげる。','Kore ageru.  (kore ageroo, not OK,  kore ageyoo, OK,  to make the plain speech ‘shall’ for a u verb, add oo to the root, for an ru verb, add yoo to the root)',100,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Eh!  Will you give to me or my in-group?  Use no to soften this. Don’t use ka.','えっ！くれるの。','Ee!  Kureru no?',101,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To receive from an equal or inferior. Plain speech.','もらう。','Morau.',102,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To receive from an equal or superior. Plain speech.','いただく。','Itadaku.',103,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I received a book from the teacher.  Use ni.','私は先生に本をいただきました。','Watashi wa sensei ni hon o itadakimashita.',104,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that stamp, from whom did you receive it? Use kara.  Soften this.','その切手は誰からもらったんですか。','Sono kitte wa dare kara morattan desu ka.  (sono kitte o dare ni also OK)',105,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I received it from my younger brother.  Use ni.','弟にもらいました。','Otooto ni moraimashita.   (otooto kara also OK)',106,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Who received that chocolate over there?  Soften this.','誰があのチョコレートをもらったんですか。','Dare ga ano chokoleeto o morattan desu ka.',107,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Young man Tanaka received it.  Soften this.','田中君がもらったんです。','Tanaka kun ga morattan desu.',108,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in your country, to do-marriage people, what kind of things do you give?','あなたの国では結婚する人にどんなものを上げますか。','Anata no kuni de wa kekkon suru hito ni donna mono o agemasu ka.  (kekkon suru no hito – not OK, since suru modifies hito)  (not OK to substitute koku for kuni here)',109,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What do you want to give for the honorable father''s birthday?','お父さんの誕生日に何をあげたいですか。','Otoo san no tanjoobi ni nani o agetai desu ka.  (nani ga, also OK)',110,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He became 36 years old.','３６歳になりました。','Sanjuu roku sai ni narimashita.',111,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The from-the-honorable wife’s present was a white apron.','奥さんからのプレゼントは白いエプロンでした。','Okusan kara no purezento wa shiroi epuron deshita.  (not OK to omit no)',112,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the son’s young Hiroshi (meaning the son Hiroshi), he drew the honorable father''s face skillfully and did a present.  Use te to mean and.  Don’t use ni or wo after present.','息子のひろし君はお父さんの顔を上手に描いてプレゼントしました。','Musuko no Hiroshi kun wa otoosan no kao o joozu ni kaite purezento shimashita.  (purezento ni shimashita also OK, meaning turned it into a present)',113,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanaka, he saw the picture that young Hiroshi drew and got very much delighted.  Use no rather than ga after ‘young Hiroshi.’','田中さんはひろし君の描いた絵を見てとても喜びました。','Tanaka san wa Hiroshi kun no kaita e o mite totemoyorokobimashita.',114,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In front of Tanaka''s home, since about an hour before, a red sports car is parked.','田中さんの家の前に１時間ぐらい前から、赤いスポーツカーが止まっています。','Tanaka san no uchi no mae ni, ichijikan gurai mae kara,  akai supootsukaa ga tomatte imasu.',115,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That over there is younger brother Jiroo''s car.','あれは弟の次郎さんの車です。','Are wa otooto no Jiroo san no kuruma desu.  (otooto san no jiroo san, not OK,  only use ‘san’ once)',116,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Jiroo, together with the family, is come and is here probably.  Jiroo is the subject.  Soften this.','次郎さんが家族と一緒に来ているんでしょう。','Jiroo san ga kazoku to issho ni kite irun deshoo.',117,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanaka''s wife, she is doing some cooking in the kitchen.','田中さんの奥さんは台所で料理をしています。','Tanaka san no okusan wa daidokoro de ryoori o shite imasu.   (died? Doko [where]?, the rodent?,  in the kitchen)',118,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this evening, undoubtedly it	will probably develop into a  lively meal.  Use konya.','今夜はきっとにぎやかな食事になるでしょう。','Konya wa kitto nigiyaka na shokuji ni naru deshoo.',119,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanaka, today, how old did he become?  Use ikutsu.','田中さんは今日いくつになりましたか。','Tanaka san wa kyoo ikutsu ni narimashita ka.  (nansai ni narimashita, also OK)',120,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What kind of thing did he receive from young Hiroshi?','ひろし君からどんな物をもらいましたか。','Hiroshi kun kara donna mono o moraimashita ka.',121,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the white apron, from whom did he receive it?  Use ni rather than kara.','白いエプロンは誰にもらいましたか。','Shiroi epuron wa dare ni moraimashita ka.  (dare kara also OK)',122,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the red car, since when is it being parked?','赤い車はいつから止まっていますか。','Akai kuruma wa itsu kara tomatte imasu ka.',123,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is Jiro being come by himself?','次郎さんは一人で来ていますか。','Jiroo san wa hitori de kite imasu ka.',124,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the one who is doing cooking in the kitchen, who is it?','台所で料理をしているのは誰ですか。','Daidokoro de ryoori o shite iru no wa dare desu ka.',125,11);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Did it already become 4 o''clock?','もう４時になりましたか。','Moo yoji ni narimashita ka.',1,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, not yet, for sure.','いいえ、まだですよ。','Iie mada desu yo.',2,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It became a little cold, right?  Use chotto.','ちょっと寒くなりましたね。','Chotto samuku narimashita ne.',3,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won''t you close that window and give?  Addressing someone outside your in-group.','その窓を閉めてくださいませんか。','Sono mado o shimete kudasaimasen ka.',4,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I turn on the heater and make it warm?  Use sutoobu.','ストーブを点けて暖かくしましょうか。','Sutoobu o tsukete atatakaku shimashoo ka.  (to express the idea of changing something, using an i adjective, remove the final i, add ku and then add suru)  (atatamemashoo, not OK,  atatamemasu just used for water etc., not for rooms)',5,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, please do.','ええ、お願いします。','Ee, onegai shimasu.',6,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, the floor cushions aren''t gone out yet, right?  Meaning, they aren’t out yet.','あっ、座布団がまだ出ていませんね。','A, zabuton ga mada dete imasen ne.',7,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me but please take them out of the bedding closet.','すみませんが押入れから出してください。','Sumimasen ga oshiire kara dashite kudasai.   (oshiire = closet,  keep oshi [honorable sheets], red, in closet) (cf. todana = cupboard)',8,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey, as for the sweets we received yesterday, where?  (use nee,  plain speech)','ねぇ、昨日もらったお菓子はどこ。','Nee, kinoo moratta okashi wa doko.',9,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, as for that over there, the kids ate it completely, but sponge cake is bought and exists.  Use kodomotachi.  Kodomotachi is the subject.  Use keredo. Use plain speech for the 1st clause.','ああ、あれは子供たちが食べてしまったけれど、カステラが買ってあります。','Aa, are wa kodomotachi ga tabete shimatta keredo, kasutera ga katte arimasu.   [katte arimasu is an example of the transitive (aru) rule:  when describing a fixed condition, the te form of atransitive verb is followed by aru, not iru]  (spongecake is like custard)',10,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s being entered in that place’s cupboard, for sure.','そこの戸棚に入っていますよ。','Soko no todana ni haitte imasu yo.  (keep the toe of Dana in the cupboard)  cf. hondana = bookcase',11,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey, Webb (meaning Mr. & Mrs. Webb, in this case), well honorably came, meaning welcome.  Use yaa to mean hey.','やあ、ウエッブさんよくいらっしゃいました。','Yaa, webbu san yoku irasshaimashita.  (OK to say irasshaimase in this situation, but not yoku irasshaimase, which means they honorably come often)',12,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead, please honorably rise, meaning please come in.','どうぞお上がりください。','Doozo oagari kudasai.  (from agaru, to enter a house) (doozo ohairi kudasai, also OK)',13,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will commit a discourtesy, meaning I''m sorry to bother you.','失礼します。','Shitsurei shimasu.',14,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s a splendid honorable home, right?','りっぱなお宅ですね。','Rippa na otaku desu ne.',15,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I’m  overwhelmed, meaning you''re so kind to say so.  (could also mean excuse me or I''m sorry)','おそれいります。','Osore irimasu.  (‘O Sole Mio [the song] irritates the master to the point that he falls down and says ‘I’m overwhelmed’)',16,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To this way go ahead.','こちらへどうぞ。','Kochira e doozo.',17,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Honorable tea, at least, how is it?  Use a polite word for how.','お茶でもいかがですか。','Ocha demo ikaga desu ka.  (demo means ‘but’ and also ‘at least’)',18,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Or, is coffee probably good?  Use a polite word for good.  Coffee is the subject.','それともコーヒーがよろしいでしょうか。','Soretomo koohii ga yoroshii deshoo ka.',19,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''ll take tea.  Very polite.','お茶をいただきます。','Ocha o itadakimasu.',20,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sweets as well, go ahead.','お菓子もどうぞ。','Okashi mo doozo.',21,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s sweet and delicious, huh?','甘くておいしいですね。','Amakute oishii desu ne.',22,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another cup, tea shall I make?  Use ireru.','もういっぱいお茶を入れましょうか。','Moo ippai ocha o iremashoo ka.  (hai is a counter used for cups, bowls etc,  ippai, nihai, sanbai etc.)',23,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No I''m already fine.','いいえ、もう結構です。','Iie moo kekkoo desu.',24,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It was a feast.','ご馳走さまでした。','Gochisoo sama deshita.',25,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It was very delicious.','とてもおいしかったです。','Totemo oishikatta desu.',26,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this house, about when was it accomplished?  Soften this.','この家はいつ頃できたんですか。','Kono ie wa itsu goro dekitan desu ka.',27,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It will already become 10 years, right?','もう十年になりますね。','Moo juu nen ni narimasu ne.',28,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I do a humble tour of the inside of the house?','家の中をご案内しましょうか。','Ie no naka o go annai shimashoo ka.  (tour the Annals of Internal Medicine)',29,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Wow, thanks. (use ara)  As for to see a Japanese house, it’s the first time, for sure.  Use no to make a noun phrase.  Use nan to soften this.','あら、どうも。日本の家を見るのは初めてなんですよ。','Ara doomo.  Nihon no ie o miru no wa hajimete nan desu yo.  (ara, with this meaning, used by women)',30,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s messy, but go ahead.  Use kedo.','散らかっていますけど、どうぞ。','Chirakatte imasu kedo, doozo. (from chirakaru)',31,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Here is the living room, and next door is the dining room.   Use de to mean and.  There are 2 subjects.','ここが居間でとなりが食堂です。','Koko ga ima de, tonari ga shokudoo desu.  (ima [now] I''m in the living room,  I have shokuji in the shokudoo [dining room])  (shokudoo also = diner)',32,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A pretty picture is hanging, right?','きれいな絵が掛かっていますね。','Kirei na e ga kakkate imasu ne.  (from kakaru, intransitive)',33,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s embarrassing, but I painted it for sure.  (using a man''s word for I, soften this twice)','はずかしいんですが、ぼくが描いたんですよ。','Hazukashiin desu ga, boku ga kaitan desu yo.  (from hazukashii, embarrassed or embarrassing) (I''m embarrassed about my ha [teeth], which are muzukashii)',34,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Eh, is that true?  You''re honorably skillful, right?','えっ、ほんとですか。お上手ですね。','E, hontoo desu ka.  Ojoozu desu ne.',35,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, I’m overwhelmed, meaning it’s good of you to say so.','いやー、おそれいります。','Iyaa, osoreirimasu.  (could also mean excuse me or I''m sorry)',36,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well (sigh), shall we go to the 2nd floor?','さあ、二階へ行きましょうか。','Saa, nikai e ikimashoo ka.  (saa doesn’t always imply a sigh,  in this case, it just means ‘well’)',37,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Here it’s the son''s room.  Here is the subject.','ここが息子の部屋です。','Koko ga musuko no heya desu.',38,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey!  Again, the light has been turned on by someone.  Use are.   Plain speech.','あれ、又、電気が点けてある。','Are, mata denki ga tsukete aru.  (denki wo, also OK, but not as good)',39,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s bad.  (plain speech with an intensifier)','だめだなぁ。','Dame da naa.',40,12);
