INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In ‘te aru’ constructions, e.g., when using shimete aru to say that something is closed, the direct object of the transitive verb may be indicated by either','を。が。','O.  Ga',294,12);
