INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the honorable cups and plates, are they already out?','コップとお皿はもう出ていますか。','Koppu to osara wa moo dete imasu ka.  (plates are for osarada [honorable salad])',306,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, they''ve been put out by someone for sure.','ええ、出してありますよ。','Ee, dashite arimasu yo.',307,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The space heater is on, right?  Shall I turn it off?','ストーブがついていますね。消しましょうか。','Sutoobu ga tsuite imasu ne.  Keshimashoo ka.',308,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, please don''t turn it off.  Because the room is cold, it is turned on.  Imply that I did it.  Soften this.','いいえ、消さないでください。部屋が寒いから点けているんです。','Iie, kesanai de kudasai.  Heya ga samui kara, tsukette irun desu.  (to make a negative request, follow the negative plain speech form of the verb with de kudasai)  (iru, as opposed to aru, implies that the speaker turned it on.)',309,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The lights are also on, for sure.','電気もついていますよ。','Denki mo tsuite imasu yo.',310,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, as for the lights, please turn them off.','ああ、電気は消してください。','Aa, denki wa keshite kudasai.',311,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The window is open, right.  Will I close it?','窓が開いていますね。閉めますか。','Mado ga aite imasu ne.  Shimemasu ka.  (this means, shall I close it?)',312,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A little, because the air is bad, it is opened.  Use sukoshi.  Imply that the speaker did it.  Soften the 2nd clause.','少し空気が悪いからあ開けているんです。','Sukoshi kuuki ga warui kara, akete irun desu.  (the air is full of cookies)  (cf. kukkii = cookie)',313,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please line up the cups and honorable plates on top of the table.','テーブルの上にコップとお皿を並べてください。','Teeburu no ue ni koppu to osara o narabete kudasai.',314,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Already they were lined up by someone for sure.  Plain speech.  Use a woman''s word for emphasis.','もう並べたわよ。','Moo narabeta wa yo.',315,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Some cute dolls are lined up, right?  Plain speech.  Use a woman''s word for emphasis.','かわいい人形が並んでいるわね。','Kawaii ningyoo ga narande iru wa ne.  (the nincompoops who eat gyoza collect dolls)',316,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I am collecting for sure.  ‘I’ is the subject.  Plain speech.  Use no to soften this.','私が集めているのよ。','Watashi ga atsumete iru no yo.',317,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for beer, did you put it in the refrigerator?','ビールは冷蔵庫に入れましたか。','Biiru wa reizooko ni iremashita ka.',318,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it''s already in.  (shortened for speech)','はい、もう入ってます。','Hai, moo haittemasu.',319,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Did it already become 4 o''clock?  2 responses. The first response is polite.  In the 2nd response, use plain speech.  Don’t use ka in the 2nd response.','もう四時になりましたか。もう四時になった。','Moo yoji ni narimashita ka.  Moo yoji ni natta.',320,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it still won’t become 4 o''clock.','いいえ、まだ四時になりません。','Iie, mada yoji ni narimasen.',321,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It still isn’t becoming 4 o''clock.','まだ四時になっていません。','Mada yoji ni natte imasen.',322,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Not yet.','まだです。','Mada desu.',323,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, not yet.  Use a grunt to mean no.   Don’t use desu.','うん、まだ。','Uun, mada.',324,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for lunch, did you already eat it?','昼ごはんはもう食べましたか。','Hirugohan wa moo tabemashita ka.',325,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for lunch, did you already eat it?  Plain speech.  Don’t use ka.','昼ごはんはもう食べた。','Hirugohan wa moo tabeta.',326,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I am not eating yet.','いいえ、まだ食べていません。','Iie, mada tabete imasen.',327,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I still am not eating.  Use a grunt to mean no.  Use a contracted form of ‘am not eating.’  Plain speech.','うん、まだ食べてない。','Uun, mada tabetenai.  (meaning, I haven’t eaten)',328,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I still am not buying.','いいえ、まだ買っていません。','Iie, mada katte imasen.  (meaning, I haven’t bought)',329,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes he already went.','はい、もう行きました。','Hai moo ikimashita.',330,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No he still isn''t going, meaning he hasn’t gone.','いいえ、まだ行っていません。','Iie, mada itte imasen.',331,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A verb that can be added to the te form of a verb to suggest that the action is done thoroughly and completely','しまう。','Shimau',332,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The child ate the sweets completely.','子供がお菓子を食べてしまいました。','Kodomo ga okashi o tabete shimaimashita.',333,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I already completely did  the cleaning also the laundry also.','もう掃除も洗濯もしてしまいました。','Moo sooji mo sentaku mo shite shimaimashita.',334,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I will now finish reading this book, please be waiting for a moment.','今この本を読んでしまうからちょっと待っていてください。','Ima kono hon o yonde shimau kara, chotto matte ite kudasai.',335,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In ordinary speech, te shimau is shortened to','ちゃう','Chau',336,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In ordinary speech, de shimau is shortened to','じゃう','Jau',337,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the letter, I already finished writing it.  Use the short form of te shimau.','手紙はもう書いちゃいました。','Tegami wa moo kai cha imashita.  (kaite shimau = kaichau) (kaite shimaimashita = kai chatta, also OK in this sentence)',338,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this book, I already finished reading it.  Use the short form of de shimau.  Plain speech.','この本はもう読んじゃった。','Kono hon wa moo yon jatta.',339,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Already, did you completely memorize hiragana?','もうひらがなを覚えてしまいましたか。','Moo hiragana o oboete shimaimashita ka.',340,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Already, did you completely memorize hiragana? Use the short form of te shimau.  Plain speech.','もうひらがなを覚えちゃった。','Moo hiragana o oboe chatta.',341,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, I memorized it completely.','はい、覚えてしまいました。','Hai, oboete shimaimashita.',342,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, I memorized it completely.  Use a grunt to mean yeah.   Use the short form of  te shimau.  Plain speech.','うん、覚えちゃった。','Un, oboe chatta.',343,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, not yet.  2 responses, the first one polite.  For the second response, use a grunt to mean no.  Don’t use desu.','いいえ、まだです。うん、まだ。','Iie, mada desu.  Uun, mada.',344,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will go to the post office, but are there any to-send, meaning outgoing, letters?  Use kedo.  Plain speech.  Don’t use wa or ga.','郵便局に行くけど出す手紙ある。','Yuubinkyoku ni iku kedo, dasu tegami aru.',345,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, be waiting for a moment.  (use a shortened form of be waiting)','じゃ、ちょっと待ってて。','Ja, chotto matte te.',346,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because this letter soon I will write completely. Plain speech.  Don’t use wo, wa or ga.','この手紙すぐ書いてしまうから。','Kono tegami sugu kaite shimau kara.',347,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will go to the library, but are there any to-take-back books?  Soften the first clause.  Use dakedo.  Plain speech. Don’t use wa or ga.','図書館に行くんだけど返す本ある。','Toshokan ni ikun dakedo, kaesu hon aru.  (your toe shows in that can,  don''t wear it to the library)',348,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I will now read this book completely, i.e. finish it.  2 responses, both are plain speech.  For the first response, use the longer form and for the second, the shorter form of the verbs.','今この本を読んでしまうから。今この本を読んじゃうから。','Ima kono hon o yonde shimau kara.Ima kono hon o yon jau kara.',349,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall we leave already?','もう出ましょうか。','Moo demashoo ka.  (here, moo = now or soon)',350,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will now finish drinking this tea.  2 responses, both plain speech.  For the first response, use the longer form and for the second response, the shorter form of the verbs.','今このお茶を飲んでしまう。今このお茶を飲んじゃう。','Ima kono ocha o nonde shimau.  Ima kono ocha o non jau.',351,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Already, someone will start the meeting, for sure.','もう会議を始めますよ。','Moo kaigi o hajimemasu yo. (in this sentence, moo = now, soon)',352,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Be waiting for a moment (shortened form).','ちょっと待ってて。','Chotto matte te.',353,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because now someone will do the copy completely.  Someone is understood.  Use suru.  2 responses, both plain speech.  For the first response, use the longer form and for the second, the shorter form of the verbs.','今コピーをしてしまうから。今コピーをしちゃうから。','Ima kopii o shite shimau kara.  Ima kopii o shi chau kara.',354,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won''t you go for a walk?  Plain speech.  Don’t use ka.','散歩に行かない。','Sanpo ni ikanai.',355,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I will completely wash the honorable plates, meaning the dishes.  2 responses, both plain speech.  For the first response, use the longer form and for the second, the shorter form of the verbs.','お皿を洗ってしまうから。お皿を洗っちゃうから。','Osara o arate shimau kara.  Osara o ara chau kara.  (arau = wash,  Arafat under table washing dishes)',356,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To express the idea of turning a noun into another noun, such as changing fruit to juice, or of changing something using a na adjective, such as making something clean, add this phrase to the resulting noun or na adjective','にする。にします。','Ni suru.  Ni shimasu',357,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To express the idea of changing something using an i adjective, such as making something warm, remove the final i & add this phrase to the i adjective','くする。くします。','Ku suru.  Ku shimasu',358,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this fish, since it’s fresh, let''s make sashimi out of it.','この魚は新しいからさしみにしましょう。','Kono sakana wa atarashii kara, sashimi ni shimashoo.  (sakana ga, not OK, because kono already makes it specific)',359,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''ll make the room warm.','部屋を暖かくします。','Heya o atatakaku shimasu.',360,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Adding ice, let''s make the juice cold.','氷を入れてジュースを冷たくしましょう。','Koori o irete juusu o tsumetaku shimashoo.  (they have a lot of ice in Corinth)',361,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Cleaning, I made the room clean.','掃除をして部屋をきれいにしました。','Sooji o shite heya o kirei ni shimashita.',362,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This suitcase is heavy, right?','このスーツケースは重いですね。','Kono suutsukeesu wa omoi desu ne.',363,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, taking out the books, let''s make it light.','じゃ、本を出して軽くしましょう。','Ja, hon o dashite, karuku shimashoo.',364,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me, I''m studying now.  Soften this.','すみません。今勉強しているんです。','Sumimasen, ima benkyoo shite irun desu.',365,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please make the radio''s sound small.','ラジオの音を小さくしてください。','Rajio no oto o chiisaku shite kudasai.',366,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, forgive me.','あっ、ごめんなさい。','A, gomen nasai.',367,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A little, the skirt is long, right?  Use sukoshi.','少しスカートが長いですね。','Sukoshi sukaato ga nagai desu ne.',368,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That''s so, huh?  Shall we shorten it a little?  Use sukoshi.','そうですね。少し短くしましょうか。','Soo desu ne.  Sukoshi mijikaku shimashoo ka.',369,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This bag is expensive, huh?  Won''t you make it a little cheaper and give?  Speaking to someone outside your in-group.','このバッグは高いですね。もう少し安くしてくださいませんか。','Kono baggu wa takai desu ne.  Moo sukoshi yasuku shite kudasaimasen ka.  (itadakemasen ka, also OK)  (Japanese people pronounce baggu ‘bakku’)',370,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The soup became cold, huh?','スープが冷たくなりましたね。','Suupu ga tsumetaku narimashita ne.',371,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, let''s make it a little warm.  Use sukoshi.','じゃ少し暖かくしましょう。','Ja sukoshi atatakaku shimashoo.  (atatamemashoo, also OK)',372,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the honorable customer is coming, please clean the room and make it clean.','お客さんが来るから部屋を掃除してきれいにしてください。','Okyakusan ga kuru kara, heya o sooji shite, kirei ni shite kudasai.  (not OK to reverse the word order, i.e., to say kirei ni shite, soji shite, as this sounds strange)',373,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I catch cold often, for sure.  (soften this)','よく風邪を引くんですよ。','Yoku kaze o hikun desu yo.',374,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, it would be better if you do sports and make your body durable, right?','じゃスポーツをして体を丈夫にしたほうがいいですね。','Ja supootsu o shite, karada o joobu ni shita hoo ga ii desu ne.',375,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This room is dark, huh?','この部屋は暗いですね。','Kono heya wa kurai desu ne.  (cf. kuroi = black,  karui = light, easy,  karai = spicy)',376,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It would be better if you turned on that place’s','そこのスタンドを点けて明るくしたほうがいいですね。','standing light and brightened it, right?Soko no sutando o tsukete, akaruku shita hoo ga ii desu ne.',377,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I got a lot of tangerines.','みかんをたくさんもらいました。','Mikan o takusan moraimashita.  (takusan no mikan o moraimashita, also OK)',378,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, let''s turn them into juice and drink them.','じゃ、ジュースにして飲みましょう。','Ja, juusu ni shite nomimashoo.',379,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s hot, right?  Shall I open the window?','暑いですね。窓をあけましょうか。','Atsui desu ne.  Mado o akemashoo ka.',380,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, please do.','ええ、お願いします。','Ee, onegai shimasu.',381,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s fine.','いいえ、結構です。','Iie, kekkoo desu.',382,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I hold that bag?','そのバッグを持ちましょうか。','Sono baggu o mochimashoo ka.  (this means, shall I carry it?)  (Japanese people pronounce baggu ‘bakku’)',383,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, thanks.  Excuse me.','あっ、どうもすみません。','A! Doomo sumimasen.',384,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I see you off by car until the station?','駅まで車で送りましょうか。','Eki made kuruma de okurimashoo ka.  (from okuru,as opposed to okureru meaning to get late,  okuru also = to send) (I send the o [honorable] curry)',385,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s fine.  Because I will walk.','いいえ、結構です。歩きますから。','Ie, kekkoo desu.  Arukimasu kara.',386,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I explain it in English?','英語で説明しましょうか。','Eigo de setsumei shimashoo ka.  (explain why the set has Sue and May on it)',387,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, please do.  Because, as for Japanese,  I hardly don’t understand.  (use node)','はい、お願いします。日本語はあまりわかりませんので。','Hai onegai shimasu.  Nihongo wa amari wakarimasen node.',388,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I bring a fork?','フォークを持ってきましょうか。','Fooku o motte kimashoo ka.',389,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s fine.  I''m OK with chopsticks.  (use kekkoo and daijoobu)','いえ、結構です。はしで大丈夫です。','Ie, kekkoo desu.  Hashi de daijoobu desu.',390,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I attach a ribbon?','リボンを付けましょうか。','Ribon o tsukemashoo ka.',391,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, please do.  Because it''s a present.','はい、お願いします。プレゼントですから。','Hai onegai shimasu.  Purezento desu kara.',392,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I help?  Use tetsudau as a verb.','手伝いましょうか。','Tetsudaimashoo ka.  (tetsudau = to help, the tetanus that Superman gave my daughter was intended to help her)',393,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me. As for by myself, I can’t do it.  (soften this)','すみません。一人ではできないんです。','Sumimasen.  Hitori de wa dekinain desu.  (OK to omit wa) (jibun de, not OK, but jibun hitori de, OK)',394,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I treat you to dinner?  Use yuushoku.','夕食をご馳走しましょうか。','Yuushoku o gochisoo shimashoo ka.',395,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Wow, thank you a lot.  Thanks for the treat.  (abbreviated phrase)','わぁ、有難うございます。ご馳走さま。','Wa, arigatoo gozaimasu.  Gochisoo sama.',396,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Breakfast.  Use shoku in the next 3 responses.','朝食','Chooshoku (eat breakfast with Margaret Cho)',397,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Lunch','昼食','Chuushoku   (chew a lot during lunch)',398,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Dinner','夕食','Yuushoku (eat dinner with you)(cf. shoogakkoo = primary school, chuugakkoo = middle school, kookoo = high school) (cf. yuube = last night, yuugata = evening)',399,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Who is it?  (answering the door)','どなたですか。','Donata desu ka.',400,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('We were humbly waiting, meaning we''ve been expecting you.','お待ちしていました。','Omachi shite imashita.',401,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead, please come in.  2 responses','どうぞお上がりください。どうぞお入りください。','Doozo oagari kudasai.  Doozo ohairi kudasai.',402,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead to this way.','どうぞこちらへ。','Doozo kochira e.',403,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will commit a discourtesy, meaning I’ll inconvenience you.  Use de wa.  2 responses.','では失礼します。ではおじゃまします。','Dewa shitsurei shimasu.  Dewa ojama shimasu.',404,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, at this place, meaning at this moment, I''ll commit a discourtesy.  Because I''m hurrying.  (Use node.)','今日はここで失礼します。急いでいますので。','Kyoo wa koko de shitsurei shimasu.  Isoide imasu node.  (from isogu = isogimasu = to hurry) (because I''m isogashii [busy], I isogu [hurry])  (koko de = right now, literally ‘from here’)',405,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is a worthless thing but go ahead.  Don’t use wa or ga after ‘this.’','これつまらないものですが、どうぞ。','Kore tsumaranai mono desu ga, doozo.  (this is a standard phrase used when you give a gift)',406,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Thanks.  I’m overwhelmed.','どうもおろれいります。','Doomo osoreirimasu.   (‘O Sole Mio [the song] irritates the master to the point that he falls down and says ‘I’m overwhelmed’)',407,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead, please honorably sit.  Use a short version of this expression.  Imply that the person will sit on a chair or sofa.','どうぞお掛けください。','Doozo okake kudasai.  (sitting with a cake on your lap)  (doozo okake ni natte kudasai, also OK)',408,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please honorably sit.  Use a short version of this expression.  Imply that the person will sit on a zabuton.','お座りください。','Osuwari kudasai. (chanto suwatte kudasai is OK, but this means ‘sit nicely,’ spoken to a child,  suwatte kudasai, OK,  osuwari ni natte kudasai, also OK,  cannot say osuwatte kudasai or okakete kudasai)',409,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will commit a discourtesy, meaning excuse me, I will sit down.','失礼します。','Shitsurei shimasu.',410,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nothing exists, but go ahead.','何もありませんが、どうぞ。','Nani mo arimasen ga, doozo.  (this is a standard expression used when serving someone a meal)',411,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will humbly receive, meaning thanks, I will.','いただきます。','Itadakimasu.',412,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another cup or glass, how is it? Cup or glass is understood. Use a polite word for how.','もう一杯いかがですか。','Moo ippai ikaga desu ka.',413,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Another piece, how is it?  Piece is understood.  Use a polite word for how.','もう一ついかがですか。','Moo hitotsu ikaga desu ka.',414,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Thank you a lot.  I will humbly receive.','有難うございます。いただきます。','Arigatoo gozaimasu.  Itadakimasu.',415,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I''m already fine.  It was a treat.','いいえ、もう結構です。ご馳走さまでした。','Iie, moo kekkoo desu.  Gochisoo sama deshita.',416,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Gradually, I’ll commit a discourtesy, meaning I’ll be going.','そろそろ失礼します。','Soro soro shitsurei shimasu.  (this is a standard expression used before leaving)',417,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Still, won’t it be good,  meaning, can''t you say a little longer?  2 responses, for the 2nd response, use a polite word for good.','まだいいじゃありませんか。まだよろしいじゃありませんか。','Mada ii ja arimasen ka.  (also OK to say mada yokunai desu ka,  cannot say mada yoku arimasen ka)Mada yoroshii ja arimasen ka.',418,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, thank you very much for what you did.','今日はどうも有難うございました。','Kyoo wa doomo arigatoo gozaimashita.',419,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, the pleasure was all mine.','いいえ、こちらこそ。','Iie, kochira koso.  (kochira [this way] to see Kosovo is always a pleasure,  kochira = this way)',420,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Again, by all means,  go ahead, please honorably come.','又、ぜひどうぞいらしてください。','Mata, zehi doozo irashite kudasai.  (a zebra hides by all means)  (irashatte, also OK, but sounds weird)',421,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will commite a discourtesy.  Good-bye.  Good night.','失礼します。さようなら。おやすみなさい。','Shitsurei shimasu.  Sayoonara.  Oyasuminasai.',422,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('These are cookies I made, but go ahead.  Don’t use wa or ga after ‘these.’','これ私が作ったクッキーですが、どうぞ。','Kore watashi ga tsukutta kukkii desu ga, doozo.',423,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are they ones that you made?  Use ‘n to make a','あなたが作ったんですか。','noun phrase.Anata ga tsukuttan desu ka. (same as tsukutta no desu)  (not OK to omit ‘n or no) (you may combine a verb with ‘n or no to make a pronoun meaning ‘one’ or ‘ones’,  this makes a noun phrase)',424,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is coffee good?  Or is tea good?  Use a polite word for good.','コーヒーがよろしいですか、それともお茶がよろしいですか。','Koohii ga yoroshii desu ka.  Soretomo ocha gayoroshii desu ka.  (wa, not OK in these sentences, wa is too general here with choices, however, either sentence by itself is OK with wa, e.g., koohii wa yoroshii desu ka, or ocha wa yoroshii desu ka)',425,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it already became dark, gradually I’ll commit a discourtesy, meaning I''d better get going.','もう暗くなりましたから、そろそろ失礼します。','Moo kuraku narimashita kara, soro soro shitsurei shimasu.',426,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Already it will become half past 4, but, as for the son, he still doesn’t return from school.','もう四時半になりますが息子はまだ学校から帰りません。','Moo yojihan ni narimasu ga, musuko wa mada gakkoo kara kaerimasen.  (musuko ga, also OK)',427,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please look at the son''s room.','息子の部屋を見てください。','Musuko no heya o mite kudasai.',428,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Socks, magazines, balls etc. are messy.  Use ya, nado.','靴下や雑誌やボールなどが散らかっています。','Kutsushita ya zasshi ya booru nado ga chirakatte imasu.  (OK to omit ga when using nado)',429,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Moreover, as for on top of the bed, taken-off pajamas and a guitar are placed.','それにベッドの上には脱いだパジャマとギターが置いてあります。','Sore ni beddo no ue ni wa nuida pajama to gitaa ga oite arimasu.  (from nugu, nugimashita, to take off) (from oku, to place) (I took off my shirt because it had new goo in it)  (pajama to gitaa o oite arimasu, not as good, sounds strange)',430,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The room light is turned off  by someone, but the light on top of the desk is not turned off by anyone.  (use wa rather than ga [or wo] because you are contrasting 2 different expressions)','部屋の電気は消してありますが、机の上の電気は消してありません。','Heya no denki wa keshite arimasu ga, tsukue no ue no denki wa keshite arimasen.',431,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Moreover, the door also, the window also, are not closed (by anyone).','それにドアも窓も閉めてありません。','Sore ni doa mo mado mo shimete arimasen.',432,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The CD''s are neatly lined up by someone, but the books on top of the desk are not neatly lined up (by anyone).','CDはきれいに並べてありますが、机の上の本はきれいに並べてありません。','CD wa kirei ni narabete arimasu ga, tsukue no ue no hon wa kirei ni narabete arimasen.',433,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('On the wall, a paper is pasted up by someone.  Use ga rather than wo.','壁に紙がはってあります。','Kabe ni kami ga hatte arimasu.  (from haru, to put up or paste) (I paste in haru [Spring] because it''s humid)  (kami o hatte arimasu, not OK here, sounds strange)',434,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To that paper, ‘I won’t make a mess’ is written.','その紙に「散らかさない」と書いてあります。','Sono kami ni ‘chirakasanai’ to kaite arimasu.  (use ni to write to a paper)  (chirakasanai means ‘I won’t make a mess’)',435,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the honorable customer, why did he say he will return?   Use naze.','お客さんはなぜ帰ると言いましたか','Okyakusan wa naze kaeru to iimashita ka.  (naze kaeru to itta no desu ka, also OK)',436,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because it''s already 10 o''clock.','もう十時ですから。','Moo juuji desu kara.',437,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because it already became late.','もう遅くなりましたから。','Moo osoku narimashita kara.',438,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because there wasn''t a bus.  (it disappeared)','バスがなくなりましたから。','Basu ga naku narimashita kara.  (from nakunaru = to disappear)',439,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now apples three are entered in the refrigerator.','今、冷蔵庫にりんごが三つ入っています。','Ima reizooko ni ringo ga mittsu haitte imasu.',440,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now in the refrigerator, not even one apple is entered .','今、冷蔵庫にりんごが一つも入っていません。','Ima reizooko ni ringo ga hitotsu mo haitte imasen.',441,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a Japanese language letter, I still have never written one.','日本語の手紙はまだ書いたことがありません。','Nihongo no tegami wa mada kaita 	koto ga arimasen.  (OK to use tegami o also)  (koto wa arimasen, also OK, but not as good)',442,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a Japanese language letter, I still have never read one.','日本語の手紙はまだ読んだことがありません。','Nihongo no tegami wa mada yonda koto ga arimasen.',443,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a Japanese language letter, I still have never received one.','日本語の手紙はまだもらったことがありません。','Nihongo no tegami wa mada moratta koto ga arimasen.',444,12);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Chapter 11.  Literally, ‘# 11 Chapter.’','第11章。','Dai juisshoo. (dai means number, shoo means chapter)',1,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hello.  (on the phone)','もしもし。','Moshi moshi.',2,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, this is Abe Industries, business, first section.','はい、安部産業営業一課です。','Hai, Abe Sangyoo Eigyoo ikka desu.  (dai ikka desu, also OK)',3,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I am humbly called Yamakawa Trading’s Tsukino, but does Section Manager’s Kitamura honorably exist?  Don’t use wa or ga after Kitamura.','山川貿易の月野と申しますが課長の北村さんいらっしゃいますか。','Yamakawa Boeki no Tsukino to mooshimasu, ga kachoo no Kitamura san irasshaimasu ka.  (booeki = international trade or commerce,  the international trading company trades bows and arrows at the eki,  cf. shoosha = trading company)  (mooshimasu = to say humbly, cf. iimasu = to say)',4,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it''s Mr. Sukino, right?','はい、すきのさんですね。','Hai, ‘Sukino’ san desu ne.',5,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s ‘Tsukino.’','いいえ、つきのです、','Iie, ‘Tsukino’ desu.',6,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What?  I’m overwhelmed, but once again I beg.  Use haa.','はぁ、おそれいりますがもう一度お願いします。','Haa?  Osore irimasu ga, moo ichido onegaishimasu.',7,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tsukino.  It’s the Tsu from ta,chi,tsu,te,to.','月野。た、ち、つ、て、と、の’つ’です。','‘Tsukino.’  Ta, chi, tsu, te, to no ‘Tsu’ desu.',8,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Kuroda, the literature beside that phone, 3 apiece, hurriedly, do copies in advance.  Use yoko.  Use bu as a counter.  Plain speech.','黒田さん、その電話の横の資料を三部ずついそいでコピーしておいて。','Kuroda san, sono denwa no yoko no shiryoo o sanbu zutsu isoide kopii shite oite.  (shiryoo = literature, documents) (literature about shiroi [white] cheerios,  cf. shiro = white) (bu is a counter used for magazines, brochures, parts of a novel.)  (oite from oku, meaning ‘to place’, when used after a te form, oku means to have something ready in advance)',9,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because Tsukino of Yamakawa Trading will honorably come.  Plain speech.','山川貿易の月野さんがいらっしゃるから。','Yamakawa Booeki no Tsukino san ga irassharu kara.',10,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it this blue one?','この青いのですか。','Kono aoi no desu ka.',11,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, that white one.','いや、その白いの。','Iya, sono shiroi no.',12,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('And then, as for the Osaka branch office report?  Use sore kara.  Use hookoku.','それから、大阪支社の報告は。','Sore kara, oosaka shisha no hookoku wa.  (branch office is a ‘she’ office run by women,  cf. shiten = branch store) (hookoku = report,  report on a holistic practitioners’ koku [country]) (cf. kookoku = advertising)',13,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, it is being entered in that place’s cabinet’s second drawer for sure.','あ、そこのキャビネットの二番目の引き出しに入っていますよ。','A, soko no kyabinetto no niban me no hikidashi ni haitte imasu yo.  (me is used as a counter for ordinal numbers in a series, e.g., daiissho me = chapter one) (the toy car has a hickey on the dash, so keep it in the drawer)',14,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, here it is, here it is.  Report what you see as an exclamation.  Plain speech.','あ、あった、あった。','A, atta, atta.  (atta = arimashita,  use the past tense to report what you see as an exclamation – see Lesson 20)',15,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, this also I will request, for sure.  (plain speech)','じゃ、これも頼むよ。','Ja, kore mo tanomu yo.  (tanomu  = request,  request a tan omelet)',16,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the copies, put them on top of my desk in advance.  Use a man’s word for ‘my.’ (plain speech)','コピーは僕の机の上に置いておいて。','Kopii wa boku no tsukue no ue ni oite oite.  (the 1st oite from oku = to place,  the 2nd oite from oku = to do in advance)',17,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Can you do it by 2 o’clock?  (plain speech)','2時までにできる。','Niji made ni dekiru?  (made ni means ‘by’ a certain time)',18,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, I’ll do them in a hurry.','はい、急いでします。','Hai, isoide shimasu.',19,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will do humble introductions.','ご紹介します。','Go shookai shimasu.  (introduce Shokei and Kyle) (cf. kyookasho, textbook)',20,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this way, it’s the Michael Webb-called and next-time-to-accomplish, in-group’s, America branch office’s manager.  Use a humble phrase to say ‘called.’  Use te to mean and.','こちらはマイケルウエッブと申しまして、今度できるうちのアメリカ支社の担当です。','Kochira wa Maikeru Uebbu to mooshimashite, kondo dekiru, uchi no Amerika shisha no tantoo desu.  (tantoo = to be in charge,  put Tonto who tans his toes in charge)  (cannot say to mooshimatte,  moosu = mooshimasu = to humbly say or be called,  the te form is mooshimashite) (don’t use san because Michael is in the speaker’s in-group,  use moosu for the same reason)',21,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because Japanese is OK (meaning he speaks OK).  Don’t use wa or ga.','日本語大丈夫ですから。','Nihongo daijoobu desu kara.',22,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, as for this, good. Use doomo.  (meaning, it’s good to meet you,  this also means what a surprise, how nice)','あっ、これはどうも。','A, kore wa doomo.',23,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I am humbly called YamakawaTrading’s Tsukino.  Use watakushi.  Don’t use wa or ga.','私、山川貿易の月野と申します。','Watakushi Yamakawa Booeki no Tsukino to mooshimasu.',24,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead, be good to me.  Use the shorter form of this expression.','どうぞよろしく。','Doozo yoroshiku.',25,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s Michael Webb, meaning I’m  Michael Webb.','マイケルウエッブです。','Maikeru Uebbu desu.',26,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Pleased to meet you.','初めまして。','Hajimemashite.',27,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for earlier, on the phone, I committed a discourtesy.','さっきは電話で失礼しました。','Sakki wa denwa de shitsurei shimashita.  (saki hodo wa = a little while ago, also OK in this sentence,  cannot say sakki hodo, with 2 k’s in sakki)',28,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you read it as ‘Tsukino yoo’?  soften this.','月野洋と読むんですか。','‘Tsukino yoo’ to yomun desu ka.',29,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, you write it as ‘yoo’ and you read it as ‘Hiroshi.’ Soften this.  Use te to mean and.','いや、洋と書いてひろしと読むんです。','Iya, ‘yoo’ to kaite, ‘Hiroshi’ to yomun desu.',30,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I see.  Japanese people’s names are difficult, huh?','なるほど、日本人の名前は難しいですね。','Naruhodo.  Nihonjin no namae wa muzukashii desu ne.',31,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Chairman of the board.','会長。','Kaichoo (Kyle is Chairman)',32,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Company president.','社長。','Shachoo',33,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Senior managing director.','専務。','Senmu (the senior managing director has sen [1,000] moo cows)',34,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Managing director.','常務。','Joomu (Joe has a moo cow & is managing director)',35,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Division manager','部長。','Buchoo',36,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Section manager','課長。','Kachoo',37,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Assistant manager','係長。','Kakarichoo',38,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Foreman','主任。','Shunin (also = person in charge) (the person in charge shoos nin [people] away) (cf. shumi = hobby,   Grandpa shooed me away when my hobby got annoying)  (cf. shoonin = merchant,  the merchant shows nin [people] merchandise)',39,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Who, to whom, called on the phone?','誰が誰に電話を掛けましたか。','Dare ga dare ni denwa o kakemashita ka.',40,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What did Kitamura request of Kuroda?','北村さんは黒田さんに何を頼みましたか。','Kitamura san wa Kuroda san ni nani o tanomimashita ka.',41,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the white literature, where was it being placed?','白い資料はどこに置いてありましたか。','Shiroi shiryoo wa doko ni oite arimashita ka.',42,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Kuroda, in what manner will she place the copied literature?  Use dooshite.','黒田さんはコピーした資料をどうしておきますか。','Kuroda san wa kopii shita shiryoo o dooshite okimasu ka.  (kopii o shita, also OK) (could also mean, why will she place it) (cannot substitute dooyatte for dooshite in this case, as it sounds wrong)',43,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have Tsukino and Webb met at before?','月野さんとウエッブさんは前に会ったことがありますか。','Tsukino san to Uebbu san wa mae ni atta koto ga arimasu ka.',44,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tsukino''s name, is it Yoo, is it Hiroshi?','月野さんの名前はようですか、ひろしですか。','Tsukino san no namae wa ‘Yoo’ desu ka, ‘Hiroshi’ desu ka.',45,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Daughters and sons, 3 each exist, meaning I have 3 of each.','娘と息子が三人ずついます。','Musume to musuko ga sannin zutsu imasu.  (not OK to substitute wa for ga here, since it would sound as if you were talking about someone else’s kids)',46,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Writing new kanji 10 times each, I practice.  Use kai.  Plain speech.','新しい漢字を十回ずつ書いて練習する。','Atarashii kanji o jikkai zutsu kaite renshuu suru.  (jukkai also OK)  (kai = ‘times,’ similar to do,  juudo doesn’t sound as good as jikkai, or jikkai, in this sentence )',47,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('62-yen stamps and 200-yen stamps, 5 of each please.','６２円の切手と200円の切手を5枚ずつください。','Roku juu ni en no kitte to nihyaku en no kitte ogomai zutsu kudasai.',48,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Altogether, it comes to 1310 yen.','全部で1310円になります。','Zenbu de sen sambyaku juu en ni narimasu.',49,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please insert, or add, a little at a time.','少しずつ入れてください。','Sukoshi zutsu irete kudasai.',50,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What did he receive?','何をもらいましたか。','Nani o moraimashita ka.',51,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Apple and tangerine, he received one each.','りんごとみかんを一つずつもらいました。','Ringo to mikan o hitotsu zutsu moraimashita.',52,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Altogether he received two.','全部で二つもらいました。','Zenbu de futatsu moraimashita.	  (you don’t need to use ‘wo’ after numbers)',53,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for honorable children, how many people is it?','お子さんは何人ですか。','Okosan wa nannin desu ka.',54,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Girls and boys, two each exist.','女の子と男の子が二人ずついます。','Onna no ko to otoko no ko ga futari zutsu imasu.',55,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for honorable sushi, what did you eat?','お寿司は何を食べましたか。','Osushi wa nani o tabemashita ka.',56,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Fatty tuna and shrimp 3 apiece I ate.','トロとエビを三つずつ食べました。','Toro to ebi o mittsu zutsu tabemashita.',57,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Altogether I ate 6.','全部で六つ食べました。','Zenbu de muttsu tabemashita.',58,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for drinks, what will you choose?','飲み物は何にしますか。','Nomimono wa nani ni shimasu ka.  (nan ni, also OK)  (ni suru is used after a noun or pronoun to mean ‘choose’ – see the discussion in Lesson 12)',59,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Beer and juice 4 bottles each, I beg.','ビールとジュースを4本ずつお願いします。','Biiru to juusu o yonhon zutsu onegai shimasu.',60,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Altogether, they are 8 bottles.','全部で8本です。','Zenbu de happon desu.',61,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Shall I do copying?','コピーしましょうか。','Kopii shimashoo ka.  (kopii o, also OK)',62,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, the literature, 10 sheets each, please do copying.','えぇ、資料を十枚ずつコピーしてください。','Eee, shiryoo o juumai zutsu kopii shite kudasai.',63,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Altogether, it''s 20 sheets.','全部で20枚です。','Zenbu de nijuumai desu.',64,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Did you get stamps?  (soften this)','切手をもらったんですか。','Kitte o morattan desu ka.',65,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, I got 5 each of the 62-yen','ええ、62円のと41円のを5枚ずつもらったんです。','and 41-yen ones.  (soften this)Ee, lokujuu ni en no to yonjuu ichi en no o gomai zutsu morattan desu.',66,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To show that an action is being done in preparation for future use, use the te or de form followed by','おく。','Oku.',67,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since a meeting exists, let''s copy the literature in advance.','会議があるから資料をコピーしておきましょう。','Kaigi ga aru kara, shiryoo o kopii shite okimashoo.',68,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for tomorrow, since a test exists, please do a thorough review in advance.  Use shiken.  Use yoku to mean thorough.','明日は試験があるからよく復習しておいてください。','Ashita wa shiken ga aru kara, yoku fukushuu shite oite kudasai.  (fukushuu suru = to review lessons,  fuku [blow] a shuu [week] on a review)  (cf. yoshuu suru = prepare for new lessons, study ahead,  the yodeler studies ahead one shuu [week])',69,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the planes will get crowded, it would be better to make the reservation at 3 months in advance, for sure.','飛行機は込むから3ヶ月前に予約しておいたほうがいいですよ。','Hikooki wa komu kara, sanka getsu mae ni yoyaku shite oita hoo ga ii desu yo.  (hikooki ga, also OK,  wa implies that planes in general get crowded)',70,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Honorable guests will come.  What will they do in advance?','お客さんが来ます。何をしておきますか。','Okyaku san ga kimasu.  Nani o shite okimasu ka. (shite okimasu = to do in advance,  you cannot say shite oite imasu,  however, you may sometimes say shite oite okimasu = to do in advance in advance)',71,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hiroshi will straighten up the room in advance.','ひろしは部屋を方づけておきます。','Hiroshi wa heya o katatzukete okimasu.  (Hiroshi ga, also OK)  (from katatzukeru = to put in order or straighten up) (a kata [person], in a zoo kennel, straightens up,  kata = honorific word for person)',72,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Honorable Mother will do some cooking in advance.','お母さんは料理をしておきます。','Okaasan wa ryoori o shite okimasu.',73,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Honorable Father will put beer in the refrigerator in advance.','お父さんは冷蔵庫にビールを入れておきます。','Otoosan wa reizooko ni biiru o irete okimasu.',74,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ichiro will clean the front hallway in advance.','一郎は玄関を掃除しておきます。','Ichiroo wa genkan o sooji shite okimasu.  (Genghis Khan is standing in the front hallway)',75,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yurie will wipe the table in advance.','ゆりえはテーブルを拭いておきます。','Yurie wa teeburu o fuite okimasu.  (from fuku = to wipe or to blow)  (cf. fukushuu = review)',76,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will travel.  At before that, what kinds of things will I do in advance?','旅行をします。その前にどんなことをしておきますか。','Ryokoo o shimasu.  Sono mae ni donna koto o shite okimasu ka.',77,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will put my baggage in my suitcase in advance.','スーツケースに荷物を入れておきます。','Suutsukeesu ni nimotsu o irete okimasu.',78,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will reserve a hotel in advance.','ホテルを予約しておきます。','Hoteru o yoyaku shite okimasu.',79,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will buy a ticket in advance.','切符を買っておきます。','Kippu o katte okimasu.',80,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will look at a map in advance.','地図を見ておきます。','Chizu o mite okimasu.',81,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will marry.  At before that, what kinds of things will I do in advance?','結婚します。その前にどんなことをしておきますか。','Kekkon shimasu.   Sono mae ni donna koto o shite okimasu ka.',82,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will rent a room in advance.','部屋を借りておきます。','Heya o karite okimasu.',83,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will buy furniture in advance.','家具を買っておきます。','Kagu o katte okimasu.  (kagu = furniture,  the kagi was under the furniture,  kagi = key)',84,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will send wedding’s invitations in advance.  Use dasu.','結婚式の招待状を出しておきます。','Kekkon shiki no shootaijoo o dashite okimasu.  (show your tie, Joe, if you want an invitation)',85,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A Japanese language test exists.  At before that, what kinds of things will I do in advance.  Use tesuto.','日本語のテストがあります。その前にどんなことをしておきますか。','Nihongo no testo ga arimasu.  Sono mae ni donna koto o shite okimasu ka.',86,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will review the textbook in advance.  Use tekisuto.','テキストを復習しておきます。','Tekisuto o fukushuu shite okimasu.  (cf. kyookasho = textbook,  kyookai [churches], are shown in art textbooks)',87,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will listen to the tapes in advance.','テープを聞いておきます。','Teepu o kiite okimasu.',88,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will study kanji in advance.','漢字を勉強しておきます。','Kanji o benkyoo shite okimasu.',89,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Which form of the verb can be used as an adverb?','','The te or de form',90,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Riding by train, let’s go, meaning ‘let''s go riding by train.’','電車に乗って行きましょう。','Densha ni notte ikimashoo.  (from noru)',91,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('We went running until the station.','駅まで走って行きました。','Eki made hashitte ikimashita.  (from hashiru)',92,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''ll copy it hurriedly.','急いでコピーします。','Isoide kopii shimasu.  (from isogu)',93,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''ll visit you delightedly, meaning with pleasure.  You is understood.  Use ukagau.','喜んで伺います。','Yorokonde ukagaimasu.  (from yorokobu, ukagau = humble form of ‘to visit’) (visit UK and again au [meet])  (cf. tazuneru, a less humble form of visit,  not OK to substitute tazunemasu for ukagaimasu here,  it would sound like you were saying ‘I will ask you,’ since tazuneru also = to ask)',94,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I overslept, I left home in a frenzy.  Use node.','寝坊したのであわてて家を出ました。','Nebooshita node awatete uchi o demashita.  (from neboo suru = to oversleep,  awateru = to act in a frenzy) (neru [sleep] was boring so I overslept) (awakened by a terrorist, I was in a frenzy)',95,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the movie theater was crowded, I watched the movie standing up.  Use node.','映画館が込んでいたので映画を立って見ました。','Eigakan ga konde ita node eiga o tatte mimashita.  (from tatsu)',96,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the thin buckwheat noodles, I will eat them using hashi.','そばははしを使って食べます。','Soba wa hashi o tsukatte tabemasu.  (from tsukau) (hashi de tabemasu, also OK) (soba = thin buckwheat noodles, served dry, udon = thick white noodles, served wet,  raamen = Chinese style noodles in broth)',97,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since there isn''t time, let''s go riding in a taxi.  Use plain speech for the 1st clause.','時間がないからタクシーに乗って行きましょう。','Jikan ga nai kara, takushii ni notte ikimashoo.',98,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the station, since it’s close, let''s go walking.','駅は近いから歩いて行きましょう。','Eki wa chikai kara aruite ikimashoo.  (ga, also OK)',99,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the small characters, I''ll look at them wearing glasses.','小さい字はめがねを掛けて見ます。','Chiisai ji wa megane o kakette mimasu.  (chiisai ji o also OK)',100,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the friend is waiting, I went running.  Use node.','友達が待っているので走って行きました。','Tomodachi ga matte iru node, hashitte ikimashita.  (from hashiru)  (matte ita also OK)',101,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you return from school until the house walking?','学校から家まで歩いて帰りますか。','Gakkoo kara ie made aruite kaerimasu ka.',102,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I return riding a train.','いいえ、電車に乗って帰ります。','Iie, densha ni notte kaerimasu.',103,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Japanese, how doing do you memorize it?  Use yaru.','日本語はどうやって覚えますか。','Nihongo wa doo yatte oboemasu ka.',104,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I memorize listening to tapes.','テープを聞いて覚えます。','Teepu o kiite oboemasu.',105,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Webb, how doing does he study katakana?  Use yaru.','ウエッブさんはどうやってカタカナを勉強しますか。','Uebbu san wa dooyatte katakana o benkyoo shimasu ka.  (cannot say dooshite rather than dooyatte, as that would mean why,  dono yoo ni shite katakana o benkyoo shimasu ka, is OK)',106,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He studies looking at inside-the-train ads.','電車の中の広告を見て勉強します。','Densha no naka no kookoku o mite benkyoo shimasu.(cf. kookoo = high school)',107,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To indicate the time by which an action is','までに。','completed, use a plain non-past verb form, or various time words, followed byMade ni',108,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the copying, will it be ready by 10 o''clock?  Plain speech.','コピーは十時までにできる。','Kopii wa juuji made ni dekiru?  (ga, also OK)',109,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Can you do overtime work until 10 o''clock?  Plain speech.  Don’t use wa or ga.','十時まで残業できる。','Juuji made zangyoo dekiru?  (zangyoo ga, also OK) (overtime work on Zanker Road making geography textbooks ) (Note the difference between made = until, and made ni = by)',110,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since a test exists next week, by then, I want to memorize these kanji.  Use tesuto.  Soften the last verb.','来週テストがありますから、それまでにこの漢字を覚えたいんです。','Raishuu tesuto ga arimasu kara, sore made ni, kono kanji o oboetain desu.  (cannot say sono made ni, although you could say sono koro made ni = by about that time)  (also OK to substitute either sono mae ni, or sono toki made ni, for sore made ni)',111,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since there''s a test next week, let''s study together until then.','来週テストがありますから、それまで一緒に勉強しましょう。','Raishuu tesuto ga arimasu kara, sore made issho ni benkyoo shimashoo.',112,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for by the time the honorable guest comes, it will probably finish.  Time is understood.','お客さんが来るまでには終わるでしょう。','Okyaku san ga kuru made ni wa, owaru deshoo.',113,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me.  I''m hurrying, but will these shirts accomplish by the day after tomorrow, meaning will they be ready?  Soften the 1st verb.  Don’t use wa or ga after shirts.','すみません。急いでいるんですがこのシャツあさってまでにできますか。','Sumimasen.  Isoide irun desu ga, kono shatsu asatte made ni dekimasu ka.',114,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s the day after tomorrow, huh?  Yes, they will accomplish for sure, meaning they’ll be done.','あさってですね。はい、できますよ。','Asatte desu ne.  Hai, dekimasu yo.',115,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for by the day after tomorrow, a little bit ...','あさってまでには、ちょっと...','Asatte made ni wa, chotto ...  (meaning, they won’t be ready)',116,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please come to Ueno station by 6:40.','6時40分までに上野駅に来てください。','Rokuji yonjuppun made ni Ueno Eki ni kite kudasai.  (yoji = 4:00, yonjuu = 40)',117,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I was living in Roppongi until last year.','去年まで六本木に住んでいました。','Kyonen made Roppongi ni sunde imashita.',118,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since summer vacation is from the day after tomorrow, I''d like the work to finish by tomorrow I think.  Use wo after work.','あさってから夏休みですから、仕事を明日までに終わりたいと思います。','Asatte kara natsuyasumi desu kara, shigoto o ashita made ni owaritai to omoimasu.  (shigoto ga, also OK) (natsuyasumi ga asatte kara dakara, also OK)',119,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('By the time the honorable guest comes, I will clean the room in advance.','お客さんが来るまでに部屋を掃除しておきます。','Okyakusan ga kuru made ni, heya o sooji shite okimasu.',120,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the report, will it accomplish by 3 o''clock?  Use hookokusho.','報告書は3時までにできますか。','Hookokusho wa sanji made ni dekimasu ka.',121,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I''ll do other work until 2 o''clock ...','他の仕事を2時までしますから...','Hoka no shigoto o niji made shimasu kara ...  (meaning, I won’t be able to do it)',122,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Until I go, please be waiting, huh?  I is the subject.','私が行くまで待っていてくださいね。','Watashi ga iku made matte ite kudasai ne.',123,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I''ll go hurriedly by 2 o''clock.','急いで2時までに行きますから。','Isoide niji made ni ikimasu kara.',124,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Sundays, until lunch is ready, I’m sleeping for sure.  Soften the last verb.','日曜日は昼ごはんができるまで寝ているんですよ。','Nichiyoobi wa hirugohan ga dekiru made nete irun desu yo.',125,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for by about 10 o''clock, it would be better to get up for sure.','10時ごろまでには起きたほうがいいですよ。','Juuji goro made ni wa, okita hoo ga ii desu yo.',126,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In the following section, you are in an office, using very polite speech with customers and very humble speech to refer to yourself and your co-workers.','はい、山田でございます。','Answering the phone.   Yes, this is Yamada.  Use a humble word to say ‘it is.’Hai, Yamada de gozaimasu.',127,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This is Harada, but I beg Tanaka, meaning may I speak to him?','原田ですが、田中さんをお願いします。','Harada desu ga, Tanaka san o onegaishimasu.  (o is optional)',128,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, please wait a moment.  Use a very polite form of this expression.','はい、少々お待ちください。','Hai, shooshoo omachi kudasai.',129,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s Tanaka, but ...','はい、田中ですが。','Hai, Tanaka desu ga.',130,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''m sorry to have kept you waiting.  It’s Tanaka.','お待たせしました。田中です。','Omatase shimashita.  Tanaka desu.',131,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is leaving the seat humbly, but ...  Use hazusu.','田中は席をはずしておりますが。','Tanaka wa seki o hazushite orimasu ga.  (seki o hazusu = to leave one''s seat) (hazards of usury caused me to leave my seat) (orimasu = oru = humble form of iru = imasu, oru is used in very polite situations to refer to yourself or members of your in-group)',132,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is in the middle of going out, but...  Use gaishutsu.','田中は外出中ですが...','Tanaka wa gaishutsu chuu desu ga ... (gaishutsu suru = to go out,  chuu means during, in the middle of doing) (cf. yushutsu suru = export,  yunyuu suru = import) (cf. gaishutsugi = street dress, cf. gaijin = foreigner)',133,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is in the middle of a meeting, but ...','田中は会議中ですが...','Tanaka wa kaigi chuu desu ga ...',134,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is departing humbly, but ...  Use dekakeru to mean depart in these lessons.','田中は出かけておりますが...','Tanaka wa dekakete orimasu ga ...  (use demasu to mean leave)',135,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is absent, but ...','田中は留守ですが...','Tanaka wa rusu desu ga ...(the Russian surgeon is absent)',136,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka still doesn''t return, but ...','田中はまだ帰りませんが...','Tanaka wa mada kaerimasen ga ...',137,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is there anything message?','何かおことずけがありますか。','Nanika okotozuke ga arimasu ka.  (message re: o [honorable] koto at zoo and the key to open it)  (cf.  dengon = message)',138,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it’s fine.','いいえ、結構です。','Iie, kekkoo desu.',139,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, again, more in the future, I will do a humble phone call.  Use dewa in this section to mean well.','では、又、後ほどお電話します。','Dewa, mata nochihodo odenwa shimasu.  (denwa o itashimasu, sounds strange) (nochihodo = later, after awhile) (nochi = future,  hodo = more,  I''ll like gnocchi in the future) (naruhodo = I see)  (odenwa o shimasu, not OK)',140,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, well, please honorably report that there was a phone call from Harada.','はい、では原田から電話があったとお伝えください。','Hai, dewa, Harada kara denwa ga atta to otsutae kudasai.  (kara no denwa, also OK) (tsutaeru, to report, to tell,  the honorific or humble form is otsutaeru) (report oats-tying Ruth)  (otsutae kudasai is standard business speech – see Lesson 21)',141,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that very honorable way’s phone number?','そちら様の電話番号は。','Sochira sama no denwa bango wa?',142,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will humbly report.  Use tsutaeru.','お伝えします。','Otsutae shimasu.  (otsutaemasu, not OK)  (to form a humble verb construction, referring to actions that you perform, put o in front of the verb stem, and follow the verb stem with suru or shimasu  – see Lesson 21)',143,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I beg you well.  Use yoroshii.','では、よろしくお願いします。','Dewa, yoroshiku onegai shimasu.  (yoroshiku = well,  yoroshiku onegai shimasu = pleased to meet you)',144,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it the Tanaka honorable residence?','田中さんのお宅ですか。','Tanaka san no otaku desu ka.',145,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it''s different, but ...  Use kedo.','いいえ、違いますけど。','Iie, chigaimasu kedo.',146,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, I''m sorry for committing a discourtesy.','あ、どうも失礼しました。','A, doomo shitsurei shimashita.',147,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, meaning ‘not at all.’','いいえ。','Iie.',148,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('About when will he honorably return?','いつごろお帰りになりますか。','Itsu goro okaeri ni narimasu ka.  (to make an honorific verb, put o in front of the verb stem and follow the stem with ni naru – see Lesson 21)',149,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for by 5 o''clock, I think he will return, but is there anything message?','5時までには帰ると思いますが、何かおことづけがありますか。','Goji made ni wa kaeru to omoimasu ga, nanika okotozuke ga arimasu ka.',150,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Again about that time, I will do a humble phone call.  Use koro to mean approximate time.','又、そのころお電話します。','Mata sono koro odenwa shimasu.  (sono goro doesn’t sound right in this sentence)',151,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will commit a discourtesy.  Use dewa.','では、失礼します。','Dewa, shitsurei shimasu.',152,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please forgive, meaning good-bye.','ごめんください。','Gomen kudasai.',153,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''m overwhelmed, but I beg Hayashi.  That is, may I speak to him?','おそれいりますが、林さんをお願いします。','Osore irimasu ga, Hayashi san o onegaishimasu.  (osore = fear, awe, reverence,  osore iru = to be overwhelmed)',154,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Hayashi, right this moment he is being departed humbly, but ...  Use tadaima.','林はただいま出かけておりますが...','Hayashi wa tadaima dekakete orimasu ga ..  (tadaima means right now)',155,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, when he returns, please honorably report that there was a phone call from Webb.  Use ja to mean well.  Use the tara form of the verb to mean if or when.  Plain speech.','じゃぁ、帰ったらウエッブから電話があったとお伝えください。','Jaa, kaettara Uebbu kara denwa ga atta to otsutae kudasai.  (‘use tara’ means to add ra to the past tense of a verb to mean if or when, e.g., tabetara = if or when I eat – see Lesson 15)',156,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I beg you well. Use a polite word to mean well.','よろしくお願いします。','Yoroshiku onegai shimasu.',157,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is being departed humbly now.','田中は今出かけております。','Tanaka wa ima dekakete orimasu.',158,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please honorably report that the appointment time changed.','約束の時間が変わったとお伝えください。','Yakusoku no jikan ga kawatta to otsutae kudasai.  (from kawaru, intransitive = change, take the place of,  cf. kaeru, transitive = change, replace, exchange, transform) (an appointment for yak soaking)',159,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This elevator isn''t moving now, huh?','このエレベーターは今動いていませんね。','Kono erebeetaa wa ima ugoite imasen ne.  (from ugoku = to move) (an ugly gopher moves a lot)',160,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s climb the stairs.','階段を登りましょう。','Kaidan o noborimashoo.',161,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It still isn''t open, huh?  Plain speech.  Use desu.','まだあいていないですね。','Mada aite inai desu ne.',162,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s go to another store.','他の店に行きましょう。','Hoka no mise ni ikimashoo.',163,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s being broken, huh?  (referring to a machine) Meaning, it’s broken.  Soften this.','壊れているんですね。','Kowarete irun desu ne.  (kowareru = to break, intransitive,  cf. kowasu = to break, transitive) (in Koala reruns, things get broken) (the koala will surely break it)',164,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because it’s only one phone, it’s inconvenient, huh.  Use hitotsu.  Use dakara.','電話が一つだけだから不便ですね。','Denwa ga hitotsu dake dakara, fuben desu ne.  (cannot substitute hitotsu mo arimasen kara for hitotsu dake dakara in this sentence, as hitotsu mo arimasen = ‘not even one exists’)',165,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, it was good.  As for here, it’s open.  Plain speech.','ああ、よかった。ここはあいている。','Aa, yokatta.  Koko wa aite iru.  (ga, also OK)  (yokatta is the exclamatory tense – see Lesson 20)',166,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s do a meal here.','ここで食事をしましょう。','Koko de shokuji o shimashoo.',167,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this street, it’s bad.  Use dame.  (plain speech)','この道はだめだ。','Kono michi wa dame da.',168,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s pass by on another street.','他の道を通りましょう。','Hoka no michi o toorimashoo.  (tooru = to pass through or by, to pass an exam)  (michi de, not OK)',169,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The other person is using it.  Let''s wait a bit.  Use sukoshi.','他の人が使っています。少し待ちましょう。','Hoka no hito ga tsukatte imasu.  Sukoshi machimashoo.',170,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now a meeting is being done by someone, huh?  By someone is understood.  Meaning, a meeting is being held.  (soften this)','今、会議をしているんですね。','Ima kaigi o shite irun desu ne.',171,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Again, of later, let’s come, meaning ‘let''s come again later.’','又、後で来ましょう。','Mata ato de kimashoo.   (ato ni, not OK,  but you can say ‘ato ni shimasu’,  ato de tabemasu is OK, ato ni tabemasu is not OK,  as a rule, it’s safer to use ato de, rather than ato ni)',172,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the correct one, which is it?  Assume you only have two choices.','正しいのはどちらですか。','Tadashii no wa dochira desu ka.  (use dochira for 2 items,  use dore for 3 or more items)',173,13);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanabe, he’s late, huh?','田辺さんは遅いですね。','Tanabe san wa osoi desu ne.',1,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He might not know/understand  the place, eh?  Use basho.  Use no to soften the first verb.','場所がわからないのかもしれませんねぇ。','Basho ga wakaranai no kamoshiremasen nee.  (OK to omit no)  (tokoro is not as good as basho here)',2,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I reported precisely, he should be knowing, but ... eh?  Use a plain speech contracted form of ‘be knowing.’','ちゃんと伝えたから知ってるはずですがねぇ。','Chanto tsutaeta kara shitteru hazu desu ga nee.  [(o)tsutaeru = to tell, report, pass on, infect, report oats-tying Ruth ) (hazu desu = ‘should be’)',3,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A memo and together also a map I handed and...  Plain speech, use shi 	after the verb to mean ‘and.’','メモと一緒に地図も渡したし...','Memo to issho ni chizu mo watashita shi ...  (watasu = to hand, give, pass,  shi used after a verbal means ‘and,’ used as an explanation)',4,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey guys, excuse me.  Use iyaa.','いやぁ、すみません。','Iyaa, sumimasen.  (iyaa, similar to y’all)',5,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I became late.','遅くなりました。','Osoku narimashita.',6,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From Hibiya, I rode in a taxi, but streets awfully crowded, huh.  (plain speech, soften the first clause, use dakedo,  use hidoi)','日比谷からタクシーに乗ったんだけど道がひどく込んでねぇ。','Hibiya kara takushii ni nottan dakedo michi ga hidoku konde nee.  (hidoi = awful) (cf. taihen = terrible) (must convert the adjective hidoi = awful to the adverb hidoku because you are using it as an adverb, to modify konde)  (the hideous oily monster is awful)',7,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because today is Friday, huh?','今日は金曜日ですからねぇ。','Kyoo wa kinyoobi desu kara nee.  (dakara, also OK)',8,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, shall we start it gradually?','じゃ、そろそろ始めましょうか。','Jaa soro soro hajimemashoo ka.  (hajimarimashoo, not OK, since hajimaru is intransitive)',9,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey, as for Hanada, won’t she come?  Use no to soften this.  Plain speech.  Don’t use ka.','あれ、花田さんは来ないの。','Are, Hanada san wa konai no?  (cf. ‘ara,’ same meaning as ‘are,’ but ‘ara’ is almost exclusively used by women)',10,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She''ll become a little late, but she''s supposed to come.  Use sukoshi.  Use kedo.  Use plain speech for the 1st 2 verbs.','少し遅くなるけど来るはずです。','Sukoshi osoku naru kedo kuru hazu desu.',11,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('And then, the Public Relations Section''s Hamano also, if the work finishes early he will come he was saying.  Use a contracted form of ‘he was saying.’  Use sore kara.  Use tara.','それから広報課の浜野さんも仕事が早く終わったら来ると言ってました。','Sore kara koohooka no Hamano san mo shigoto ga hayaku owattara kuru to ittemashita.  (koohoo = official report,  ka = section,  co-workers smoking a hookah in public relations)  (sore kara = and then, next,  soshite = and then)',12,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Honorable everyone, good evening.','みなさん、こんばんは。','Minasan, konbanwa.',13,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I am called Michael Webb.  Use a polite form of ‘I am called.’','私はマイケルウエッブと申します。','Watashi wa maikeru uebbu to mooshimasu.  (to iimasu, also OK,  to yobimasu, not OK)',14,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This time, together with honorable everyone, it was  scheduled that I will labor.','今度みなさんと一緒に働くことになりました。','Kondo, minasan to issho ni hataraku koto ni narimashita.  (to show that an action is scheduled, follow the verb with koto ni naru)',15,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At one month ago, I came from New York.','一ヶ月前にニューヨークから来ました。','Ikka getsu mae ni nyuuyooku kara kimashita.',16,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in America, I was being employed in a bank, but in college I learned a little Japanese and...  (use ga, use sukoshi, use plain speech to say ‘I learned,’ use shi after the verb to mean ‘and’ or to imply an explanation)','アメリカでは銀行に勤めていましたが、大学で日本語を少しならったし...','Amerika de wa ginkoo ni tsutomete imashita ga, daigaku de, nihongo o sukoshi naratta shi.',17,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Besides, since interest to Japan exists, I entered this company.  Use plain speech to say ‘exists.’  Use node.','それに、日本に興味があるのでこの会社に入りました。','Sore ni, nihon ni kyoomi ga aru node, kono kaisha ni hairimashita.  (an interest in Kyoto meat)',18,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The hobbies are music and tennis.','趣味は音楽とテニスです。','Shumi wa ongaku to tenisu desu.',19,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If there is time, travel also I’d like to do, I''m thinking.  Use tara.  Use plain speech for the 1st clause.','時間があったら旅行もしたいと思っています。','Jikan ga attara, ryokoo mo shitai to omotte imasu.',20,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the family, with the wife it’s two people.  Use tsuma.','家族は妻と二人です。','Kazoku wa tsuma to futari desu.',21,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Go ahead, be good to me.','どうぞよろしくお願いします。','Doozo yoroshiku onegai shimasu.',22,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, let’s do welcome on Webb and drink a toast.  Use de wa.  Use te to mean and.','ではウエッブさんを歓迎して乾杯しましょう。','Dewa uebbu san o kangei shite kanpai shimashoo. (kangei = welcome) (a kangaroo and a geisha welcome us)  (cf. aisatsu suru = to greet)',23,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey!  A cherry blossom’s petal entered my cup for sure.  Use yaa.  Use a man’s word for my.','やあ！ぼくのコップに桜の花びらが入りましたよ。','Yaa!  Boku no koppu ni sakura no hanabira ga hairimashita yo.  (petals are bi-racial) (cf. iyaa = hey guys) (are and ara also = hey)',24,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Today was extremely lively, huh?  Use zuibun.','今日は随分にぎやかでしたね。','Kyoo wa zuibun nigiyaka deshita ne.',25,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Webb, with everyone, won''t you go to one more place?  Use de to mean with.  Use moo.  Use ken.','ウエッブさん、みんなでもう一軒行きませんか。','Uebbu san minna de moo ikken ikimasen ka. (ken = counter for shops, houses etc.,  Ken lives in a house)  (cannot say ikken ni or ikken e,  ni & e not needed after numbers)',26,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At this closely, a good store exists. Soften this.  Meaning, near by there’s a good bar.','この近くにいい店があるんです。','Kono chikaku ni ii mise ga arun desu.  (koko no chikaku ni, also OK)',27,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let me see.  It’s kind of you, but as for today, since I also drank beer and also drank honorable sake, the health-related feeling is a little ...  Use shi after the first verb to mean and & to imply an explanation . Use sukoshi.  Use plain speech to say ‘drank.’','そうですね。せっかくですが今日はビールも飲んだし、お酒も飲んだから、気分が少し...','Soo desu nee.  Sekkaku desu ga, kyoo wa biiru mo nonda shi, osake mo nonda kara, kibun ga sukoshi ...   (sekkaku desu ga = it''s kind of you to ask, but,  sekkaku means with much effort or devotion,  or especially) (it''s kind of you to ask me to go to seca [dry in Spanish] Kuwait) (could substitute kimochi for kibun, but kibun is more related to health and so sounds better)',28,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that so?  A little, you drank too much, huh?  Use sukoshi. Soften the 2nd sentence.','そうですか。少し飲みすぎたんですね。','Soo desu ka.  Sukoshi nomisugitan desu ne.  (the suffix -sugiru = ‘to excess’,  this is combined with a verb stem,  e.g. nomisugiru = to drink too much)',29,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you develop a hangover, it will inconvenience, huh?  Use tara.','二日酔いになったら困りますね。','Futsuka yoi ni nattara komarimasu ne.  (komaru = get perplexed, embarrassed, troubled)  (yoo = to get drunk)',30,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, as for today, I decide to return early.','ええ、今日は早く帰ることにします。','Ee, kyoo wa hayaku kaeru koto ni shimasu.  (to say ‘decide,’ follow the plain verb with koto ni suru)',31,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Again, next time, please invite me along.','又、今度さそってください。','Mata kondo sasotte kudasai.  (sasou = to invite along) (the sassy owl invited us along)',32,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That''s so huh?.  That''s too bad, but let''s do so.  (use ga)','そうですね。残念ですがそうしましょう。','Soo desu ne.  Zannen desu ga, soo shimashoo.',33,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanabe was having a map and a memo.','田辺さんは地図とメモを持っていました。','Tanabe san wa chizu to memo o motte imashita.  (ga, also OK)',34,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hanada will not come to the party.','花田さんはパーティに来ません。','Hanada san wa paatii ni kimasen.',35,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanabe, because the train gets crowded, he was delayed.  Use te or de to mean because.','田辺さんは電車が込んで遅れました。','Tanabe san wa densha ga konde okuremashita. (konde itta node, also OK) (cf. okurimashita = sent or dropped off,  I send the o [honorable] curry)',36,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Webb came from New York at 2 months ago.','ウエッブさんはニューヨークから二ヶ月前に来ました。','Uebbu san wa nyuuyooku kara nikagetsu mae ni kimashita.',37,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Webb studied Japanese at a Japanese company.','ウエッブさんは日本の会社で日本語を勉強しました。','Uebbu san wa nihon no kaisha de nihongo o benkyoo shimashita.',38,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to the Webbs, there are no children.  Use kodomo.','ウエッブさんに子供はいません。','Uebbu san ni wa kodomo wa imasen.  (kodomo ga, also OK)',39,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Webb drank beer and honorable sake.','ウエッブさんはビールとお酒を飲みました。','Uebbu san wa biiru to osake o nomimashita.',40,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Webb, now it’s good health-related feeling.','ウエッブさんは今いい気分です。','Uebbu san wa ima ii kibun desu.  (kibun ga ii, also OK) (not OK to substitute kimochi for kibun, as kibun more related to health)',41,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Webb, from now, with everyone, he will go to closely’s store, i.e., the one nearby.  Use kore to mean now.  Use ‘to’ to mean with.','ウエッブさんはこれからみんなと近くの店に行きます。','Uebbu san wa kore kara minna to chikaku no mise ni ikimasu.  (ima kara, also OK,  koko kara not OK, since koko only refers to place, not to time) (chikai mise, also OK)',42,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To show that something ought to, should or is supposed to be, after the plain form of a verb, or after an i adjective, add','はずです。','Hazu desu.',43,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('After a na adjective, to show that something ought to be, add','なはずです。','Na hazu desu.',44,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('After a noun, to show that something ought to be, add','のはずです。','No hazu desu.',45,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka was supposed to come by 6:00, but he didn''t come readily.','田中さんは６時までに来るはずでしたが、なかなか来ませんでした。','Tanaka san wa rokuji made ni kuru hazu deshita ga, nakanaka kimasen deshita.  (nakanaka = considerably, quite, not easily, not readily) (because he was nakanaka [inside the inside], he was considerably accepted by the others and he could do things readily) (Tanaka san ga, also OK)',46,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This shop''s coffee ought to be delicious.','この店のコーヒーはおいしいはずです。','Kono mise no koohii wa oishii hazu desu.  (don’t use ga here, since kono mise no already is specific)',47,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I cleaned, the rooms ought to be clean.','掃除をしたから部屋はきれいなはずです。','Sooji o shita kara heya wa kirei na hazu desu.  (heya ga, also OK, not as good) (sooji o shite kara, not OK, as this means after I cleaned)',48,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I expect this is Barbara''s handbag.','これはバーバラさんのハンドバッグのはずです。','Kore wa baabarasan no handobaggu no hazu desu.  (kore ga, also OK) (Japanese people pronounce handobaggu ‘handobakku’)',49,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To say that something is not supposed or not expected to be, follow a negative plain speech verb or adjective by','はずです。','Hazu desu.',50,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To say that something is impossible, follow hazu with (4 responses)','がない。はない。がありません。はありません。','Ga nai, wa nai, ga arimasen, wa arimasen.  (even though the opposite of desu is ja nai, cannot say hazu ja nai or hazu ja arimasen)',51,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since Tanaka is still working, I expect him not to come.','田中さんはまだ仕事をしているから、来ないはずです。','Tanaka san wa mada shigoto o shite iru kara, konai hazu desu.  (Tanaka san ga, not OK)',52,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since Tanaka is traveling now, it''s impossible that he will come.','田中さんは今旅行しているから来るはずがありません。','Tanaka san wa ima ryokoo shite iru kara, kuru hazu ga arimasen.  (Tanaka san ga, not OK)  (kuru hazu wa, also OK, not as good, since you’re talking about a specific event)',53,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since this is instant coffee, I expect it to be not very delicious.	Use dakara.','これはインスタントコーヒーだから、あまりおいしくないはずです。','Kore wa insutanto koohii dakara, amari oishikunai hazu desu.  (kore ga, not OK)',54,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It already became 12:00, right?','もう１２時になりましたね。','Moo juuniji ni narimashita ne.',55,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, that kind of expectation doesn’t exist, meaning that kind of thing is impossible.','いや、そんなはずはありません。','Iya, sonna hazu wa arimasen.  (hazu ga, also OK)',56,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To state what you intend to do, don''t use hazu.  Instead use','つもり。','Tsumori.',57,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To state what you are scheduled to do, don''t use hazu.  Instead use','ことになっている。','Koto ni natte iru.',58,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, tomorrow, I plan to attend the meeting.  Use asu.','私は明日会議に出るつもりです。','Watashi wa asu kaigi ni derutsumori desu. (asu = tomorrow, spelled with exactly the same kanji as ashita, spend ashita [tomorrow] with you)',59,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I am scheduled to attend the meeting tomorrow.  Use asu.','私は明日会議に出ることになっています。','Watashi wa asu kaigi ni deru koto ni natte imasu.',60,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is late, huh?','田中さんは遅いですねぇ。','Tanaka san wa osoi desu nee.  (Tanaka san ga, OK, if you are distinguishing him from a group.)',61,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since he was saying that he would come at 3:00, he ought to come pretty soon.  Use plain speech to say ‘he was saying.’','３時に来ると言っていたからもうすぐ来るはずです。','Sanji ni kuru to itte ita kara, moo sugu kuru hazu desu.',62,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Did Tanabe depart already?  Use dekakeru to mean depart in this lessons.','田辺さんはもう出かけましたか。','Tanabe san wa moo dekakemashita ka.  (Tanabe san ga, not OK)  (use demasu to mean leave in these lessons)',63,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since Tanabe''s car exists, he should still not be being departed.','田辺さんの車があるからまだ出かけていないはずです。','Tanabe san no kuruma ga aru kara, mada dekakete inai hazu desu.',64,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do you think that Hamano understands German?','浜野さんはドイツ語がわかると思いますか。','Hamano san wa doitsugo ga wakaru to omoimasu ka.  (to omotte imasu ka, also OK)',65,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since he graduated a German university, he ought to understand it. Use deru. Soften the 1st phrase.  Use dakara.','ドイツの大学を出たんだからわかるはずです。','Doitsu no daigaku o detan dakara, wakaru hazu desu.',66,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are these chopsticks clean?','このはしはきれいですか。','Kono hashi wa kirei desu ka.',67,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since no one still isn''t using them, they ought to be clean.','まだ誰も使っていないから、きれいなはずです。','Mada daremo tsukatte inai kara, kirei na hazu desu.',68,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What nationality is Chin, probably?','チンさんは何人でしょうか。','Chin san wa nani jin deshoo ka.  (nan jin, not OK)',69,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since he was saying that he came from Peking, he ought to be a Chinese person.  Use plain speech to say ‘he was saying.’','ペキンから来たと言っていたから、中国人のはずです。','Pekin kara kita to itte ita kara, chugokujin no hazu desu.',70,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will Tanabe come by bus?','田辺さんはバスで来ますか。','Tanabe san wa basu de kimasu ka.  (Tanabe san ga, not OK)',71,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, from the subway station, he ought to come by taxi, for sure.','いや、地下鉄の駅からタクシーで来るはずですよ。','Iya, chikatetsu no eki kara, takushii de kuru hazu desu yo.',72,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since she is having a map, she ought to be knowing the place.  Use basho.','地図を持っているから場所を知っているはずです。','Chizu o motte iru kara, basho o shitte iru hazu desu.',73,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since she''s sick, as for today, I expect her not to come.  Use dakara.','病気だから今日は来ないはずです。','Byooki dakara, kyoo wa konai hazu desu.',74,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it''s Saturday, the banks ought to be holiday, meaning closed.  Use dakara.','土曜日だから銀行は休みのはずです。','Doyoobi dakara, ginkoo wa yasumi no hazu desu.',75,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since the lamp is on, someone ought to exist. Use akari.','明かりがついているから、誰かいるはずです。','Akari ga tsuite iru kara, dareka iru hazu desu. (akari = light) (from akarui = bright) (dareka ga, also OK)',76,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since she was born in 1940, she should be 50 years old.','1940年に生まれたから、50歳のはずです。','Sen kyuuhyaku yonjuu nen ni umareta kara, gojussai no hazu desu.  (umareru = to be born)',77,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since there''s a bargain sale, it should be cheap.  Use dakara.','バーゲンセールだから安いはずです。','Baagenseeru dakara, yasui hazu desu.',78,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will Tanaka probably come to the party?','田中さんはパーティーに来るでしょうか。','Tanaka san wa paatii ni kuru deshoo ka.',79,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He ought to come.','来るはずです。','Kuru hazu desu.',80,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Probably he will probably come, I think.  Use plain speech for the 2nd ‘probably.’','多分来るだろうと思います。','Tabun kuru daroo to omoimasu.',81,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He might come.','来るかもしれません。','Kuru kamoshiremasen.',82,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Does your mother have an interest in Japan?','あなたのお母さんは日本に興味がありますか。','Anata no okaasan wa nihon ni kyoomi ga arimasu ka.',83,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will your mother travel to a foreign country next year’s summer?','あなたのお母さんは来年の夏、外国に旅行しますか。','Anata no okaasan wa rainen no natsu gaikoku ni ryokoo shimasu ka.',84,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('By next year’s January, will the study of this textbook completely finish?  Use zenbu.','来年の一月までにこの教科書の勉強が全部終わりますか。','Rainen no ichigatsu made ni, kono kyookasho no benkyoo ga zenbu owarimasu ka.  (owatte shimaimasu ka, also OK)  (kyookai [churches], are shown in art textbooks)  (benkyoo wa, also OK,  benkyoo wo, not OK, since owaru is an intransitive verb)',85,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the strong yen, will it probably continue?','円高は続くでしょうか。','Endaka wa tsuzuku deshoo ka.  (endaka = rise in value of the yen,  cf. enyasu = a yen decline)  (when the tree gets higher, it gets darker) (tsuzuku, intransitive,  tsuzukeru, transitive)',86,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for your friend, will she probably give a birthday present to you?  Speaking to someone in your in-group.','あなたの友達はあなたに誕生日のプレゼントをくれるでしょうか。','Anata no tomodachi wa anata ni tanjoobi no purezento o kureru deshoo ka.  (ageru also OK, if the person being addressed is not in your in-group)',87,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To show that a person himself decides upon or chooses an action, follow the verb with','ことにする。','Koto ni suru',88,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To show that a person himself chooses a noun, follow the noun with','にする。','Ni suru.',89,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for drinks, what will you choose?','飲み物は何にしますか。','Nomimono wa nan ni shimasu ka. (nani, also OK)',90,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it''s hot, shall I choose beer?','暑いからビールにしましょうか。','Atsui kara, biiru ni shimashoo ka.',91,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I decided to study French starting next year.','来年からフランス語を勉強することにしました。','Rainen kara furansugo o benkyoo suru koto ni shimashita.',92,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the meeting, I decide to start it at 10:00.','会議は１０時に始めることにします。','Kaigi wa juuji ni hajimeru koto ni shimasu.',93,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To indicate that you have decided upon or set a course of action for yourself (in the past), or that you do some action routinely, instead of koto ni suru, say','ことにしている。','Koto ni shite iru',94,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My routine is to call on the phone to the New York parents every month.','ニューヨークの両親に毎月電話を掛けることにしています。','Nyuuyooku no ryoshin ni maitsuki denwa o kakeru koto ni shite imasu.  (maigetsu & maigatsu, not OK)',95,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to ride the train time, while I look at advertisements, my routine is to do kanji’s study.','電車に乗るときは広告を見ながら漢字の勉強をすることにしています。','Densha ni noru toki wa, kookoku o mi nagara, kanji no benkyoo o suru koto ni shite imasu.  (benkyoo o shite iru koto ni, not OK)',96,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To show that an action is to be decided on or scheduled (an impersonal decision), follow the verb with','ことになる。','Koto ni naru',97,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It was scheduled that I''ll study French starting next year.','来年からフランス語を勉強することになりました。','Rainen kara furansugo o benkyoo suru koto ni narimashita.',98,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the meeting, it was scheduled that someone will start it from 10:00.  Someone is understood.','会議は１０時から始めることになりました。','Kaigi wa juuji kara hajimeru koto ni narimashita.',99,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When stating the ways things are, such as schedules, rules, customs etc., say','ことになっている。','Koto ni natte iru',100,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Next week, I''m scheduled to leave for Europe.  (use shuppatsu)','来週ヨーロッパに出発することになっています。','Raishuu yooroppa ni shuppatsu suru koto ni natte imasu.',101,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in Japan, as for inside houses, it''s the custom not to wear shoes.','日本では家の中では靴をはかないことになっています。','Nihon de wa ie no naka de wa kutsu o hakanai koto ni natte imasu.  (from haku, to put on or wear) (hackers wear thongs)',102,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the department stores, today is a holiday for sure.','デパートは今日は休みですよ。','Depaato wa kyoo wa yasumi desu yo.',103,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will decide to go tomorrow.','じゃ、明日行くことにします。','Ja, ashita iku koto ni shimasu.',104,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for lunch, I am always deciding to eat a sandwich.','昼ごはんはいつもサンドイッチを食べることにしています。','Hirugohan wa itsumo sandoicchi o taberu koto ni shite imasu.  (natte imasu, not OK here)',105,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I got tired, as for today, already, I will decide to return to home.  Use node.  Plain speech.','疲れたので今日はもう家に帰ることにする。','Tsukareta node, kyoo wa, moo, uchi ni kaeru koto ni suru.',106,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, as for the continuation of the conversation, let''s do it tomorrow.  Use dewa.  Use hanashi.','では、話の続きは明日にしましょう。','Dewa hanashi no tsuzuki wa, ashita ni shimashoo.  [tsuzuku = to continue (intransitive), tsuzukeru = to continue (transitive)] (tsuzuki is a noun derived from the stem form of tsuzukimasu)',107,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Sunday, since I''m scheduled to go to a department store for shopping with my wife, the circumstances are bad, but ... (use node)  Soften the last clause.','日曜日は家内とデパートに買い物に行くことになっているので都合が悪いんですが...','Nichiyoobi wa kanai to depaato ni kaimono ni iku koto ni natte iru node tsugoo ga waruin desu ga ...',108,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won''t you drink beer for a short time and return?','ちょっとビールを飲んで帰りませんか。','Chotto biiru o nonde kaerimasen ka.',109,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for today, I am decided to return home early.','今日は家に早く帰ることにしています。','Kyoo wa uchi ni hayaku kaeru koto ni shite imasu.',110,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that so?  To you, by all means, wanting to talk in advance thing existed, but ... Use koto to mean thing, soften this, use kedo.','そうですか。あなたに是非、話しておきたいことがあったんですけど。','Soo desu ka.  Anata ni zehi hanashite okitai koto ga attan desu kedo ...',111,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I''ll decide to go together.','じゃ、一緒に行くことにします。','Jaa, issho ni iku koto ni shimasu.',112,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I will do a phone call to home for a short time and come.  (use kara)','ちょっと家に電話をして来ますから...','Chotto uchi ni denwa o shite kimasu kara ...',113,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is scheduled to attend the meeting at 9 in the morning.','田中さんは朝９時に会議に出ることになっています。','Tanaka san wa asa kuji ni kaigi ni deru koto ni natte imasu.',114,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanaka is scheduled to meet a person from the Yomiuri newspaper company at half past 10.','田中さんは１０時半に読売新聞社の人に会うことになっています。','Tanaka san wa juuji han ni Yomiuri shinbunsha no hito ni au koto ni natte imasu.',115,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At 1:00, he will go to the Bunpo company and, at half past 4, he’s scheduled to return to the company.  Use modoru.','１時にぶんぽ社に行って４時半に会社にもどることになっています。','Ichiji ni bunpo sha ni itte yoji han ni kaisha ni modoru koto ni natte imasu.',116,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From 3:00 there''s a meeting, and at 7:00 he is scheduled to meet with Koch from Asahi company.','３時から会議があって、７時に朝日社のカークさんと会うことになっています。','Sanji kara kaigi ga atte, shichiji ni Asahi sha no kaaku san to au koto ni natte imasu.',117,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for drinks, what is good?  Is coffee good, is black tea good?','飲み物は何がいいですか。コーヒーがいいですか、紅茶がいいですか。','Nomimono wa nani ga ii desu ka.  Koohii ga ii desu ka, kocha ga ii desu ka.',118,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since today hot, iced coffee is good, right?','今日は暑いからアイスコーヒーがいいですね。','Kyoo wa atsui kara, aisu koohii ga ii desu ne.',119,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, it’s good, right?  I also shall choose that.','ああ、いいですね。私もそれにしましょう。','Aa, ii desu ne.  Watashi mo sore ni shimashoo.',120,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for a meal, what is good?','食事は何がいいですか。','Shokuji wa, nani ga ii desu ka.',121,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so huh, meaning, let’s see.  As for me, I will choose the A lunch.','そうですね。私はAランチにします。','Soo desu ne.  Watashi wa A ranchi ni shimasu.',122,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, as for me, I shall choose spaghetti.','じゃ、私はスパゲッティーにしましょう。','Ja, watashi wa supagetti ni shimashoo.',123,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me, 2 iced coffees, one each of the A lunch and the spaghetti I beg.','すみません。アイスコーヒー二つ、Aランチとスパゲッティーを一つずつお願いします。','Sumimasen, aisukoohii futatsu, A ranchi to supagetti o hitotsu zutsu onegaishimasu.',124,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will humbly receive 2300 yen.','では、2千300円いただきます。','Dewa, nisen sambyaku en itadakimasu.  (you don’t need to use wo after numbers)',125,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I drank too much beer, a little, the health-related feeling is bad.  Use te to mean since.  Use sukoshi.  Plain speech.','ビールを飲みすぎて少し気分が悪い。','Biiru o nomi sugi te, sukoshi kibun ga warui.',126,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this sweater, as for to me, it’s too big.','このセーターは私には大きすぎます。','Kono seetaa wa watashi ni wa, ookii sugimasu.',127,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This shop is too lively, huh?','この店はにぎやかすぎますね。','Kono mise wa nigiyaka sugimasu ne.',128,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s go to a quieter place.','もっと静かなところへ行きましょう。','Motto shizuka na tokoro e ikimashoo.',129,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This bath is too hot.','この風呂は熱すぎます。','Kono furo wa atsusugimasu.',130,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The diamond necklace, as for to  me, is too expensive.','ダイヤのネックレスは私には高すぎます。','Daiya no nekkurasu wa, watashi ni wa, takasugimasu.',131,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This cake, as for to me, is too sweet.','このケーキは私には甘すぎます。','Kono keeki wa watashi ni wa, amasugimasu.',132,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This shirt is too big.','このシャツは大きすぎます。','Kono shatsu wa ookisugimasu.',133,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yamada ate too much.','山田さんは食べすぎました。','Yamada san wa tabesugimashita.',134,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for last night, I drank too much honorable sake.','夕べはお酒を飲みすぎました。','Yuube wa osake o nomisugimashita.',135,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Saturday, shall we depart at 6 in the morning?','土曜日は朝6時に出かけましょうか。','Doyoobi wa asa rokuji ni dekakemashoo ka.',136,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In that case, it''s too early for sure. Won’t you choose 7:00?	Use sore jaa.','それじゃ、早すぎますよ。7時にしませんか。','Sore jaa, hayasugimasu yo.  Shichiji ni shimasen ka.',137,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From 8 in the morning until 10 at night, I am working.','朝8時から夜10時まで仕事をしています。','Asa hachiji kara yoru juuji made shigoto o shite imasu.',138,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because you labor too much, don''t you tire?','働きすぎて疲れませんか。','Hataraki sugite, tsukaremasen ka.  (tsukareru = to tire,  I got tired carrying the tsuki [moon] on the carrera [road in Spanish])',139,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this test''s problems, I soon understood all of them, for sure.  Use tesuto.  Use zenbu.','このテストの問題は全部わかりましたよ。','Kono tesuto no mondai wa zenbu sugu wakarimashita yo.  (either minna or subete could replace zenbu)',140,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that so?  As for for you, it was too easy, right?','そうですか。あなたにはやさしすぎたね。','Soo desu ka.  Anata ni wa yasashisugimashita ne.',141,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the family, it''s two people, but there are 9 rooms.','家族は二人ですが、部屋が九つあります。','Kazoku wa futari desu ga, heya ga kokonotsu arimasu.',142,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In that case, it''s probably too spacious.  Use sore jaa.','それじゃ、広すぎるでしょう。','Sore jaa, hirosugiru deshoo.',143,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please taste for a second.','ちょっと、味をみてください。','Chotto aji o mite kudasai.',144,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Salty!  Didn''t you put in too much salt?  Soften the verb ‘put in too much.’','しょっぱい！塩を入れすぎたんじゃありませんか。','Shoppai!  Shio o iresugitan ja arimasen ka.  (OK to omit the ‘n’) (iresugimasen deshita ka, or iresugitan ja nai desu ka, also OK) (cannot say iresuginai deshita ka or iresuginakattan desu ka)  (a shio [salt] pie is a salty pie)',145,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for tomorrow, shall we go at 10:00?','明日は10時に行きましょうか。','Ashita wa juuji ni ikimashoo ka.',146,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, in that case, it''s too late.  Please come by 9:00.  Use de wa.','いいえ、それでは遅すぎます。9時までに来てください。','Iie, sore de wa ososugimasu.  Kuji made ni kite kudasai.',147,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Today is hot, huh?','今日は暑いですね。','Kyoo wa atsui desu ne.',148,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, since I came from Hokkaido, Tokyo summers are too hot.  Use node.  Plain speech.','私は北海道から来たので東京の夏は暑すぎる。','Watashi wa hokkaidoo kara kita node tookyoo no natsu wa atsusugiru .',149,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are you reading a Japan’s newspaper? Soften this.','日本の新聞を読んでいるんですか。','Nihon no shinbun o yonde irun desu ka.',150,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, but because the kanji are too difficult, I don''t understand it for sure.  Use te to mean since. Soften this.','ええ、でも漢字が難しすぎてわからないんですよ。','Ee, demo kanji ga muzukashisugi te wakaranain desu yo.',151,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When or if there is time.  Plain speech.  Use tara to mean if or when, unless otherwise specified.','時間があったら。','Jikan ga attara.',152,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Supposing.  In the event that.','もし。もしも。','2 responsesMoshi.  Moshimo.',153,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When Tanaka comes, let''s start the meeting.','田中さんが来たら会議を始めましょう。','Tanaka san ga kitara kaigi o hajimemashoo.',154,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If he doesn''t come...','来なかったら。','Konakattara.',155,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it''s hot, please open the window.','暑かったら窓を開けてください。','Atsukattara, mado o akete kudasai.  (atsui deshitara, not OK)',156,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it isn''t hot.','暑くなかったら。','Atsukunakattara.',157,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If that room is clean, I''ll rent it.  Use ga to be very specific.','その部屋がきれいだったら借ります。','Sono heya ga kirei dattara, karimasu.  (heya wa, also OK)',158,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it isn''t clean.  3 responses.','きれいじゃなかったら。きれいでなかったら。きれいではなかったら。','Kirei ja nakattara.  Kirei de nakattara.  Kirei de wa nakattara.',159,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it''s delicious sushi, I''ll eat it.','おいしい寿司だったら食べます。','Oishii sushi dattara tabemasu.  (sushi ga oishii dattara, not OK,  sushi ga oiishikattara, also OK, but changes meaning a little)',160,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it isn''t sushi.  3 responses.','寿司じゃなかったら。寿司でなかったら。寿司ではなかったら。','Sushi ja nakattara.  Sushi de nakattara.  Sushi de wa nakattara.',161,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Supposing the bath is too hot, please add water.  Use moshi.','もし風呂が熱すぎたら水を入れてください。','Moshi furo ga atsusugitara mizu o irete kudasai.',162,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it rains, as for the honorable flower viewing, it will be stopped by someone.  Use moshimo.  Use yameru.','もしも雨が降ったらお花見はやめます。','Moshimo ame ga futtara ohanami wa yamemasu.  (ohanami o yamemasu, also OK) (yamu, intransitive, yameru, transitive,  both = to stop) (sumasu = sumaseru = to finish, both transitive,  sumu = to finish, intransitive,  not OK to substitute sumasu or sumaseru for yamemasu in this sentence)',163,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It would be nice if.','たらいい。','Tara ii.  (たらtara, when combined with verbs, = if or when,  e.g., dattara = if it is, or when it is)',164,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow, it would be nice if it got sunny, huh?','明日晴れたらいいですね。','Ashita haretara ii desu ne.',164,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If Tanaka also together had come, it was good, huh?','田中さんも一緒に来たらよかったですね。','Tanaka san mo issho ni kitara yokatta desu ne.',165,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If I catch a cold, I will take medicine.  Use moshi.','もし風邪を引いたら薬を飲みます。','Moshi kaze o hiitara kusuri o nomimasu.',166,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If the street is being crowded, I''ll go by train.  Use moshi.','もし道が込んでいたら、電車で行きます。','Moshi michi ga konde itara, densha de ikimasu.',167,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If 1 million yen exist, I''ll buy a diamond.  Use moshimo.  Don’t use wa or ga.','もしも百万円あったらダイヤを買います。','Moshimo hyaku man en attara, daiya o kaimasu.   (hyaku man en ga attara, also OK, but you don’t need to use ga or wa after numbers)',168,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it rains tomorrow, I''ll read a book at home.  Use moshi.','もし明日雨が降ったらうちで本を読みます。','Moshi ashita ame ga futtara, uchi de hon o yomimasu.',169,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If the room is cold, please turn on the space heater.  Use moshi.','もし部屋が寒かったらストーブを点けてください。','Moshi heya ga samukattara, sutoobu o tsukete kudasai.',170,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When it becomes 12:00, I will eat a sandwich.','12時になったらサンドイッチを食べます。','Juuniji ni nattara, sandoichi o tabemasu.',171,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When the Japanaese becomes skillful, I would like to become a guide.','日本語が上手になったらガイドになりたいです。','Nihongo ga joozu ni nattara, gaido ni naritai desu.',172,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it’s a cheap and good apartment, I''ll make a contract.  Use moshi.','もし安くていいアパートだったら契約します。','Moshi yasukute ii apaato dattara, keiyaku shimasu.  (keiyakusho is the contract document) (a contract to buy Kay''s yak) (keiyaku o shimasu, also OK)',173,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A word to use after plain verbals to mean and or therefore.  If used after enumerating facts, it suggests that such facts justify one''s actions, feelings or opinions.','し。','Shi.',174,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for inside the train, people who are reading newspapers also exist and, people who are sleeping also exist.  Use plain speech the 1st time you say ‘exist’ and use polite speech the 2nd time.','電車の中には新聞を読んでいる人もいるし、寝ている人もいます。','Densha no naka ni wa, shinbun o yonde iru hito mo iru shi, nete iru hito mo imasu.',175,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow won''t you play tennis?','明日テニスをしませんか。','Ashita tenisu o shimasen ka.',176,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let me see.  But, as for tomorrow, I also want to see a movie, and so therefore ...','そうですね。でも明日は映画も見たいし...','Soo desu nee.  Demo, ashita wa, eiga mo mitai shi ...',177,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This knife is light and, which is why I like it, cuts well.','このナイフは軽いしよく切れます。','Kono naifu wa karui shi, yoku kiremasu.  (kireru = to cut, intransitive,  kiru = to cut, transitive,  these verbs are exceptions to the ‘e rule’) (an example of the use of kiru is ‘kono naifu de niku o yoku kirimasu’ = I cut meat well with this knife)',178,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tanaka, he is handsome, and his head is good (meaning, intelligent) and a lot of popularity exists.  Use shi twice.  Use totemo to mean ‘a lot.’','田中さんはハンサムだし頭がいいしとても人気があります。','Tanaka san wa hansamu da shi, atama ga ii shi, totemo ninki ga arimasu.  (ninki = popularity) (the nincompoop has key to the liquor, is popular)  (takusan no ninki, not OK)',179,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That apartment is far from the station and, because it''s small it isn''t very good.  Use shi.','そのアパートは駅から遠いし狭いからあまりよくありません。','Sono apaato wa eki kara tooi shi, semai kara, amari yoku arimasen.  (OK to use semakute, instead of semai kara, in this sentence)',180,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This ice cream is delicious but expensive.  Plain speech.','このアイスクリームはおいしいですが高い。','Kono aisu kuriimu wa oishii ga, takai.',181,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this knife, it’s light and it cuts well and, moreover, it''s cheap.   Use shi twice. Soften the word cheap.  Use desu only once, at the end.','このナイフは軽いしよく切れるしそれに安いんです。','Kono naifu wa karui shi, yoku kireru shi, sore ni yasuin desu.  (kiru, transitive,  kireru, intransitive,  these are exceptions to the e rule)',182,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The subways are fast and, because they’re convenient, I ride them often.  Use shi in the next 5 responses.  Use dakara.','地下鉄は速いし便利だからよく乗ります。','Chikatetsu wa hayai shi, benri dakara, yoku norimasu.',183,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That restaurant over there is delicious and, because it''s cheap, it''s always crowded.  Use kara.','あのレストランはおいしいし安いからいつも込んでいます。','Ano resutoran wa oishii shi, yasui kara itsumo konde imasu.',184,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tokyo apartments are small and, since they''re expensive, one gets disgusted.','東京のアパートはせまいし高いからいやになります。','Tookyoo no apaato wa semai shi, takai kara iya ni narimasu.',185,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nikko is close and, since it''s famous, if you go once, how would it be?  Use dakara.','日光は近いし有名だから一度行ったらどうですか。','Nikkoo wa chikai shi, yuumei dakara, ichido ittara doo desu ka.',186,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This bag is light and, since it''s big, it''s convenient.','このバッグは軽いし大きいから便利です。','Kono baggu wa karui shi, ookii kara, benri desu.  (not OK to substitute hiroi for ookii here, since hiroi is generally used for large physical spaces, but OK to say ‘baggu no naka wa hiroi desu’) (Japanese people pronounce baggu ‘bakku’)',187,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for tomorrow, I will get up early.','明日は早く起きます。','Ashita wa hayaku okimasu.  (to convert an i adjective to an adverb, remove the final i and add ku)',188,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please cut the sweets small (i.e. into small pieces).','お菓子を小さく切ってください。','Okashi o chiisaku kitte kudasai.',189,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('We greet energetically.','元気にあいさつをします。','Genki ni aisatsu o shimasu.  (to convert a na adjective to an adverb, follow it with ni)  (aisatsu = greetings) (ai = love,  satsu =  folding money, counter for books etc.)  (ai shite iru [I love you] and with satsu, [paper currency] I greet you)  (cf. kangei suru = welcome)',190,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s clean the room cleanly.','部屋をきれいに掃除しましょう。','Heya o kirei ni sooji shimashoo.',191,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s go hurriedly.','急いで行きましょう。','Isoide ikimashoo.  (from isogu = to hurry)',192,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I wash the honorable dishes carefully.','お皿をていねいに洗います。','Osara o teinei ni araimasu. (teinei na = polite, careful) (careful not to catch your tail on a nail)',193,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the characters, please write them large.','字は大きく書いてください。','Ji wa ookiku kaite kudasai.',194,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for in the hospital, let''s walk quietly.','病院では静かに歩きましょう。','Byooin de wa shizuka ni arukimashoo.',195,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please cut the hair short.','髪を短く切ってください。','Kami o mijikaku kitte kudasai.',196,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for last night, you returned late.','夕べは遅く帰りました。','Yuube wa osoku kaerimashita.',197,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for dinner, we eat with everyone in a lively way.   Use minna de.  Use yuushoku.','夕食はみんなでにぎやかに食べます。','Yuushoku wa minna de nigiyaka ni tabemasu.',198,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I am called Michael Webb.  (less polite)','私はマイケルウエッブといいます。','Watashi wa maikeru uebbu to iimasu.  (yobimasu, not OK,  yobu = to call out to or send for someone)',199,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I am called Michael Webb.  (more polite)','私はマイケルウエッブと申します。','Watashi wa maikeru uebbu to mooshimasu.',200,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I came from California.','カルフォルニアから来ました。','Karuforunia kara kimashita.',201,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The country is America, meaning my country.','国はアメリカです。','Kuni wa amerika desu.',202,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My alma mater is Colorado College.','私の出身はコロラドカレッジです。','Watashi no shusshin wa, Colorado College desu.  (I majored in shoe shining at my alma mater)  (cf. shooshin = promotion,  if you get promoted, you can show shiny coins)',203,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I graduated the University of California.  Use sotsugyoo.','カルフォルニア大学を卒業しました。','Karuforunia daigaku o sotsugyoo shimashita.  (sotted Sue majored in geography and managed to graduate)',204,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now I''m 60 years old.','今60歳です。','Ima rokujissai desu.  (rokujussai, also OK)',205,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now I’m 62 years old.','今62歳です。','Ima rokujuu nisai desu.',206,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''m single.','独身です。','Dokushin desu.  (doku means poison,  also means to get out of the way,  a doc with a shiny face tends to be single,  cf. dokusho = reading)',207,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I have a wife and two children.  Use tsuma.','妻と子供が二人います。','Tsuma to kodomo ga futari imasu.',208,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Employee of a trading company','商社員。','Shooshain.  (a trading company shows shawls)',209,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('A  public employee','公務員。','Koomuin.  (to be a government employee you have to comb your hair before going in)',210,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Lawyer','弁護士。','Bengoshi.',211,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Photographer','写真家。','Shashinka.',212,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('University professor','大学教授。','Daigaku kyooju.  (professor is a Kyoto Jew)',213,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sales clerk','店員。','Tenin.',214,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Housewife','主婦。','Shufu. (housewife is a shuffling fool)',215,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the hobby, it’s music.','趣味は音楽です。','Shumi wa ongaku desu.',216,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In order to study Japanese culture, I came to Japan.  Use tame ni.','日本の文化を勉強するために日本に来ました。','Nihon no bunka o benkyoo suru tame ni nihon ni kimashita.  (bunka = culture, bungaku = literature, bunpoo = grammar)  (so-called culture is a lot of bunka)  (tame ni = ‘in order to’)',217,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I came to Japan for work.','仕事で日本に来ました。','Shigoto de nihon ni kimashita.  (shigoto ni, not OK, but OK to say shigoto ni ikimasu = I go for the purpose of work)',218,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I came to Japan for sightseeing.  Use kankoo.','観光で日本に来ました。','Kankoo de nihon ni kimashita.  (sightseers will see canned corn)  (cf. kankoku means S. Korea,  S. Korea can cook) (cf. kenbutsu = sightseeing, but not OK to substitute kenbutsu for kankoo in this sentence, because you can''t use kenbutsu as a stand-alone noun in that way,  however, you could say kenbutsu suru tame ni nihon ni kimashita) (kankoo ni, not OK, but OK to say kankoo ni ikimasu = I go for the purpose of sightseeing)',219,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael inside the return’s train thought/felt thing.  Michael is the subject.  Don’t use san.','マイケルが帰りの電車の中で思ったこと。','Maikeru ga kaeri no densha no naka de omotta koto.  (cannot say kaeru densha because the verb ''to return'' refers to a person, not to the train,   OK to substitute kangaetta koto for omotta koto in this sentence)',220,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this evening, it was my welcome party.  Use konya.  Plain speech.','今夜は私の歓迎会だった。','Konya wa watashi no kangeikai datta.  (kangeikai = welcome party,  konya = this evening) 	(a kangaroo and a geisha welcomed us) (cf. enkai = banquet)',221,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In a park, while looking at cherry blossoms, as for to drink honorable sake, it was the first time.  Use no to make a noun phrase.  Plain speech.','公園で桜の花を見ながらお酒を飲むのは初めてだった。','Kooen de sakura no hana o mi nagara, osake o nomu no wa hajimete datta.',222,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('''It''s called ‘night cherry sightseeing’ for sure,'' Yamashita was saying.  Use yo to mean night.  Use kenbutsu.  Soften the first clause.  Use plain speech to say ‘was saying.’','夜桜見物と言うんですよと山下さんが言っていた。','Yozakura kenbutsu to iun desu yo to Yamashita san ga itte ita.  (yo from yoru,  zakura from sakura,  kenbutsu = sightseeing,  Ken butts you when you go sightseeing)',223,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it is night, I was thinking it will probably be cold, but it wasn''t so at all.  Use yoru.  Use demo rather than ja.  Plain speech.','夜だから寒いだろうと思っていたがそうでもなかった。','Yoru dakara, samui daroo to omotte ita ga, soo demo nakatta.  (soo demo nakatta = it wasn''t so at all) (soo demo nakatta, literally = in that way, but it didn’t exist)  (soo ja nakatta is also OK in this sentence)',224,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Other people also, a lot were come and, since we also drank to a great degree, it was very lively. Use takusan.  Use shi.  Plain speech.','他の人たちもたくさん来ていたし、私たちも随分飲んだからとてもにぎやかだった。','Hoka no hitotachi mo, takusan kite ita shi, watashitachi mo zuibun nonda kara, totemo nigiyaka datta.',225,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I drank a little too much.  Use chotto.  Plain speech.','私はちょっと飲みすぎた。','Watashi wa chotto nomi sugita.',226,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('With a blue face, if I return home, as for Barbara, she  will probably do worry.  Plain speech.','青い顔で家に帰ったらバーバラは心配するだろう。','Aoi kao de, uchi ni kaettara, baabaara wa shinpai suru daroo.  (aoi kao = looks sick)',227,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('But if I return with a red face, she might get angry and ...  Plain speech.  Use shi.','でも赤い顔で帰ったらおこるかもしれないし...','Demo akai kao de kaettara, okoru kamoshirenai shi ..(okoru = get angry, also = happens, occurs,  Oklahoma Ruth gets mad) (akai kao = red face = looks drunk)',228,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('That’s so, meaning ‘I got an idea.’  On the way, I shall decide to drink coffee and return.  Plain speech.','そうだ、途中でコーヒーを飲んで帰ることにしよう。','Soo da tochuu de koohii o nonde kaeru koto ni shiyoo.  (tochuu de = on the way,  shiyoo = shimashoo) (in the train, the men touch you on the way)  (soo da implies a new idea has occurred)  (OK to substitute kaeri ni for tochuu de in this sentence, but the meaning changes)  (to say I shall, for an ru verb, add yoo to the root, i.e. to the pre-ru form)',229,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael''s welcome party was done by someone at a restaurant.  Someone is understood.  Plain speech.','レストランでマイケルさんの歓迎会をした。','Resutoran de maikeru san no kangeikai o shita.',230,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the park, it was very cold.  Plain speech.','公園はとても寒かった。','Kooen wa totemo samukatta.',231,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, now, very much, the health-related feeling is good.  Plain speech.','マイケルさんは今とても気分がいい。','Maikeru san wa ima totemo kibun ga ii. (ii kibun da, also OK)',232,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael decided to drink coffee and return.  Plain speech.','マイケルさんはコーヒーを飲んで帰ることにした。','Maikeru san wa koohii o nonde kaeru koto ni shita.',233,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I’m an alumnus of West Texas University.','私はウエストテキサス大学の出身です。','Watashi wa uesuto tekisasu daigaku no shusshin desu.',234,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now I am employed at a newspaper company.','今、新聞社に勤めています。','Ima shinbunsha ni tsutomete imasu.',235,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to come to Japan, it’s the first time.  Use no to make a noun phrase.','日本に来るのは初めてです。','Nihon ni kuru no wa hajimete desu.',236,14);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tanabe, as for the post office, lunch break also, is it open?','田辺さん、郵便局は昼休みもあいていますか。','Tanabe san, yuubinkyoku wa hiruyasumi mo aite imasu ka.',1,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, of course, it''s open for sure.','ああ、もちろんあいていますよ。','Aa, mochiron, aite imasu yo.',2,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, for a moment, I will go for the purpose of sending a package and return.  Use dasu.','じゃ、ちょっと小包を出しに行ってきます。','Jaa, chotto kozutsumi o dashi ni itte kimasu.  (use the stem of the verb followed by ni to express the idea of doing something for a purpose)',3,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Excuse me, but while you''re at it, won''t you buy some stamps & postcards & come and give?  Use kedo.','すみませんけど、ついでに切手とはがきを買ってきてくれませんか。','Sumimasen kedo, tsuide ni, kitte to hagaki o katte kite kuremasen ka.  (tsuide ni = while you''re at it, at the same time,  on the tsuitachi [first of the month] day, I’ll go while I’m at it,  distinguish from tsuite = concerning)  (cf. tochu de = on the way) (ha = tooth, leaf,  writing on a hagaki [postcard] is like writing on a ha [leaf])',4,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s good, for sure.  About how many shall I buy and come?','はい、いいですよ。どのくらい買ってきましょうか。','Hai, ii desu yo.  Dono kurai katte kimashoo ka.',5,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('62 yen stamps 300 and postcards 100 if you please.','62円の切手を300枚とはがきを100枚お願いします。','Roku juu ni en no kitte o sambyakumai to hagaki o hyakumai, onegai shimasu.',6,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will return and come as quickly as possible.','なるべく早く帰ってきます。','Narubeku hayaku kaette kimasu.  (narubeku = as ... as possible,  distinguish from naruhodo = I see,  Naru beckoned me to come as soon as possible)',7,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please don''t forget the receipt.','領収書を忘れないでください。','Ryooshuusho o wasurenai de kudasai.  (a receipt from Leo''s shoe show)  (use nai de kudasai after a verb verb stem to make a negative request)',8,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This package if you please.','この小包お願いします。','Kono kozutsumi onegai shimasu.',9,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is it air mail?  Is it ship mail?','航空便ですか、船便ですか。','Kookuubin desu ka, funabin desu ka.  (kookuu = aviation,  bin = mail,  fune = ship,  kuukoo = airport,  cf.  express train = kyuukoo,  I wear my cute coat in the express train) (we ship Coke in a bin by airmail)',10,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for by ship mail, until America, about how long will it take?','船便ではアメリカまでどのくらいかかりますか。','Funabin de wa amerika made dono kurai kakarimasu ka.  (itsu goro, not OK,  dono goro, not OK)',11,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will it arrive in about 3 weeks?  Use todoku.','3週間ぐらいで届きますか。','San shuukan gurai de todokimasu ka.  (sanshuukan goro, not OK)  (from todoku = to be received,  Tom’s dog from KU, i.e., Kansas U., was received,  cf. todokeru = to deliver or notify)',12,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for ordinarily it will arive, but since it''s crowded now, I don''t know/understand whether it will arrive in 3 weeks, for sure.','普通は届きますが、今込んでいるから3週間で届くかどうかわかりませんよ。','Futsuu wa todokimasu ga, ima konde iru kara, sanshuukan de todoku ka dooka wakarimasen yo.  (futsuu = ordinarily,  ordinary people ordinarily have sore futsus [feet]) (ka dooka = whether or not)',13,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I choose air mail.','じゃ、航空便にします。','Jaa, kookuubin ni shimasu.',14,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It will arrive by the 10th, right?','十日までに届きますね。','Tooka made ni todokimasu ne.',15,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s OK, for sure.','大丈夫ですよ。','Daijoobu desu yo.',16,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To this form please write your name and your address, the receiver''s information and the contents, etc.  Use nado.','この用紙にお名前とご住所、あて先と中身などを書いてください。','Kono yooshi ni onamae to gojusho, atesaki to nakami nado o, kaite kudasai. (onamae = honorific form of namae,  gojusho = honorific form of jusho = address,  atesaki = receiver''s name & address,  he ate the saki [tip] of the asparagus and wrote the receiver’s name & address with the stalk) (saki = tip, point, future) (nakami = content,  mi = body) (mooshikomi = application,  mooshikomi yooshi = application form,  mooshikomu = apply for)',17,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('And then, I''d like to send money, but to what number service window, if I go, will it probably be good?   Use okuru.  Use tara.  Soften this twice.','それからお金を送りたいんですが、何番の窓口へ行ったらいいんでしょうか。','Sore kara, okane o okuritain desu ga, nanban no madoguchi e ittara iin deshooka.   (from okuru = to send)',18,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, it’s stamps and postcards and receipt.','はい、切手とはがきと領収書です。','Hai, kitte to hagaki to ryooshuusho desu. (said when handing them to Tanabe)',19,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Aa, excuse me for what I did.','あっ、すみませんでした。','A, sumimasen deshita.',20,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('By the way, Webb, as for next weekend, do anything plans exist?','ところでウエッブさん今度の週末は何か予定がありますか。','Tokoro de, Uebbu san, kondo no shuumatsu wa nanika yotei ga arimasu ka.',21,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Saturday, I plan to play tennis, but as for Sunday, there is nothing in particular, but.  Use betsu.','土曜日はテニスをするつもりですが、日曜日は別に何もありませんが。','Doyoobi wa tenisu o surutsumori desu ga, nichiyoobi wa betsu ni nani mo arimasen ga.  (OK to substitute toku ni for betsu ni in this sentence)',22,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The fact is, I received two Sunday sumo tickets,  but, as for me,  suddenly my circumstances became bad.   Use boku.  Use kedo.  Soften this twice.','実は日曜日の相撲の切符を二枚もらったんですけど僕は急に都合が悪くなったんです。','Jitsu wa nichiyoobi no sumoo no kippu o nimai morattan desu kedo boku wa kyuuni tsugoo ga warukunattan desu.   (kyuu = sudden, unexpected, urgent,  a cucumber fell on my knee suddenly )',23,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If good, with your wife together, how is it?  Use tara.  Use a polite word for how.','よかったら、奥さんと一緒にいかがですか。','Yokattara, okusan to issho ni ikaga desu ka.',24,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Wow, as for that, thanks.','わあ、それはどうも。','Waa, sore wa doomo.',25,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('One time I was thinking that I''d like to try seeing sumo for sure.   Soften this.','一度相撲を見てみたいと思っていたんですよ。','Ichido sumoo o mite mitai to omotte itan desu yo.',26,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the post office, as for noon, it’s on break.','郵便局は昼は休みです。','Yuubinkyoku wa hiru wa yasumi desu.',27,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, because he wants to send a package, he will go to the post office.  Use dasu.  Use node.','マイケルさんは小包を出したいので郵便局に行きます。','Maikeru san wa kozutsumi o dashitai node yuubinkyoku ni ikimasu.',28,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, he will receive the package''s receipt and come.','マイケルさんは小包の領収書をもらってきます。','Maikeru san wa kozutsumi no ryooshuusho o moratte kimasu.',29,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, he will send the package by ship mail.  Use dasu.','マイケルさんは船便で小包を出します。','Maikeru san wa funabin de kozutsumi o dashimasu.',30,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it''s sent by airmail, it will arrive in America in 3 weeks.  Use dasu.  Use tara.  Use todoku.','航空便で出したらアメリカに3週間で届きます。','Kookuubin de dashitara, amerika ni san shuukan de todokimasu.',31,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, he is thinking that he would like to play tennis on Saturday.','マイケルさんは土曜日にテニスをしたいと思っています。','Maikeru san wa doyoobi ni tenisu o shitai to omotte imasu.',32,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Michael, he decided to watch sumo on Sunday with Tanabe.','マイケルさんは日曜日に田辺さんと相撲を見ることにしました。','Maikeru san wa nichiyoobi ni Tanabe san to sumoo o miru koto ni shimashita.',33,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael has never watched sumo.','マイケルさんは相撲を見たことがありません。','Maikeru san wa sumoo o mita koto ga arimasen.',34,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Use the stem of the verb, i.e. the pre-masu form, followed by ni to express the idea of doing something for a purpose.  Won''t you go together to see a movie?','一緒に映画を見に行きませんか。','Issho ni eiga o mi ni ikimasen ka.',35,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to Japan, what did you come to do?  Use no to soften this.','日本へは何をしに来たのですか。','Nihon e wa nani o shi ni kita no desu ka.',36,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I came to study Japanese.','日本語を勉強しに来ました。','Nihongo o benkyoo shi ni kimashita.',37,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I forgot the key at home, I want to return to get it, but...  Use node.  Soften the last verb.','うちに鍵を忘れたので取りに帰りたいんですが。','Uchi ni kagi o wasureta node, tori ni kaeritain desu ga.  (from toru = to get or take) (use ni, rather than de, with wasureru = to forget)',38,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To go for the purpose of shopping.  (You may omit the verb stem shi in this expression.)  Use plain speech for the next 2 responses.','買い物に行く。','Kaimono ni iku.',39,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To go to play golf.  (You may omit the verb stem shi in this expression, as well as in going to play tennis, going to dance etc.)','ゴルフに行く。','Gorofu ni iku.',40,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('For the purpose of doing what will he go?','何をしに行きますか。','Nani o shi ni ikimasu ka.',41,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will go for the purpose of withdrawing money.  Use orosu.','お金をおろしに行きます。','Okane o oroshi ni ikimasu.  (from orosu = to withdraw money) (oro [gold in Spanish] sues to be withdrawn)',42,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will go for the purpose of borrowing a book.','本を借りにいきます。','Hon o kari ni ikimasu.',43,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will go for the purpose of sightseeing Tokyo’s town.  Use kenbutsu.','東京の町を見物しに行きます。','Tookyo no machi o kenbutsu shi ni ikimasu.  (OK to substitute kankoo for kenbutsu in this sentence)',44,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will go for the purpose of eating sushi.','寿司を食べに行きます。','Sushi o tabe ni ikimasu.',45,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To the ocean, he will go for the purpose of swimming.','海に泳ぎに行きます。','Umi ni oyogi ni ikimasu.',46,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('He will go for the purpose of buying a TV.','テレビを買いに行きます。','Terebi o kai ni ikimasu.',47,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will buy some fish and come.','魚を買ってきます。','Sakana o katte kimasu.',48,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will go for the purpose of mailing a postcard and come.','はがきを出しに行ってきます。','Hagaki o dashi ni itte kimasu.',49,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('For a short time I went for the purpose of tennis and came.','ちょっとテニスに行ってきました。','Chotto tenisu ni itte kimashita.',50,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I will go for the purpose of buying some fish.  Plain speech.','魚を買いに行く。','Sakana o kai ni iku.',51,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I''ll buy some fish and come, huh.','魚を買ってきますね。','Sakana o katte kimasu ne.',52,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I bought some fish and came.','魚を買ってきました。','Sakana o katte kimashita.',53,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because I will buy some fish and go.  Plain speech.','魚を買っていくから...','Sakana o katte iku kara ...',54,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I bought some fish and came for sure.  Plain speech.','魚を買ってきたよ。','Sakana o katte kita yo.',55,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the post office, mail the package and come.','郵便局にいって小包を出してきてください。','Yuubinkyoku ni itte kozutsumi o dashite kite kudasai.',56,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the bookstore, buy the magazine and come.','本屋に行って雑誌を買ってきてください。','Honya ni itte zasshi o katte kite kudasai.',57,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the hospital, receive the medicine and come.','病院へ行って薬をもらってきてください。','Byooin e itte kusuri o moratte kite kudasai.',58,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the bank, withdraw some money and come.','銀行へ行ってお金をおろしてきてください。','Ginkoo e itte okane o oroshite kite kudasai.  (from orosu)',59,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the library, return the books and come.','図書館に行って本を返してきてください。','Toshokan e itte hon o kaeshite kite kudasai.',60,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please go to the ward office, do the alien registration and come.','区役所に行って外国人登録をしてきてください。','Kuyakusho ni itte gaikokujin tooroku o shite kite kudasai.   (registration is held from too to roku [10 to 6])',61,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow I will go to Narita airport and come.','明日、成田空港に行ってきます。','Ashita Narita kuukoo ni itte kimasu.',62,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for yesterday, at the zoo, I saw the panda baby and came.','昨日は動物園でパンダの赤ちゃんを見てきました。','Kinoo wa doobutsuen de panda no akachan o mite kimashita.  (doobutsu = animal,  animals have doughy butts)',63,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Previously, Mr. newspaper store came for the purpose of getting money.','さっき新聞屋さんがお金を取りに来ました。','Sakki shinbunya san ga okane o tori ni kimashita.  (OK to say saki hodo, but not sakki hodo)',64,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yesterday a friend came to meet me.','昨日友達が私に会いに来ました。','Kinoo tomodachi ga watashi ni ai ni kimashita.',65,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yesterday, as for me, I met the friend and came.','昨日私は友達に会ってきました。','Kinoo watashi wa tomodachi ni atte kimashita.',66,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow I will borrow books at the library and come.','明日図書館で本を借りてきます。','Ashita toshokan de hon o karite kimasu.',67,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The day after tomorrow at the station, I will reserve seats for the bullet train and come.   Use zaseki.','あさって駅で新幹線の座席を予約してきます。','Asatte eki de shinkansen no zaseki o yoyaku shite kimasu. (Zach has a seki [cough], and he’s sitting in my seat) (zaseki = a seat,  used for transportation, theaters)',68,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Next week my hometown parents will come for the purpose of seeing my new house.','来週いなかの両親が私の新しい家を見にきます。','Raishuu inaka no ryooshin ga watashi no atarashii ie o mi ni kimasu.',69,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you follow a question word with ka, it means','','Some.',70,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you follow a question word with mo, generally speaking, it means','','All or every in positive constructions, never, none or nowhere in negative constructions.',71,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('However, in affirmative sentences, use one of the following 3 words instead of daremo or  nanimo.','みんな。みな。すべて。','Minna.  Mina.  Subete.',72,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you follow a question word with demo, it means','','Any.',73,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sometime.','いつか。','Itsuka.',74,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('All the time.  Always.','いつも。','Itsumo.',75,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Never.','いつもない。','Itsumo nai.',76,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anytime.  Whenever.','いつでも。','Itsudemo.',77,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Somewhere.','どこか。','Dokoka.',78,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everywhere.','どこも。','Dokomo.',79,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nowhere.','どこもない。','Dokomo nai.',80,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anywhere.  Wherever.','どこでも。','Dokodemo.',81,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Someone.','だれか。','Dareka.',82,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everyone.','みんな。みな。すべて。','Minna.  Mina.  Subete.',83,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No one.','だれも...ない。','Daremo ... nai.',84,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anyone.  Whoever.','だれでも。','Daredemo.',85,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Something.','何か。','Nanika.',86,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everything.','みんな。みな。すべて。','Minna.  Mina.  Subete.',87,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nothing.','何もない。','Nanimo nai.',88,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anything.  Whatever.','何でも。','Nandemo.  (nanidemo, not OK)',89,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('One of them.','どれか。','Doreka.',90,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Everyone.   All of them.','どれも。','Doremo.',91,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('None of them.','どれもない。','Doremo nai.',92,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Any of them.  Whichever one.','どれでも。','Doredemo.',93,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Sometime, I''d like to go to the honorable home, meaning your home, but.  Soften this.','いつかお宅に行きたいんですが。','Itsuka otaku ni ikitain desu ga.',94,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If it isn''t a Tuesday, anytime is good for sure.  Use de rather than ja.  Use tara.','火曜日でなかったらいつでもいいですよ。','Kayoobi de nakattara itsudemo ii desu yo.  (kayoobi ja nakattara is also OK,  kayoobi de wa nakattara, also OK, not as good)  (kayoobi ja nai dattara, not OK)',95,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tuesdays, I''m always going for the purpose of learning dancing.  Use dansu.  Soften this.','火曜日はいつもダンスを習いに行っているんです。','Kayoobi wa itsumo dansu o narai ni itte irun desu.  (OK to substitute odori for dansu)',96,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, for that reason, as for Tuesdays, always you are not at home, right?  Soften this.','ああ、それで火曜日はいつもうちにいないんですね。','Aa, sore de, kayoobi wa itsumo uchi ni inain desu ne.',97,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Among X and Y and Z, I will give you one of them, but which one is good?  Use uchi no.','XとYとZのうちのどれかをあげますが、どれがいいですか。','X to Y to Z no uchi no, doreka o agemasu ga, dore ga ii desu ka.  (no uchi de, no naka kara, also OK)  (OK to omit ‘o’ after doreka)',98,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yes, all  of them are good, right?  Use a grunt.','うーん、どれもいいですね。','Uun, dore mo ii desu nee.',99,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, any of them is good for sure.','私はどれでもいいですよ。','Watashi wa doredemo ii desu yo.',100,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Today will someone come to your home?','今日だれかあなたのうちにきますか。','Kyoo dareka anata no uchi ni kimasu ka.  (dareka ga,also OK)',101,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, no one will come.','いいえ、だれも来ません。','Iie daremo kimasen.',102,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In that box, what is entered?','その箱に何が入っていますか。','Sono hako ni nani ga haitte imasu ka.',103,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In that box, is something entered?','その箱に何か入っていますか。','Sono hako ni nanika haitte imasu ka.  (nanika ga, also OK, but not as good)',104,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I didn''t meet anyone.','だれにも会いませんでした。','Dare ni mo aimasen deshita.  [when the particles ni, e, to, kara & made are used with some interrogative pronouns (nani, dore, dare, donata, doko, dotchi & dochira) followed by mo, they are placed in the middle of the phrase, as seen here and in the following sentence]',105,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I didn''t go anywhere.','どこへも行きませんでした。','Doko e mo ikimasen deshita.',106,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From where can it be seen?','どこから見えますか。','Doko kara miemasu ka. (mieru = to be visible,  cf. mirareru, which means ‘able to see,’ and is the potential form of miru,  potential forms are covered in Lesson 18)',107,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It can be seen from anywhere for sure.','どこからでも見えますよ。','Doko kara demo miemasu yo.',108,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I didn''t buy anything.','何も買いませんでした。','Nani mo kaimasen deshita.',109,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is someone in the room next door?','となりの部屋に誰かいますか。','Tonari no heya ni dareka imasu ka.  (dareka ga, also OK)',110,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please look for a second and come.','ちょっと見てきてください。','Chotto mite kite kudasai.',111,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No one was there, for sure.','誰もいませんでしたよ。','Daremo imasen deshita yo.',112,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are the subways always this crowded?  Soften this.','地下鉄はいつもこんなに込んでいるんですか。','Chikatetsu wa itsumo konna ni konde irun desu ka.',113,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it''s only morning and evening.  Use yuugata.','いいえ、朝と夕方だけです。','Iie, asa to yuugata dake desu.  (yuugata = evening, similar to yuube = last night,  cf. konya = tonight,  I wear my yukata [summer robe] in the yuugata [evening])',114,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for sashimi, sushi, tempura etc., will you eat them?','さしみや寿司や天ぷらは食べますか。','Sashimi ya, sushi ya, tempura wa tabemasu ka.',115,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, since I like all of them, I will eat them often, for sure.  Use dakara.','ええ、どれも好きだからよく食べますよ。','Ee, doremo suki dakara, yoku tabemasu yo.',116,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the next trip, where shall we go?','今度の旅行はどこに行きましょうか。','Kondo no ryokoo wa doko ni ikimashoo ka.  (tsugi no ryokoo, also OK)',117,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, anywhere is good for sure.','私はどこでもいいですよ。','Watashi wa dokodemo ii desu yo.',118,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you gone to Hokkaido?','北海道に行ったことがありますか。','Hokkaidoo ni itta koto ga arimasu ka.',119,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, but sometime I would like to go, I''m thinking.','いいえ、でもいつか行きたいと思っています。','Iie, demo itsuka ikitai to omotte imasu.',120,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When shall we go to your home?','いつあなたのうちに行きましょうか。','Itsu anata no uchi ni ikimashoo ka.',121,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anytime is good for sure.','いつでもいいですよ。','Itsu demo ii desu yo.',122,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since sweet thing nothing doesn’t exist, let''s buy something and come.  ‘Sweet thing’ is the subject.  Use plain speech in the first clause.','甘いものが何もないから何か買ってきましょう。','Amai mono ga nanimo nai kara, nanika katte kimashoo.   (ok to omit ga) (OK to omit nanimo) (doreka, not OK, unless you have a group to choose from)  (nanika o katte, also OK)',123,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('What is good?','何がいいですか。','Nani ga ii desu ka.',124,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Anything is good for sure.','何でもいいですよ。','Nan demo ii desu yo.',125,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('From the at-here-existing 3 things, please choose one of them, one thing.  Use naka kara.','ここにある三つのものの中からどれか一つを選んでください。','Koko ni aru mittsu no mono no naka kara, doreka hitotsu o erande kudasai.  (OK to omit wo after hitotsu)  (uchi de, also OK,  naka de, not OK) (from erabu = erabimasu, to choose,  in the era of Bill Clinton, we got to choose)',126,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Are any of them good?  Soften this.','どれでもいいんですか。','Doredemo iin desu ka.',127,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To that person over there, at somewhere, I ought to have met, but.  Use nan to soften this.','あの人にどこかで会ったはずなんですが。','Ano hito ni dokoka de, atta hazu nan desu ga.',128,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If there is a book you want to read, any of them, one volume, please choose.  Use tara.','読みたい本があったらどれでも一冊選んでください。','Yomitai hon ga attara, doredemo issatsu, erande kudasai.  (hon ga yomitai dattara, not OK,  either yomitakattara or yomitai nara is OK)  (doredemo = any of them,  doreka = one of them,  doremo = all of them)',129,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, all of them I don''t want to read much, right?  Use a grunt.','うん、どれもあまり読みたくありませんねぇ。','Uun.  Doremo amari yomitaku arimasen nee.',130,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this past Sunday, did you go somewhere?','この前の日曜日はどこかへ行きましたか。','Kono mae no nichiyoobi wa dokoka e ikimashita ka.',131,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, I didn''t go to nowhere.','いいえ、どこにも行きませんでした。','Iie doko ni mo ikimasen deshita.',132,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because, as for Sunday, everywhere is crowded.','日曜日はどこも込んでいますから。','Nichiyoobi wa dokomo konde imasu kara.',133,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for this, what kind of juice is it probably?  Use nan no.  Don’t use ka.','これは何のジュースでしょう。','Kore wa nan no juusu deshoo.',134,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please drink it for a second and see.','ちょっと飲んでみてください。','Chotto nonde mite kudasai.',135,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Let''s ask that person over there the way and see.','あの人に道を聞いてみましょう。','Ano hito ni michi o kiite mimashoo.',136,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To a Japanese person, when I spoke in Japanese to see, I communicated.  Use tara.','日本人に日本語で話してみたら通じました。','Nihonjin ni nihongo de hanashite mitara, tsuujimashita.  (from tsuujiru = to communicate or make oneself understood, to pervade,  the man in the tsuki [the man in the moon] jeers under the table while we are communicating, and this pervades the room,  cf. otsuuji = bowel movement,  otsuuji ga aru = one''s bowels move)',137,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever eaten pickled plums?','梅干を食べたことがありますか。','Umeboshi o tabeta koto ga arimasu ka.  (ume = plum,  uma = horse,  umi = ocean,  umareru = be born, umeboshi = pickled plum or apricot,  boshi = hat,  put the ume [plum] under a boshi [hat] and you get pickled plums) (uma, ume, umi, in alphabetical order:  a horse on a plum in the ocean)',138,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, it still doesn’t exist, meaning ‘I haven''t yet.’','いいえ、まだありません。','Iie, mada arimasen.',139,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('They are delicious for sure.  By all means, once, please try eating them and see.','おいしいですよ。ぜひ一度食べてみてください。','Oishii desu yo.  Zehi ichido tabete mite kudsai.',140,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever read a Japan’s novel?','日本の小説を読んだことがありますか。','Nihon no shoosetsu o yonda koto ga arimasu ka.  (in the shoosetsu [novel], he shows the sets)',141,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s interesting for sure.  By all means, please try reading once and see.','面白いですよ。ぜひ一度読んでみてください。','Omoshiroi desu yo.  Zehi ichido yonde mite kudasai',142,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever gone to Izu?','伊豆に行ったことがありますか。','Izu ni itta koto ga arimasu ka.',143,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s a pretty place for sure.  By all means, please try going once and see.','きれいなところですよ。ぜひ一度行ってみてください。','Kireina tokoro desu yo.  Zehi ichido itte mite kudasai.',144,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever worn a Japanese kimono?','日本の着物を着たことがありますか。','Nihon no kimono o kita koto ga arimasu ka.',145,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s pretty for sure.  By all means, please try wearing one once and see.','きれいですよ。ぜひ一度着てみてください。','Kirei desu yo.  Zehi ichido kite mite kudasai.',146,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever used a fax?','ファクシミリを使ったことがありますか。','Fakushimiri o tsukatta koto ga arimasu ka.',147,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s convenient for sure.  By all means, please try using one once and see.','便利ですよ。ぜひ一度使ってみてください。','Benri desu yo.  Zehi ichido tsukatte mite kudasai.',148,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Have you ever gone to Nara?','奈良に行ったことがありますか。','Nara ni itta koto ga arimasu ka.',149,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, not yet.  I want to go once and see.','いいえ、まだです。一度行ってみたいです。','Iie, mada desu.  Ichido itte mitai desu.',150,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey, I wonder if I mistook the street?   Use machigaeru.  Use no to soften this.  Use kana.','あれ、道を間違えたのかな。','Are, michi o machigaeta no kana.  (michi means ‘way’ here) (kana means I wonder,  I wonder how to learn kana [hiragana & katakana])  (ara and are both  mean ‘hey’) (the no is optional)  (machigaeru = machigau,  so OK to substitute machigatta for machigaeta in this sentence)',151,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I’ll look at a map for a second and see.','ちょっと地図を見てみます。','Chotto chizu o mite mimasu.',152,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('In front of the station, a new restaurant was accomplished, for sure.','駅の前に新しいレストランができましたよ。','Eki no mae ni atarashii resutoran ga dekimashita yo.  (not OK to say eki no mae de, since you must use ni with dekiru)',153,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Next time, together, to eat, let’s go and see.','今度一緒に食べに行ってみましょう。','Kondo issho ni tabe ni itte mimashoo.',154,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This frock, how is it?  Don’t use wa or ga.  Use a polite word for how.','このワンピース如何ですか。','Kono wanpiisu ikaga desu ka.',155,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, I will wear it for a second and see.','じゃ、ちょっと着てみます。','Ja, chotto kite mimasu.',156,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won’t you invite the friend along and come?','友達を誘ってきませんか。','Tomodachi o sasotte kimasen ka. (sasou = sasoimasu = to invite or tempt,  the sassy owl invited me)',157,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, well, I will do a phone call, ask and see.','ええ、じゃ電話して聞いてみます。','Ee, ja, denwa shite kiite mimasu.',158,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether or not.','かどうか。','Kadooka.',159,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow I don’t know whether the weather will become good.  Use wakaru to mean ‘know’ in this section.','明日天気がよくなるかどうかわかりません。','Ashita tenki ga yoku naru kadooka wakarimasen.',160,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This soup, whether it’s delicious, drink it for a second and see.  Don’t use wo after soup.  Plain speech.','このスープおいしいかどうかちょっと飲んでみて。','Kono suupu oishii kadooka chotto nonde mite.',161,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will Yamada come?','山田さんは来ますか。','Yamada san wa kimasu ka.',162,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don’t know whether he will come, I will ask by telephone and see.','来るかどうかわからないから電話で聞いてみます。','Kuru kadooka, wakaranai kara, denwa de kiite mimasu.',163,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Inside that box, what is entered?','その箱の中に何が入っていますか。','Sono hako no naka ni nani ga haitte imasu ka.',164,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don’t know whether something is entered in it, I will open it and see. (something is implied but not stated)','入っているかどうかわからないからあけてみます。','Haitte iru kadooka wakaranai kara, akete mimasu.',165,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tsuchida, as for next Thursday are the circumstances good?','土田さんは今度の木曜日は都合がいいですか。','Tsuchida san wa kondo no mokuyoobi wa tsugoo ga ii desu ka.',166,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don’t know whether the circumstances are good, I will ask and see.','都合がいいかどうかわからないから聞いてみます。','Tsugoo ga ii kadooka wakaranai kara, kiite mimasu.',167,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is Tanaka in the room?','田中さんは部屋にいますか。','Tanaka san wa heya ni imasu ka.',168,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don’t know whether he’s in the room, I will look and see.','部屋にいるかどうかわからないからみてみます。','Heya ni iru kadooka wakaranai kara, mite mimasu.',169,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Do penguins live in Africa?','ペンギンはアフリカに住んでいますか。','Pengin wa afurika ni sunde imasu ka.',170,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don''t know whether they live in Africa, I will check from a book and see.','アフリカに住んでいるかどうかわからないから本で調べてみます。','Afurika ni sunde iru kadooka wakaranai kara, hon de shirabete mimasu.  (shiraberu = to check,  Shiro & Abe do kangaroo checking,  cf. shiraseru = to inform,  I will (shiraberu [check] the serum and inform you,  cf.  kuraberu = to compare,  to cure Abe’s rupture is easy, compared to other surgery)',171,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that apartment new?','そのアパートは新しいですか。','Sono apaato wa atarashii desu ka.',172,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since I don''t know whether it''s new or not, I''ll ask a real estate store (meaning agent) and see.','新しいかどうかわからないから不動産屋に聞いてみます。','Atarashii ka dooka wakaranai kara, fudoosanya ni kiite mimasu. (fudoosan = real estate,  the realtor Foodoo san likes food & dough) (fudoosanya can mean either the office or the realtor)',173,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that work, will it finish by 4:00?','その仕事は4時までに終わりますか。','Sono shigoto wa yoji made ni owarimasu ka.',174,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether it will finish I don''t know, but I will do and see.  Use yaru.  In the next dozen or so responses, if you are asked to say ‘but,’ use keredo.','終わるかどうかわからないけれどやってみます。','Owaru kadooka wakaranai keredo yatte mimasu.  (yaru = give or do)',175,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that book, is it difficult?','その本は難しいですか。','Sono hon wa muzukashii desu ka.',176,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether it''s difficult I don''t know, but I will read it and see.','難しいかどうかわからないけれど読んでみます。','Muzukashii kadooka wakaranai keredo yonde mimasu.',177,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will you be able to explain it in Japanese?','日本語で説明ができますか。','Nihongo de setsumei ga dekimasu ka.  (explain why the set has Sue and May on it) (OK to omit ga)',178,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether I can explain it I don''t know, but I''ll do it and see.','説明ができるかどうかわからないけれどしてみます。','Setsumei ga dekiru kadooka wakaranai keredo, shite mimasu.',179,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is the ward office still open?','区役所はまだあいていますか。','Kuyakusho wa mada aite imasu ka.',180,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether it is open I don''t know, but I''ll go and see.','あいているかどうかわからないけれど行ってみます。','Aite iru kadooka wakarani keredo, itte mimasu.',181,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is that fish delicious?','その魚はおいしいですか。','Sono sakana wa oishii desu ka.',182,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether it''s delicious I don''t know, but I''ll eat it and see.','おいしいかどうかわからないけれど食べてみます。','Oishii kadooka wakaranai keredo tabete mimasu.',183,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Will Yamada probably give that rose flower over to me ?   Me is understood.  Use ordinary politeness.','山田さんはあのバラの花をくれるでしょうか。','Yamada san wa ano bara no hana o 	kureru deshoo ka.  (bara = rose,  Barabbas was freed because he gave Pilate a rose)',184,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Whether he will give it I don''t know, but I''ll request it and see.','くれるかどうかわからないけれど頼んでみます。','Kureru kadooka wakaranai keredo, tanonde mimasu.',185,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please don''t forget.','忘れないでください。','Wasurenai de kudasai.  (nai de kudasai, used after a verb stem = please don’t)',186,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won''t you please not do and give.','ないでくださいませんか。','Use nai to mean not do.  Use the honorific form.Nai de kudasaimasen ka.',187,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please don''t – polite form.','ないでください。','Nai de kudasai.',188,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please don''t – familiar  form.','ないでちょうだい。','Nai de choodai.',189,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Don’t, huh.','ないでね。','Nai de ne.',190,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Won''t you  not park the car in front of the gate and give?  Honorific form.','門の前に車を止めないでくださいませんか。','Mon no mae ni kuruma o tomenai de kudasaimasen ka.  (Monday is the gateway to the week)  (mon no mae de, also OK,  koko de tomeru and koko ni tomeru, both OK,  ni is more specific as to location)',191,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Late at night, please don''t do a phone call.','夜遅く電話をしないでください。','Yoru osoku denwa o shinai de kudasai.',192,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow, when we go for the purpose of tennis, don''t forget the money, huh?  Use toki.','明日テニスに行くときお金を忘れないでね。','Ashita tenisu ni iku toki okane o wasurenai de ne.  (tenisu ni ittara okane, OK, but changes meaning to ‘when or if we go’)',193,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Don''t tell lies, please.  Use a familiar form of ‘please.’','うそを言わないでちょうだい。','Uso o iwanai de choodai.    (he lied about his work for the USO)',194,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To say that someone must not do something, use the te form of the verb followed by wa followed by  (3 responses)  Don’t use plain speech.','いけません。だめです。こまります。','Ikemasen, dame desu, komarimasu.  (there is no u or ru verb corresponding to ikemasen or ikenai)  (komaru = to inconvenience,  kamau = to mind)',195,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('You must not throw garbage here.','ここにごみを捨ててはいけません。','Koko ni gomi o sutete wa ikemasen.  (suteru = to throw away,  he threw away the suteeki, steak) (koko de, not really wrong, but koko ni is more specific as to location)',196,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Running on the hallways, bad, for sure.  (contracted, colloquial form,  addressed to a child)  Don’t use da or desu.','廊下を走っちゃだめよ。','Rooka o hashitcha dame yo.  (hashite wa = hashitcha, used in colloquial speech,  cf. nonde wa dame desu = nonja dame desu = drinking is bad, since dewa = ja)  (rooka de, also OK, if you are somewhere else, implying a destination where the running occurs)',197,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Tomorrow, I’d like to rest the company, but.  Soften this.  Meaning, I’d like to be off.','明日会社を休みたいんですが。','Ashita kaisha o yasumitain desu ga.',198,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Nah, taking time off would inconvenience, for sure.  Use a contracted colloquial form.','いや、休んじゃこまりますよ。','Iya, yasun ja komarimasu yo.  (yasunja = yasunde wa)  (komaru, also OK)',199,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('You must not enter inside the grass.','芝生の中に入ってはいけません。','Shibafu no naka ni haitte wa ikemasen.  (shibafu = lawn,  the Queen of Sheba had fun on the lawn)',200,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('For a plain ‘do not,’ used by men or seen on signs, follow a plain non-past verb with','な。','Na  (cf. na is also used as an intensifier)',201,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Don’t throw garbage.	 Use na.','ごみを捨てるな。','Gomi o suteru na.',202,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Don’t return and come very late for sure.  Use amari.  Use na.','あまり遅く帰ってくるなよ。','Amari osoku kaette kuru na yo.',203,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for here, it’s narrow or tight, and because a small child exists... Use shi.  Use node.  Use plain speech.','ここは狭いし小さい子供がいるので...','Koko wa semai shi, chiisai kodomo ga iru node ..',204,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Say, because, as for here, it is becoming parking prohibited.','あのう、ここは駐車禁止になっていますから。','Anoo, koko wa chuusha kinshi ni natte imasu kara.  (chuusha = parking, kinshi = prohibition, short or nearsightedness) (the dog chews Shyam when parking,  there is a kin [gold] shield over prohibited things)  (cf. kin’en = no smoking)',205,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('I would like to smoke tobacco, but … Soften this.','タバコを吸いたいんですが。','Tabako o suitain desu ga.',206,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Er, as for here, there’s no smoking, 	and  since a child also exists, a little bit …  Use plain speech in the first clause.  Use shi.  Use node.','ええと、ここは禁煙だし子供もいますので、ちょっと...','Eeto, koko wa kin’en da shi, kodomo mo imasu node, chotto…. (kin’en = no smoking, I''ll give you kin [golden] yen to stop smoking)',207,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Please don’t give feed to the monkeys.','猿にえさをやらないでください。','Saru ni esa o yaranai de kudasai.  (Saruman keeps a monkey)  (cf. sara = plate) (esa = bait or feed)  (the food at Esalen tastes like animal feed)',208,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for to pick the flowers, bad for sure.  (contracted colloquial speech, said to a child)  Don’t use da or desu.','花を折っちゃだめよ。','Hana o otcha dame yo.  (oru = bend or break, or to pick a flower,  otte wa = otcha) (break the ornament under the tree)  (cf. orosu = withdraw money)',209,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To enter the grass is bad for sure.  Plain colloquial speech.','芝生に入っちゃだめだよ。','Shibafu ni haitcha dame da yo.  (haitcha = haitte wa)',210,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for inside the fountain, you must not play, for sure.  Contracted colloquial form.  Plain speech.','噴水の中では遊んじゃいけないよ。','Funsui no naka de wa asonja ikenai yo.   (asonja = asonde wa)  (have fun in the sun in the fountain)',211,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Garbage, to this kind of place, as for to throw,  bad for sure.  Plain colloquial speech.  Don’t use da or desu.','ごみをこんな所に捨てちゃだめよ。','Gomi o konna tokoro ni sutecha dame yo.  (sutecha = sutete wa)',212,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because a baby exists, can you not not smoke tobacco and I receive?  Use node.  Speaking to a stranger.','赤ちゃんがいるのでタバコを吸わないでいただけませんか。','Akachan ga iru node, tabako o suwanai de itadakemasen ka.  (itadakemasen ka = am I not able to receive,  cf.  itadakimasen ka = will I not receive,  cf.  kudasaimasen ka = won''t you please give me)',213,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It''s bad to do soccer here, for sure. Plain colloquial speech.','ここでサッカーをしちゃだめだよ。','Koko de sakkaa o shicha dame da yo.  (shite wa = shicha)',214,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Now it is scheduled that you must not smoke tobacco, huh?  Soften this.','今タバコを吸ってはいけないことになっているんですね。','Ima tabako o sutte wa ikenai koto ni natte irun desu ne.',215,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hello, since today is Tuesday, you must not throw out garbage for sure.  Use moshimoshi.  Use dakara.  Use dasu. Use a colloquial form of must not throw with ikemasen.','もしもし、今日は火曜日だからごみを出しちゃいけませんよ。','Moshimoshi, kyoo wa kayoobi dakara, gomi o dashicha ikemasen yo.  (dashite wa = dashicha)',216,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s under construction.  You may not enter inside.','工事中です。中に入ってはいけません。','Kooji-chuu desu.  Naka ni haitte wa ikemasen.  (kooji = construction,  koojoo = factory,  Conan and Joe built a factory,  when kooji [construction] is being done on a koojoo [factory], they put a ji [letter] on it)',217,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('You must not park a car here.','ここに車を止めてはいけません。','Koko ni kuruma o tomete wa ikemasen.',218,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Hey!  This sweater, in water, it’s bad to wash.  Use ara.  Don’t use wo after sweater.  Use a colloquial form.  Plain speech.  Use nan to soften this.  Use woman''s word for emphasis.','あら、このセーター水で洗っちゃだめなんだわ。','Ara, kono seetaa mizu de aratcha dame nan da wa.  (wa used for emphasis,  aratcha = aratte wa)',219,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael’s diary.  Don’t use san.','マイケルの日記。','Maikeru no nikki.',220,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('On Sunday with Barbara we went in order to see sumo.  Plain speech.','日曜日にバーバラと相撲を見に行った。','Nichiyoobi ni baabara to sumoo o mi ni itta.',221,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Safely, we arrived at the national sports arena, but we were starving.  Plain speech.  Use no to soften the first clause.','無事に国技館に着いたのだがお腹がペコペコだった。','Buji ni kokugikan ni tsuita no da ga, onaka ga pekopeko datta. (buji = safety, peace, health, good condition,  buji ni = safely, without incident,  we have to budget for a jeep if we want to travel safely, kokugikan = National Sports Arena, the koku [country] where geeks play with cans at the national sports arena,  onaka ga pekopeko da = to be starving,  all we had was pekoe tea, pekoe tea, so we''re starving)',222,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('To tell the truth, on the way, I planned to have a meal, but a suitable store was not found.  Plain speech.  Use no to soften this, twice.','実は途中で食事をするつもりだったのだが適当な店が見つからなかったのだ。','Jitsu wa, tochuu de, shokuji o suru tsumori datta no da ga, tekitoo na mise ga mitsukaranakatta no da.   (tochuu de = on the way,  tekitoo na = adequate, suitable, a techie''s toe is adequate for pressing keys on a keyboard,  mitsukaru = to be found,  mitsukeru = to find) (Mitsu found some cake)',223,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('For that reason, to people walking on the proximity, inside the national sports arena, I asked and saw whether a meal was possible.  Use soba to mean proximity.  Plain speech.','それでそばを歩いている人に国技館の中で食事ができるかどうか聞いてみた。','Sore de soba o aruite iru hito ni, kokugikan no naka de shokuji ga dekiru kadooka kiite mita.  (soba = close to) (soba de aruite, not OK,  however, other verbs can be used with soba and de, e.g., ie no soba de asobimasu = he plays in the vicinity of the house)',224,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, it’s possible for sure.','ええ、できますよ。','Ee, dekimasu yo.',225,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('There are also restaurants, and they are selling honorable box lunches also.   Use shi.','レストランもありますしお弁当も売っています。','Resutoran mo arimasu shi, obento mo utte imasu.',226,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('But, since the beer being sold inside expensive, at somewhere, it would be better to buy canned beer and go, huh.','でも、中で売っているビールは高いからどこかで缶ビールを買って行った方がいいですね。','Demo, naka de utte iru biiru wa takai kara, dokoka de kanbiiru o katte itta hoo ga ii desu ne.   (cannot say kanbiru o itte katta because katte must follow immediately after kanbiiru o)  (biiru ga, not OK, because too specific)',227,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('This was kind advice.  Plain speech.','これは親切なアドバイスだった。','Kore wa shinsetsu na adobaisu datta.',228,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, looking at the honorable Mr. sumo wrestlers, she said.  Plain speech.','バーバラはお相撲さんを見て言った。','Baabara wa osumoo san o mite, itta. (itta = said,  cf. ita = existed, itta = went)',229,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('My.  As for those people over there every day here they are drinking expensive beer.   Use an intensifier used by women.  Soften this.','まあ、あの人たちは毎日ここで高いビールを飲んでいるんだわ。','Maa.  Ano hitotachi wa mainichi koko de takai biiru o nonde irun da wa.',230,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The Michael group, the time they arrived at the national sports arena, as for were starving thing, why is it?  Use no to make a noun phrase.','マイケルさん達が国技館に着いたときお腹がペコペコだったのはどうしてですか。','Maikeru san tachi ga kokugikan ni tsuita toki onaka ga pekopeko datta no wa, dooshite desu ka.  (maikerusantachi wa, also OK)',231,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s because, on the way, they did not do a meal and come.  Use plain speech to say ‘did not come.’','途中で食事をしてこなかったからです。','Tochuu de shokuji o shite konakatta kara desu.',232,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for inside the national sports arena, what are they selling?','国技館の中では何を売っていますか。','Kokugikan no naka de wa nani o utte imasu ka.',233,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('They are selling honorable box lunches, beer, etc.','お弁当やビールを売っています。','Obentoo ya biiru o utte imasu.',234,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Barbara, looking at the honorable Mr. sumo wrestlers, ‘Everyday they are drinking expensive beer,’ as for she said thing, why is it, do you think?  Barbara is the subject. Soften her speech and include an intensifier used by women.  Use no to make a noun phrase.  Use ka only once, at the end.','バーバラさんがお相撲さんを見て「毎日高いビールを飲んでいるんだわ」と言ったのはどうしてだと思いますか。','Baabara san ga, osumoo san o mite, ‘Mainichi takai biiru o nonde irun da wa’ to itta no wa, dooshite da to omoimasu ka.  (Baabara san wa, also OK)',235,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s because the honorable Mr. sumo wrestlers are big and being fat, I think.  Use te to mean and.','お相撲さんが大きくて太っているからだと思います。','Osumoo san ga ookikute futotte iru kara da to omoimasu.  (futoru = to grow fat,  futotta = fat,  he''s so fat, he sleeps on a futon with Ruth) [cannot say futotte iru da (or dakara),  you can’t follow a verb with da]',236,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Words that mean to give.  5 responses.  Use the masu form of all verbs in the following section.','あげます。さしあげます。やります。くれます。くださいます。','Agemasu. Sashiagemasu. Yarimasu. Kuremasu. Kudasaimasu.',237,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you or someone else give to someone not in your in-group, showing normal respect, use','上げます。','Agemasu.',238,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you or someone else give to someone not in your in-group, showing extra respect, use','差し上げます。','Sashiagemasu.',239,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If you or someone else give to someone in your in-group, or to someone who is inferior, use','やります。','Yarimasu	 (however, a woman would be more likely to use agemasu than yarimasu when she gives to a person in her in-group)',240,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If something is given to you or to a member of your in-group by someone of equal or inferior status, or by someone in your in-group, use','くれます。','Kuremasu',241,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('If something is given to you or to a member of your in-group by someone outside your in-group who has equal or greater age or status, use','くださいます。','Kudasaimasu',242,15);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Who is that red-frock wearing person over there?  Use polite words for person and for who.','あの赤いワンピースを着ている方はどなたですか。','Ano akai wanpiisu o kite iru kata wa donata desu ka.',1,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, that over there is Mr. Tsuchida''s wife.','ああ、あれは土田さんの奥さんです。','Aa, are wa Tsuchida san no okusan desu.',2,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that way over there’s kimono person?  Use achira.  Use a polite word for person.','あちらの着物の方は。','Achira no kimono no kata wa?',3,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Isn''t it the office''s Suzuki?  Use jimu.','事務の鈴木さんじゃありませんか。','Jimu no Suzuki san ja arimasen ka.  (jimu = jimusho = office, jimuin = office worker or clerk) (Jim is hiding under the desk at the office)',4,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since as for party times, it’s always','パーティーのときはいつも和服だから。','Japanese clothes. Use dakara.Paatii no toki wa, itsumo wafuku dakara.',5,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, that''s true, I didn''t know/understand her at all.  Use chittomo.  Plain speech. Meaning, I didn’t recognize her.','あっ、ほんとだ。ちっともわからなかった。','A, hontoo da. Chittomo wakaranakatta.  (chittomo = not at all in negative phrases) (I was not chit chatting with Tomoko at all) (OK to substitute zenzen for chittomo)',6,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Her appearance associated with always is entirely different, right?  Use to to mean associated with.','いつもと感じが全然ちがいますね。','Itsumo to kanji ga zenzen chigaimasu ne.  (kanji = feeling, looks, appearance,  cf. kanjiru = feel or sense) (to means associated with)',7,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The shoes Hanada is wearing are lovely, huh?  Use no after Hanada.  Don’t use wa or ga.','花田さんのはいている靴すてきですね。','Hanada san no haite iru kutsu suteki desu ne.  (suteki = lovely, cute, fine, splendid, swell,  cf. suteeki = steak) (that''s a lovely little suteeki you''re eating) (haku = wear on feet or legs) (hackers wear thongs on their feet) (Hanada san ga haite iru, also OK)',8,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The time she went to Hong Kong, she bought them, reportedly, for sure. Use plain speech.  Use desu.','香港に行ったとき買ったそうですよ。','Honkon ni itta toki katta soo desu yo.  (soo desu = reportedly)',9,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('They suit her considerably, huh?  Use nakanaka.','なかなか似合いますね。','Nakanaka niaimasu ne.  (niau = to fit, to go well with, to suit) (those knee pads give me a ‘knee ow’ but they fit)  (cf. maniau = to be on time)  (nakanaka = considerably, quite in positive phrases, not easily or readily in negative phrases) (because he was nakanaka [inside the inside], he was considerably accepted by the others and he could do things readily)',10,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Expression of mild surprise. Yamashita doesn’t exist, huh?','おや、山下さんがいませんねぇ。','Oya, Yamashita san ga imasen nee.',11,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('How did it do probably?  (meaning, what happened?)  Soften this.  Don’t use ka.','どうしたんでしょう。','Dooshitan deshoo.',12,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Surely, still, it’s the company, probably, meaning he’s at the company.','きっとまだ会社でしょう。','Kitto mada kaisha deshoo.',13,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Because they were saying, by the end of today, to the Osaka branch office, a to-send written report exists.  Use okuru.  Use tte ittemashita.','今日中に大阪支社に送る報告書があるって言ってましたから。','Kyoo juu ni oosaka shisha ni okuru hookoku sho ga arutte ittemashita kara.  (kyoo juu ni = by the end of today) (hookoku = report,  sho = writing, so hookokusho = a written report) (arutte = aru to, since tte, like to, is used to show quotes)',14,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, yesterday also, all day, all the way through, he was doing it, huh.  Use yaru.','ああ、昨日も一日中ずーっとやっていましたね。','Aa, kinoo mo ichinichijuu zutto yatte imashita ne.  (yaru = to give, do, get along)',15,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that over there, it’s probably Webb''s wife.','あれはウエッブさんの奥さんでしょう。','Are wa Uebbu san no okusan deshoo.',16,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She''s wearing a fancy hat, huh?  Use an intensifier used by women.  Plain speech.','しゃれた帽子をかぶっているわね。','Shareta boshi o kabutte iru wa ne.  (shareta = fancy, stylish,  shareru = to dress fancy,  kaburu = to wear on head) (Sharon Stone retains her rubies which are fancy and stylish ) (kabuki players have rubies in the hats they wear on their heads)',17,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, I still hadn''t introduced you?','ああ、まだ紹介していませんでしたか。','Aa, mada shookai shite imasen deshita ka. (introduce a Showman to Kyle)',18,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for that, I did a discourtesy.','それは失礼しました。','Sore wa shitsurei shimashita.',19,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for of Japanese cooking, what do you like best?','日本料理では何が一番好きですか。','Nihon ryoori de wa, nani ga ichiban suki desu ka.',20,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, I like honorable sushi the best.','私はお寿司が一番好きです。','Watashi wa osushi ga ichiban suki desu.',21,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for nowadays, in America also, reportedly they eat honorable sushi often, right?','このごろはアメリカでもお寿司をよく食べるそうですね。','Kono goro wa amerika de mo osushi o yoku taberu soo desu ne.',22,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Is the other side’s honorable sushi the same as the Japanese?	 Side is understood.','むこうのお寿司は日本のと同じですか。','Mukoo no osushi wa nihon no to onaji desu ka.  (onaji = the same, a na adjective,  however, onaji differs from all other na adjectives in that it is usually used without na when it precedes a noun, e.g., onaji mono = the same thing.  Onaji is not an i adjective, since you can say onaji da = ‘it’s the same,’ whereas you could never say something like oishii da.  Also, onaji is not inflected like an i adjective, e.g., you say onaji ja nai = ‘it isn’t the same,’ not onajiku nai.  Sometimes na is used after onaji, e.g., you can say onaji na koto = ‘the same thing,’ and na is also used after onaji when you follow it with noni or node, e.g., onaji na noni = ‘in spite of the fact that it’s the same,’ and onaji na node = ‘since it’s the same’)  (cf. mukae = meeting an arriving passenger,  mukaeru = to meet/receive)',23,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah.  As for the flavor, it’s almost the same, but as for the price, it’s more cheap for sure.  Use kedo.  Use nedan.','ええ、味はほとんど同じですけど、値段はもっと安いですよ。','Ee.  Aji wa hotondo onaji desu kedo, nedan wa motto yasui desu yo.',24,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('But the variety, compared to Japan, is less numerous.  Don’t use hoo ga.','でも種類は日本ほど多くありません。','Demo shurui wa nihon hodo ooku arimasen. (shurui = kind, type, variety) (we shoo Louey away but he carries a lot of varieties) (cf. shumi = hobby,  cf. shorui = documents)  (OK to substitute yori for hodo)',25,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('At the home’s closely, since there is a very cheap and delicious Mr. honorable sushi store, the next time I go, I''ll humbly invite you along, huh?','うちの近くにとても安くておいしいお寿司屋さんがありますから、今度行くときお誘いしますね。','Uchi no chikaku ni totemo yasukute oishii osushiyasan ga arimasu kara, kondo iku toki osasoi shimasu ne.   (uchi no chikai, not OK) (to form a humble verb construction, referring to actions that you perform, put o in front of the verb stem, and follow the verb stem with suru or shimasu)',26,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, by all means if you please.','ええ、ぜひお願いします。','Ee, zehi onegaishimasu.',27,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Michael, theJapanese considerably is skillful, huh.','マイケルさん、日本語がなかなか上手ですね。','Maikeru san nihongo ga nakanaka joozu desu ne.',28,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It was a 2:00 appointment, but, as for the friend, he didn''t come readily.  Plain speech.','2時の約束だったが友達はなかなか来なかった。','Niji no yakusoku datta ga, tomodachi wa nakanaka konakatta.  (kimasen deshita also OK) (tomodachi ga is OK) (yakusoku ga atta ga, not OK, since appointment is intangible, but yakusoko de atta ga is OK)',29,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('The Japanese doesn''t become skillful readily.  Plain speech.','日本語がなかなかじょうずにならない。','Nihongo ga nakanaka joozu ni naranai.',30,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Tsuchida''s wife, she is wearing a kimono.  Plain speech.','土田さんの奥さんは着物を着ている。','Tsuchida san no okusan wa kimono o kite iru.',31,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Hanada, she is wearing the Hong Kong purchased shoes.  Plain speech.','花田さんは香港で買った靴を履いている。','Hanada san wa honkon de katta kutsu o haite iru.',32,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the company people, they are all come to the party.  Use hito.  Use minna.  Plain speech.','会社の人はみんなパーティーに来ている。','Kaisha no hito wa minna paatii ni kite iru.  (instead of minna, zenbu is OK, but subete is not OK,  may say subete no kaisha no hito ga = all of the company’s people)',33,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Kuroda, she has met Barbara at before the party.  Plain speech.','黒田さんはパーティーの前にバーバラさんに会ったことがある。','Kuroda san wa paatii no mae ni baabarasan ni atta koto ga aru.  (paatii no mae de, not OK, since de refers to place, not time)',34,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Compared to American sushi, Japanese sushi, varieties are more numerous.  Use hoo ga. Plain speech.','アメリカの寿司より日本のほうが種類が多い。','Amerika no sushi yori nihon no sushi no hoo ga shurui ga ooi.',35,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the red-frock wearing one, who is it?','赤いワンピースを着ているのは誰ですか。','Akai wanpiisu o kite iru no wa dare desu ka.',36,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s Yamada''s wife.','山田さんの奥さんです。','Yamada san no okusan desu.',37,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for the kimono-wearing one, who is it?','着物を着ているのは誰ですか。','Kimono o kite iru no wa dare desu ka.',38,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s the office’s Suzuki.','事務の鈴木さんです。','Jimu no Suzuki san desu. (jimu = office,  cf. jimusho = office,  cf. jimi na = plain)',39,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Yamashita, why is he not come to the party?  Use no to make a noun phrase.','山下さんはどうしてパーティーに来ていないのですか。','Yamashita san wa dooshite paatii ni kite inai no desu ka.  (paatii ni kite imasen ka, OK, but, paatii ni kite inai ka, not OK, so you need to make a noun phrase)',40,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It’s because he’s still doing work at the company.','まだ会社で仕事をしているからです。','Mada kaisha de shigoto o shite iru kara desu.',41,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, as for of Japanese cooking, what does she like best?','バーバラさんは日本料理では何が一番好きですか。','Baabarasan wa nihon ryoori de wa nani ga ichiban suki desu ka.',42,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('She likes honorable sushi best.','お寿司が一番好きです。','Osushi ga ichiban suki desu.',43,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for Barbara, when the party finishes, will she go to a sushi place with Hanada?  Use tara.','バーバラさんはパーティが終わったら花田さんと寿司屋へ行きますか。','Baabarasan wa paatii ga owattara hanada san to sushiya e ikimasu ka.',44,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('No, but sometime she will probably go.','いいえ、でもいつか行くでしょう。','Iie, demo itsuka iku deshoo.',45,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Next time, when I go to the sushi place, I will humbly invite you along.  Use toki.','今度、寿司屋へ行くときお誘いします。','Kondo sushiya e iku toki osasoi shimasu.  (sushiya e ittara, not OK)',46,16);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('When going to Hawaii (before going), I bought a swimsuit.  (with toki, the present tense may be used although the action occurred in the past)','ハワイに行くとき水着を買いました。','Hawai ni iku toki mizugi o kaimashita.  (mizu = water, mizugi = swimsuit, shitagi = underwear)',47,16);
