INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('More polite.  There’s no excuse.','申し訳ございません。申し訳ございませんでした。','There''s no excuse for what I did.Mooshiwake gozaimasen.  Mooshiwake gozaimasen deshita.',42,20);
