INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Ah, is this a summer robe?','あぁ、これが浴衣ですか。','Aa, kore ga yukata desu ka. (kore wa, also OK)',11,27);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Yeah, after you entered the hot springs, you will wear it for sure.  Use ato.  Soften the second clause.','ええ、温泉に入った後、着るんですよ。','Ee, onsen ni haitta ato, kirun desu yo.  (kiru = to wear, to cut,  I wear kiwi roots: I cut them too)  (ato ni, ato de, both also OK)',12,27);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('It looks hot, huh.  Use soo.','熱そうですね。','Atsu soo desu ne.',13,27);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('As for me, as for hot honorable baths, they are bad for sure.  Use boku.  Use nan to soften this.','ぼくは熱いお風呂はだめなんですよ。','Boku wa, atsui ofuro wa dame nan desu yo.',14,27);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Since it isn’t that hot, it’s OK for sure.','そんなに熱くないから大丈夫ですよ。','Sonna ni atsukunai kara, daijoobu desu yo.',15,27);
INSERT INTO JAPANESE_LESSON(english,japanese,romaji,exercise,lesson) VALUES('Well, me first.  Use boku.','じゃ、ぼくがお先に。','Jaa, boku ga osaki ni.  (use ‘o’ before saki ni, meaning ''to go first,'' regardless of who goes first,  if you go first, the ‘o’ is humble speech,  if the other person goes first, it is honorific speech)',16,27);
